insert into unit_enemy_data values (308601, '似龍生物', 308600, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】以龍？一般的大翼翱翔於天際，\\n並以龍？一般的灼熱吐息將敵人燒得一片焦黑。\\n\\n‧在戰鬥開始時，\\n\u3000向自己追加70個【火焰果實】。\\n‧必殺技將會依據似龍生物所持有的\\n\u3000【火焰果實】數量多寡，\\n\u3000對敵方全體造成物理比例傷害。\\n\u3000使用後，【火焰果實】將減少60個。\\n‧對前方 3 名角色依照其最大HP造成\\n\u3000物理比例傷害，並使其暈眩。\\n‧發動時，在短時間內\\n\u3000大幅度降低自己的物理防禦力\\n\u3000與魔法防禦力，並且在進食的行動中，\\n\u3000將會持續增加【火焰果實】\\n\u3000直到最大60個為止。\\n\u3000在進食的行動中，當似龍生物受到了\\n\u30005000以上的傷害時，\\n\u3000此技能將會被迫中斷，\\n\u3000同時減少牠持有30個【火焰果實】。');
insert into unit_enemy_data values (308602, '似龍生物', 308600, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】以龍？一般的大翼翱翔於天際，\\n並以龍？一般的灼熱吐息將敵人燒得一片焦黑。\\n\\n‧在戰鬥開始時，向自己追加70個【火焰果實】\\n‧必殺技將會依據似龍生物所持有的\\n\u3000【火焰果實】數量多寡，\\n\u3000對全體敵人造成物理比例傷害。\\n\u3000使用後，【火焰果實】將減少60個。\\n‧對前方 3 名角色依照其最大HP造成\\n\u3000物理比例傷害，並使其暈眩。\\n‧發動時，在短時間內\\n\u3000大幅度降低自己的物理防禦力\\n\u3000與魔法防禦力，並且在進食的行動中，\\n\u3000將會持續增加【火焰果實】\\n\u3000直到最大60個為止。\\n\u3000在進食的行動中，當似龍生物受到了\\n\u300020000以上的傷害時，\\n\u3000此技能將會被迫中斷\\n\u3000同時減少牠持有30個【火焰果實】。');
insert into unit_enemy_data values (308603, '似龍生物', 308600, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】以龍？一般的大翼翱翔於天際，\\n並以龍？一般的灼熱吐息將敵人燒得一片焦黑。\\n\\n‧在戰鬥開始時，向自己追加70個【火焰果實】\\n‧必殺技將會依據似龍生物所持有的\\n\u3000【火焰果實】數量多寡，\\n\u3000對全體敵人造成物理比例傷害。\\n\u3000使用後，【火焰果實】將減少60個。\\n‧對前方 3 名角色依照其最大HP造成\\n\u3000物理比例傷害，並使其暈眩。\\n‧發動時，在短時間內\\n\u3000大幅度降低自己的物理防禦力\\n\u3000與魔法防禦力，並且在進食的行動中，\\n\u3000將會持續增加【火焰果實】\\n\u3000直到最大60個為止。\\n\u3000在進食的行動中，當似龍生物受到了\\n\u3000160000以上的傷害時，\\n\u3000此技能將會被迫中斷，\\n\u3000同時減少牠持有30個【火焰果實】。\\n‧當似龍生物的 HP 在 40% 以下時，\\n\u3000牠的攻擊將會變得更加狂暴。');
insert into unit_enemy_data values (308604, '似龍生物', 308600, 0, 16, 270, 2160, 1, 1.8, 0, 0, 0, '【MODE1】從蒼天傳來的咆哮撕裂了天際，\\n以璀璨烈陽般的自豪雙翼，巨大偽龍應聲降臨。\\n\\n‧當似龍生物所剩的HP低於80％時，\\n\u3000將會進到下一個MODE。\\n\\n‧在戰鬥開始時，向自己追加70個【火焰果實】\\n‧必殺技將會依據似龍生物所持有的\\n\u3000【火焰果實】數量多寡，\\n\u3000對全體敵人造成物理比例傷害。\\n\u3000使用後，【火焰果實】將減少60個。\\n‧對前方 3 名角色依照其最大HP造成\\n\u3000物理比例傷害，並使其暈眩。\\n‧發動時，在短時間內\\n\u3000大幅度降低自己的物理防禦力與\\n\u3000魔法防禦力，並且在進食的行動中，\\n\u3000將會持續增加【火焰果實】\\n\u3000直到最大60個為止。\\n\u3000在進食的行動中，當似龍生物受到了\\n\u3000150000以上的傷害時，\\n\u3000此技能將會被迫中斷，同時減少\\n\u3000牠持有的30個【火焰果實】。\\n‧叫出4隻魔物，\\n\u3000同時對全體敵人造成小幅度的擊退。');
insert into unit_enemy_data values (308605, '似龍生物', 308600, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【MODE2】偽龍的一聲令下，森林內的魔物們\\n便集結而至。且為了守護牠拼上了小命。\\n\\n‧當魔像、多倫多獸\\n\u3000和毒莢花都被打倒時，\\n\u3000將會進到下一個MODE。\\n‧似龍生物將會對在牠面前的 1 名角色\\n\u3000造成物理攻擊。');
insert into unit_enemy_data values (308606, '似龍生物', 308606, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【MODE3】只因對龍的憧憬，使那蜥蜴覺醒了\\n統治天際之霸者的吹息，可使座島陷入無盡劫火。\\n\\n‧在戰鬥開始時，向自己追加70個【火焰果實】\\n‧必殺技將會依據似龍生物所持有的\\n\u3000【火焰果實】數量多寡，對全體敵人\\n\u3000造成物理比例傷害。\\n\u3000使用後，【火焰果實】將減少60個。\\n‧對前方3名角色依照其最大HP造成\\n\u3000物理比例傷害，並使其暈眩。\\n‧發動時，在短時間內\\n\u3000大幅度降低自己的物理防禦力\\n\u3000與魔法防禦力，並且在進食的行動中，\\n\u3000將會持續增加【火焰果實】\\n\u3000直到最大60個為止。\\n\u3000在進食的行動中，當似龍生物受到了\\n\u3000250000以上的傷害時，\\n\u3000此技能將會被迫中斷，\\n\u3000同時減少牠持有的30個【火焰果實】。\\n‧在戰鬥剛開始時，\\n\u3000牠的攻擊將會變得更加狂暴。');
insert into unit_enemy_data values (309000, '沙陸樹懶', 309000, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】於夢與實的狹縫間怠惰貪睡的一種樹獺。\\n若睡眠被妨礙將會很生氣，而以超能力大鬧一番。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧中幅度降低來自沙陸樹懶所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會依照敵人的最大HP，\\n\u3000對敵方全體造成魔法比例傷害，\\n\u3000特大幅度降低其物理攻擊力，並中幅度降低魔法攻擊力。\\n\u3000此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體造成中幅度的魔法傷害。');
insert into unit_enemy_data values (309001, '沙陸樹懶', 309001, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】於夢與實的狹縫間怠惰貪睡的一種樹獺。\\n若睡眠被妨礙將會很生氣，而以超能力大鬧一番。\\n\\n‧戰鬥開始時，\\n\u3000中幅度提升自身物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000特大幅度降低敵方全體的HP吸收。\\n‧戰鬥開始時，\\n\u3000展開小幅度降低敵方全體TP上升的領域。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少50％的效果。\\n‧中幅度降低來自沙陸樹懶所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會依照敵人的最大HP，\\n\u3000對敵方全體造成魔法比例傷害，\\n\u3000特大幅度降低其物理攻擊力，\\n\u3000中幅度降低魔法攻擊力，並施加暈眩狀態。\\n\u3000此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體造成中幅度的魔法傷害，\\n\u3000並施加暈眩狀態。\\n\u3000此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (309002, '懶惰瑪瑪爾', 309002, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】棲息於廣闊叢林的巨大野獸\\n一旦遭受生命威脅，防禦反應\\n就會運作，解放龐大的野性。\\n\\n‧必殺技會對敵方全體\\n\u3000依照其最大HP\\n\u3000造成魔法比例傷害，\\n\u3000並且造成物理攻擊力與魔法攻擊力\\n\u3000大幅度下降。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n\\n‧對敵方全體造成小幅度的魔法傷害\\n\u3000並且施加暈眩。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (309003, '沙陸樹懶', 309003, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】於夢與實的狹縫間怠惰貪睡的一種樹獺。\\n若睡眠被妨礙將會很生氣，而以超能力大鬧一番。\\n\\n‧中幅度降低來自沙陸樹懶所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技能對敵方全體依照其角色\\n\u3000最大HP造成魔法比例傷害，\\n\u3000同時中幅度降低其物理攻擊力\\n\u3000與魔法攻擊力，並使其陷入暈眩狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體造成中幅度的魔法傷害。');
insert into unit_enemy_data values (309100, '泥巴鱷', 309100, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】棲息於大河中的大鱷魚怪。\\n雖有著一臉蠢樣，卻以不可思議的\\n長槍將大地融解，封鎖冒險者的行\\n動。\\n\\n‧對全體敵人造成大幅度物理傷害\\n，並且同時降低物理防禦力與\\n\u3000行動速度。\\n\\n‧以從後方數來第3位角色為中心\\n，造成小範圍的小幅度物理傷害\\n，並且使其陷入束縛狀態。');
insert into unit_enemy_data values (309101, '泥巴鱷', 309101, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】棲息於大河中的大鱷魚怪。雖有著一臉蠢樣，\\n卻以不可思議的長槍將大地融解，封鎖敵人的行動。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自泥巴鱷所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技會無視挑釁，\\n\u3000以前方數來第3名敵人為中心，\\n\u3000造成小範圍的大幅度物理傷害，\\n\u3000同時大幅度降低敵方全體的行動速度，\\n\u3000並依照此技能造成傷害的敵人數量降低TP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧無視挑釁，以前方數來第3名敵人為中心，\\n\u3000造成小範圍的小幅度物理傷害，並施加\\n\u3000束縛狀態。此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
