insert into unit_enemy_data values (309200, '蒼天女武神', 309200, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】於戰空中翩翩起舞，天上之女武神。\\n以目不暇給的連續射擊，完全封鎖敵人的退路。\\n\\n‧戰鬥開始時，小幅度提升自身物理防禦力及魔法\\n\u3000防禦力。因防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧中幅度降低來自蒼天女武神所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技能對前方範圍內最多4名敵人造成特大\\n\u3000幅度的物理傷害，並且中幅度降低其TP，同時\\n\u3000大幅度降低其行動速度。此技能所造成的狀態\\n\u3000異常，即使傷害無效也會施加。\\n‧對距離最近的4名敵人造成中幅度的物理傷害，\\n\u3000同時大幅度降低物理攻擊力與魔法攻擊力。\\n\u3000同時，特大幅度提升自身的行動速度。');
insert into unit_enemy_data values (309201, '蒼天女武神', 309201, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】於戰空中翩翩起舞，天上之女武神。\\n以目不暇給的連續射擊，完全封鎖敵人的退路。\\n\\n‧戰鬥開始時，中幅度提升自身物理防禦力及魔法\\n\u3000防禦力。因防禦力提升效果而上升的防禦力，在\\n\u3000戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，展開小幅度降低敵方全體TP上升\\n\u3000的領域。\\n‧戰鬥開始時，對自身施加TP降低減少75％\\n\u3000效果。\\n‧中幅度降低來自蒼天女武神所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧大幅度降低來自敵方所有傷害產生的蒼天女武神\\n\u3000TP恢復量。\\n‧必殺技會以距離最近的4名敵人為中心，對\\n\u3000範圍內造成特大幅度的物理傷害，同時大幅度\\n\u3000降低TP，特大幅度降低TP上昇與行動速度。\\n\u3000並且若使用此技能將敵人擊倒，則能特大幅度\\n\u3000提升自身的物理攻擊力。此技能所造成的狀態\\n\u3000異常，即使傷害無效也會施加。\\n‧對距離最近的4名敵人造成中幅度的物理傷害，\\n\u3000同時大幅度降低物理攻擊力與魔法攻擊力。\\n\u3000同時，特大幅度提升自身的行動速度。此技能\\n\u3000所造成的狀態異常，即使傷害無效也會施加。');
insert into unit_enemy_data values (309202, '蒼天女武神', 309202, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】於戰空中翩翩起舞，天上之女武神。\\n以目不暇給的連續射擊，完全封鎖敵人的退路。\\n\\n‧中幅度降低來自蒼天女武神所有傷害產生的隊伍\\n\u3000角色TP恢復量。\\n‧必殺技能對前方範圍內最多4名角色造成特大幅度\\n\u3000的物理傷害，並且大幅度降低其TP，同時\\n\u3000特大幅度降低其TP上升與行動速度\\n\u3000直到戰鬥結束。此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的4名敵人造成大幅度的物理傷害，\\n\u3000並大幅度降低其物理攻擊力與魔法攻擊力\\n\u3000直到戰鬥結束。同時，特大幅度提升\\n\u3000自身的行動速度。此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
