insert into unit_enemy_data values (309301, '復仇者馴鹿', 309300, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】下定決心要高舉反叛旗幟的精英馴鹿。\\n向無盡慾望的聖誕老人，以從者之心揮下鐵槌。\\n\\n‧必殺技將會在消滅了召喚物後，\\n\u3000對敵方全體依照其角色最大HP\\n\u3000造成物理的比例傷害。\\n\u3000當受到此技能的傷害後，若角色的HP\\n\u3000低於40%，該名角色將會在一定時間內\\n\u3000變成馴鹿，同時降低其物理暴擊\\n\u3000和魔法暴擊。而變成了馴鹿的角色，\\n\u3000將只能夠使用普通攻擊。\\n\u3000在一定時間內，大幅度降低自身的物理防禦力。\\n‧以最前方的角色為中心，在一定範圍內\\n\u3000依照其角色最大HP造成物理的比例傷害。\\n\u3000同時提升自身的物理攻擊力。\\n‧對最前方的角色造成物理傷害與擊退，\\n\u3000並且同時使其陷入暈眩狀態。');
insert into unit_enemy_data values (309302, '復仇者馴鹿', 309300, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】下定決心要高舉反叛旗幟的精英馴鹿。\\n向無盡慾望的聖誕老人，以從者之心揮下鐵槌。\\n\\n‧必殺技將會在消滅了召喚物後，\\n\u3000對敵方全體依照其角色最大HP\\n\u3000造成物理的比例傷害。\\n\u3000當受到此技能的傷害後，若角色的HP\\n\u3000低於40%，該名角色將會在一定時間內\\n\u3000變成馴鹿，同時降低其物理暴擊\\n\u3000和魔法暴擊。而變成了馴鹿的角色，\\n\u3000將只能夠使用普通攻擊。\\n\u3000在一定時間內，大幅度降低自身的物理防禦力。\\n‧以最前方的角色為中心，在一定範圍內\\n\u3000依照其角色最大HP造成物理的比例傷害。\\n\u3000同時提升自身的物理攻擊力。\\n‧對最前方的角色造成物理傷害與擊退，\\n\u3000並且同時使其陷入暈眩狀態。');
insert into unit_enemy_data values (309303, '復仇者馴鹿', 309300, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】下定決心要高舉反叛旗幟的精英馴鹿。\\n向無盡慾望的聖誕老人，以從者之心揮下鐵槌。\\n\\n‧必殺技將會在消滅了召喚物後，\\n\u3000對敵方全體依照其角色最大HP\\n\u3000造成物理的比例傷害。\\n\u3000當受到此技能的傷害後，若角色的HP\\n\u3000低於40%，該名角色將會在一定時間內\\n\u3000變成馴鹿，同時降低其物理暴擊\\n\u3000和魔法暴擊。而變成了馴鹿的角色，\\n\u3000將只能夠使用普通攻擊。\\n\u3000在一定時間內，大幅度降低自身的物理防禦力。\\n‧以最前方的角色為中心，在一定範圍內\\n\u3000依照其角色最大HP造成物理的比例傷害。\\n\u3000同時提升自己的物理攻擊力。\\n‧對最前方的角色造成物理傷害與擊退，\\n\u3000並且同時使其陷入暈眩狀態。\\n‧當復仇者馴鹿自身的HP低於50％之時，\\n\u3000牠的攻擊將會變得更加兇暴。');
insert into unit_enemy_data values (309304, '復仇者馴鹿', 309300, 0, 16, 270, 3000, 1, 4.2, 0, 0, 0, '【MODE1】召喚出克莉絲提娜、望、伊莉亞的\\n闇影。三人那扭曲的慾望與好管閒事，\\n形成了具體之物並且將聖夜帶向了渾沌。\\n\\n‧對最前方的角色造成物理傷害。');
insert into unit_enemy_data values (309305, '復仇者馴鹿', 309300, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【MODE2】絕望如同白雪一般在聖夜的街道上\\n沉積。從那被慾望染指的大袋之中滿溢而出的，\\n是累積已久的憎惡。\\n\\n‧必殺技將會在消滅了召喚物後，\\n\u3000對敵方全體依照其角色最大HP\\n\u3000造成物理的比例傷害。\\n\u3000當受到此技能的傷害後，若角色的HP\\n\u3000低於40%，該名角色將會在一定時間內\\n\u3000變成馴鹿，同時降低其物理暴擊\\n\u3000和魔法暴擊。而變成了馴鹿的角色，\\n\u3000將只能夠使用普通攻擊。\\n\u3000在一定時間內，大幅度降低自身的物理防禦力。\\n‧以最前方的角色為中心，在一定範圍內\\n\u3000依照其角色最大HP造成物理的比例傷害。\\n\u3000同時提升自身的物理攻擊力。\\n‧在一定時間內，於前方生成一個領域。\\n\u3000對處於此領域內的角色，\\n\u3000大幅度降低其物理攻擊力與物理暴擊\\n\u3000和TP上升。\\n‧對最前方的角色造成物理傷害與擊退，\\n\u3000並且同時使其陷入暈眩狀態。');
insert into unit_enemy_data values (309306, '復仇者馴鹿', 309300, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【MODE3】身負重傷的巨獸，燃起了反擊的狼煙。\\n將自身墮落於於憎惡與憤怒之終，\\n極盡殘虐之性情至最後一刻。\\n\\n‧必殺技將會在消滅了召喚物後，\\n\u3000對敵方全體依照其角色最大HP\\n\u3000造成物理的比例傷害。\\n\u3000當受到此技能的傷害後，若角色的HP\\n\u3000低於35%，該名角色將會在一定時間內\\n\u3000變成馴鹿，同時降低其物理暴擊\\n\u3000和魔法暴擊。而變成了馴鹿的角色，\\n\u3000將只能夠使用普通攻擊。\\n\u3000在一定時間內，大幅度降低自身的物理防禦力。\\n‧以最前方的角色為中心，在一定範圍內\\n\u3000依照其角色最大HP造成物理的比例傷害。\\n\u3000同時提升自身的物理攻擊力。\\n‧對最前方的角色造成物理傷害與擊退，\\n\u3000並且同時使其陷入暈眩狀態。\\n‧在戰鬥剛開始時，\\n\u3000牠的攻擊將會變得更加兇暴。');
insert into unit_enemy_data values (309400, '僕役‧刀刃（物理）', 309400, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】由霸瞳皇帝創造而出的自立能量儲藏庫。\\n擁有司掌著體力的權能，以那極大的劍戟斬斷一切。\\n\\n‧暈眩與擊飛能發揮效果。\\n\\n‧必殺技能夠對全體敵人依照其最大 HP\\n\u3000造成大幅度物理的比例傷害。\\n\\n‧以面前的敵人為中心，對範圍內所有的敵人\\n\u3000依照其角色最大 HP\\n\u3000造成物理的比例傷害。\\n\\n‧當僕役‧刀刃（物理）自身的 HP\\n\u3000低於 40% 之時，它的攻擊\\n\u3000將會變得更加的兇暴。');
insert into unit_enemy_data values (309500, '僕役‧魔能（魔法）', 309500, 0, 16, 270, 1000, 2, 1.8, 0, 0, 0, '【魔法】由霸瞳皇帝創造而出的自立能量儲藏庫。\\n擁有司掌著魔道的權能，能以無盡魔力將萬物消滅。\\n\\n‧必殺技能夠對全體敵人依照其最大 HP\\n\u3000造成大幅度的魔法比例傷害。\\n\u3000同時小幅度降低其物理攻擊力與魔法攻擊力。\\n\\n‧對隨機的 1 名角色造成魔法傷害，並且使其\\n\u3000陷入詛咒、麻痺狀態，同時大幅度降低 TP。\\n\\n‧僕役‧魔能（魔法）自身的 HP\\n\u3000低於 40% 之時，它的攻擊\\n\u3000將會變得更加的兇暴。');
insert into unit_enemy_data values (309600, '僕役‧洛克斯（複部位）', 309600, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】由霸瞳皇帝創造而出的自立能量儲藏庫。\\n司掌著雙肩的權能，對廣泛領域的一切進行屠殺。\\n\\n‧必殺技將以面前的敵人為中心，對範圍內\\n\u3000所有的敵人依照其角色最大HP，造成物理\\n\u3000的比例傷害，同時再對面前的敵人造成\\n\u3000小幅度的物理傷害。\\n\\n‧以面前的敵人為中心，依照其\\n\u3000角色最大HP，造成物理的\\n\u3000比例傷害。\\n\\n‧當僕役‧洛克斯（複部位）自身的 HP\\n\u3000低於 40% 之時，它的防禦力\\n\u3000將會有所提升，而變得更加頑強。');
insert into unit_enemy_data values (309700, '僕役‧城牆（防衛）', 309700, 0, 16, 270, 1000, 2, 1.8, 0, 0, 0, '【魔法】由霸瞳皇帝創造而出的自立能量儲藏庫。\\n司掌著守護的權能，以毫不動搖的高牆拒絕一切。\\n\\n‧必殺技能夠大幅度提升自身的物理防禦力、\\n\u3000魔法防禦力。\\n\\n‧以面前的敵人為中心，對範圍內所有的敵人\\n\u3000造成魔法傷害。並且小幅度提升自身的\\n\u3000物理防禦力與魔法防禦力。\\n\\n‧當僕役‧城牆（防衛）自身的 HP\\n\u3000低於 40% 之時，它的防禦力\\n\u3000將會有所提升，而變得更加頑強。');
insert into unit_enemy_data values (309800, '霸瞳皇帝', 309800, 0, 16, 270, 1000, 1, 3.6, 309800, 309800, 0, '王都終末決戰_基本隊伍');
insert into unit_enemy_data values (309801, '霸瞳皇帝', 309801, 0, 16, 270, 1000, 1, 2.4, 309800, 309800, 0, '【MODE1】將一切拒絕在外的悲戚王女。\\n以神自居，將自身沉浸在禍殃之力之中。\\n並且對整個阿斯特朗揮舞起了那狂亂之刃。\\n守護那欺瞞之王女的牆壁，就用無數之刃來打破吧。\\n\\n‧展開護盾，使其不會受到敵人造成的任何攻擊。\\n\u3000此護盾與傷害量無關，而是每當受到攻擊時，\\n\u3000便會漸漸的降低其耐久度。\\n\u3000當受到280次的攻擊後，護盾將會被破壞。\\n\\n\\n‧必殺技將會依照敵方全體所剩的HP\\n\u3000造成特大幅度的比例傷害。\\n\\n‧對全體敵人造成小幅度的\\n\u3000魔法傷害。');
insert into unit_enemy_data values (309802, '霸瞳皇帝', 309800, 0, 16, 270, 1000, 1, 1.8, 309800, 309800, 0, '【MODE2】對渺小之徒的反抗露出了些許的煩躁。\\n對懷抱希望，起身反抗祂的所有人以我理裁之。\\n以悲憤之劍將其刺穿，使魂魄挫敗於塵芥之下。\\n暫居神位之人所放出的黑刃，就以正義的\\n魔法與之抗衡吧。\\n\\n‧必殺技將會依照敵方全體所剩的HP\\n\u3000造成特大幅度的比例傷害。\\n\\n‧對面前的1名敵人造成特大幅度的物理傷害，\\n\u3000並對其周圍的所有敵人造成中幅度的魔法傷害，\\n\u3000並且使其昏迷。而同時已經昏迷的角色，\\n\u3000再受到下一次攻擊前將無法行動。\\n\\n‧對全體敵人造成小幅度的\\n\u3000魔法傷害。');
insert into unit_enemy_data values (309803, '霸瞳皇帝', 309800, 0, 16, 270, 1000, 1, 1.6, 309800, 309800, 0, '【MODE3】創世之神，迎向了那終末的咆哮。\\n因怨嗟而出口的拒絕之言辭幻化為詛咒，\\n削減著身體、精神與魂魄，將一切葬送闇中。\\n一決生死之際終究到來了。各位，高舉手中的\\n劍開拓嶄新的明日吧。\\n\\n‧必殺技將會依照敵方全體所剩的HP\\n\u3000造成特大幅度的比例傷害。\\n\\n‧對面前的1名敵人造成特大幅度的物理傷害，\\n\u3000並對其周圍的所有敵人造成中幅度的魔法傷害，\\n\u3000並且使其昏迷。而同時已經昏迷的角色，\\n\u3000再受到下一次攻擊前將無法行動。\\n\\n‧對全體敵人造成小幅度的\\n\u3000魔法傷害。\\n\\n‧以最後面的1名敵人為中心，展開能夠中幅度\\n\u3000降低其物理防禦力與魔法防禦力的領域，\\n\u3000並且造成小幅度的魔法傷害，\\n\u3000同時使其被詛咒。而由詛咒所產生的持續傷害，\\n\u3000將會隨著每秒進而增加。\\n\\n‧在戰鬥開始之後，立刻大幅度增加自己的\\n\u3000行動速度，同時小幅度增加自己的\\n\u3000魔法防禦力。');
