insert into unit_enemy_data values (309900, '哥布林大師', 309900, 0, 17, 270, 1100, 2, 1.8, 0, 0, 0, '【魔法】企圖將塔之魔力收刮囊中的小鬼學者。\\n以禁術操弄著土塊之人偶，強奪著一切的自由。\\n\\n‧對全體敵人造成大幅度魔法傷害，\\n\u3000同時使所有的召喚物、分身立即死亡。\\n‧對前方廣範圍內依照其剩餘HP造成中幅度\\n\u3000的物理比例傷害，同時大幅度擊飛，\\n\u3000同時召喚1隻泥巴人偶。\\n\u3000泥巴人偶對最前衛與從前方數來第2名的角色\\n\u3000造成物理傷害，同時降低行動速度。\\n‧以從前方數來第2名的角色為中心，造成小範圍\\n\u3000的中幅度物理傷害，同時造成猛毒和暈眩。');
insert into unit_enemy_data values (309907, '哥布林大師', 309907, 0, 16, 270, 2000, 2, 1.8, 0, 0, 0, '【魔法】被領主視為右臂的哥布林大魔導士。\\n釋放禁忌的魔術，消滅所有抵抗的敵人。\\n‧戰鬥開始時，\\n小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，使用傷害上限技能，\\n\u3000在一次行動中造成的傷害值超過一定值時，\\n\u3000會根據造成的傷害值減少傷害量。\\n‧承受來自哥布林大師所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧每當受到傷害時，\\n\u3000會增加1個【小鬼徽章】。\\n\u3000當【小鬼徽章】為10個時，消耗10個\\n\u3000【小鬼徽章】，會爆破魔法之壺，對哥布林王者\\n\u3000和哥布林大師造成特大幅度的魔法傷害，\\n\u3000並特大幅度降低其物理防禦力與魔法防禦力。\\n‧必殺技能夠對敵方全體造成大幅度的魔法傷害，\\n\u3000並破壞所有的召喚物。\\n‧對前方的1名角色依照其角色最大HP\\n\u3000造成魔法比例傷害。\\n‧當剩餘HP低於2400萬時，\\n\u3000會特大幅度提升魔法攻擊力和行動速度，\\n\u3000並且會改變攻擊行動。\\n‧以從前方數來第2名角色為中心，\\n\u3000對範圍內的敵人造成中幅度的魔法傷害，\\n\u3000並且使其陷入麻痺狀態與猛毒狀態。');
insert into unit_enemy_data values (310000, '山神大人', 310000, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】狂奔之神與傳說中的山之土地神。\\n自遠古便尋覓著好對手而趾高氣昂的在大地競走著。\\n\\n‧必殺技會對敵方全體依照其角色最大\\n\u3000HP造成物理比例傷害，\\n\u3000並將其擊退。\\n\u3000同時對最前方的角色造成特大幅度的\\n\u3000追加物理傷害，並且大幅度擊飛\\n\u3000和使其暈眩。\\n\u3000而每當山神大人發動必殺技時，\\n\u3000降低自身的物理防禦力。\\n\u3000此效果將會累積。\\n‧對魔法攻擊力最高的1名角色\\n\u3000依照其角色最大HP造成魔法比例傷害，\\n\u3000同時大幅度降低TP。\\n‧提升自身的TP與行動速度。\\n‧當山神大人自身的HP低於55%時，祂的攻擊\\n\u3000將會變得更加兇暴，但相對也會\\n\u3000降低自身的物理防禦力。');
insert into unit_enemy_data values (310001, '山神大人', 310000, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】狂奔之神與傳說中的山之土地神。\\n自遠古便尋覓著好對手而趾高氣昂的在大地競走著。\\n\\n‧必殺技會對敵方全體依照其角色最大HP\\n\u3000造成物理比例傷害且將其擊退，\\n\u3000同時對最前方的角色造成特大幅度的\\n\u3000追加物理傷害，並且擊退和使其暈眩。\\n\u3000每當山神大人發動必殺技時，\\n\u3000降低自身的物理防禦力。\\n\u3000此效果將會累積。\\n‧提升自身的TP與行動速度。');
insert into unit_enemy_data values (310002, '山神大人', 310000, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】狂奔之神與傳說中的山之土地神。\\n自遠古便尋覓著好對手而趾高氣昂的在大地競走著。\\n\\n‧必殺技會對敵方全體依照其角色最大HP\\n\u3000造成物理比例傷害，並將其擊退。\\n\u3000同時對最前方的角色造成特大幅度的\\n\u3000追加物理傷害，並且擊退和使其暈眩。\\n\u3000每當山神大人發動必殺技時，\\n\u3000降低自身的物理防禦力。\\n\u3000此效果將會累積。\\n‧對魔法攻擊力最高的1名角色\\n\u3000依照其角色最大HP造成魔法比例傷害，\\n\u3000同時降低TP。\\n‧提升自身的TP與行動速度。\\n‧當山神大人自身的HP低於55％時，\\n\u3000攻擊將會變得更加兇暴，\\n\u3000同時降低自身的物理防禦力。');
insert into unit_enemy_data values (310003, '山神大人', 310000, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】狂奔之神與傳說中的山之土地神。\\n自遠古便尋覓著好對手而趾高氣昂的在大地競走著。\\n\\n‧必殺技會對敵方全體依照其角色最大HP\\n\u3000造成物理比例傷害，並將其擊退。\\n\u3000同時對最前方的角色造成特大幅度的\\n\u3000追加物理傷害，並且擊退和使其暈眩。\\n\u3000每當山神大人發動必殺技時，\\n\u3000降低自身的物理防禦力。\\n\u3000此效果將會累積。\\n‧對魔法攻擊力最高的1名角色\\n\u3000依照其角色最大HP造成魔法比例傷害，\\n\u3000同時降低TP。\\n‧提升自身的TP與行動速度。\\n‧當山神大人自身的HP低於55％時，\\n\u3000攻擊將會變得更加兇暴，\\n\u3000同時降低自身的物理防禦力。');
insert into unit_enemy_data values (310004, '山神大人', 310000, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE1】做為冒險者之力的測試，召喚出\\n了親近之人的影子。在大肆吹拂著寒風的\\n天下之險，自身脆弱的一面將會被受考驗。\\n\\n‧對最前方的角色造成物理傷害。');
insert into unit_enemy_data values (310005, '山神大人', 310000, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE2】勇夫豪傑的奔馳連大地都為之震撼。\\n那英姿宛如祝賀之勢，好兆頭連山峻也為之起舞。\\n\\n‧必殺技會對敵方全體依照其角色最大\\n\u3000HP造成物理比例傷害，\\n\u3000並將其擊退。\\n\u3000同時對最前方的角色造成特大幅度的\\n\u3000追加物理傷害，並且大幅度擊飛\\n\u3000和使其暈眩。\\n\u3000而每當山神大人發動必殺技時，\\n\u3000降低自身的物理防禦力。\\n\u3000此效果將會累積。\\n‧對魔法攻擊力最高的2名角色\\n\u3000依照其角色最大HP造成魔法比例傷害，\\n\u3000同時大幅度降低TP。\\n‧在後方展開降低物理防禦力的領域，\\n\u3000同時對敵方全體造成中幅度的傷害。\\n\u3000在大眼鯛離開背後的期間，山神大人的\\n\u3000魔法防禦力將會特大幅度地降低。');
insert into unit_enemy_data values (310006, '山神大人', 310000, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE3】給和神一較高下之強者的最終試煉。\\n各自託付了一切，朝著思慕之人身邊奔去。\\n\\n‧必殺技會對敵方全體依照其角色最大\\n\u3000HP造成物理比例傷害，\\n\u3000並將其擊退。\\n\u3000同時對最前方的角色造成特大幅度的\\n\u3000追加物理傷害，並且大幅度擊飛\\n\u3000和使其暈眩。\\n\u3000而每當山神大人發動必殺技時，\\n\u3000降低自身的物理防禦力。\\n\u3000此效果將會累積。\\n‧對魔法攻擊力最高的1名角色\\n\u3000依照其角色最大HP造成魔法比例傷害，\\n\u3000同時大幅度降低TP。\\n‧提升自身的TP與行動速度。\\n‧在戰鬥開始之後，攻擊將會變得\\n\u3000更加兇暴，但相對也會降低自身的\\n\u3000物理防禦力。');
insert into unit_enemy_data values (310100, '狂怒綠龍', 310100, 0, 14, 270, 1000, 1, 2.6, 0, 0, 0, '【物理】居住在忘卻之密境中的年輕巨龍。\\n踏進禁忌之地的冒險者將被灼熱之業火燒成灰燼。\\n\\n‧必殺技可以對全體敵人造成物理比例傷害，\\n\u3000同時降低TP。\\n‧戰鬥開始時，將會特大幅度降低自身的TP。\\n‧以前方的1名角色為中心，對一定範圍內的\\n\u3000角色造成魔法傷害，同時降低物理防禦力。\\n\\n‧對全體敵人造成物理傷害，同時使其\\n\u3000陷入火傷狀態。\\n‧對全體敵人造成物理傷害並且擊退，\\n\u3000同時消除素質提升的效果。\\n\\n\\n‧當全部的目標部位都被破壞時，\\n\u3000牠的TP將會被特大幅度減少。\\n‧當狂怒綠龍自身的血量降至10%以下時，\\n\u3000其攻擊力將會更加殘暴，同時部位將會\\n\u3000無法被破壞。\\n‧當戰鬥的時間結束時，使全體敵人無法再\\n\u3000繼續戰鬥。\\n');
insert into unit_enemy_data values (310101, '狂怒綠龍', 310101, 0, 14, 270, 1000, 1, 2.6, 0, 0, 0, '【物理】居住在忘卻之密境中的年輕巨龍。\\n踏進禁忌之地的冒險者會遭灼熱的業火燒成灰燼。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。以此防禦力提升效果上升\\n\u3000的防禦力，在戰鬥中不會受到防禦力\\n\u3000下降技能的影響。\\n‧中幅度降低來自狂怒綠龍所有\\n\u3000傷害產生的隊伍角色TP恢復量。\\n‧必殺技會解除敵方全體的護盾，\\n\u3000並對敵方全體依照最大HP\\n\u3000造成物理比例傷害，且小幅度降低\\n\u3000TP。\\n‧以距離最近的敵人為中心，對範圍內的所有敵方\\n\u3000依照最大HP造成物理比例\\n\u3000傷害，且大幅度降低物理防禦力。\\n\u3000此技能的效果，即使傷害無效\\n\u3000也會施加。\\n‧對敵方全體造成中幅度物理傷害，且施加\\n\u3000火傷狀態。\\n\u3000此技能賦予的火傷所造成的傷害\\n\u3000不會恢復TP。\\n‧解除敵方全體的護盾，對敵方全體造成中幅度\\n\u3000物理傷害並擊退，同時\\n\u3000消除能力提升效果。\\n\u3000此技能所造成的狀態異常，即使傷害無效\\n\u3000也會施加。\\n‧當左側的目標被破壞時，\\n\u3000大幅度降低自身的物理防禦力與魔法防禦力。\\n‧當中央的目標被破壞時，\\n\u3000大幅度降低自身的物理防禦力與魔法防禦力。\\n‧當右側的目標被破壞時，\\n\u3000大幅度降低自身的物理防禦力與魔法防禦力。\\n‧當全部的目標部位都被破壞時，\\n\u3000特大幅度降低自身的TP。\\n‧當狂怒綠龍自身的血量低於30％時，\\n\u3000攻擊力將會更加殘暴，部位\\n\u3000不會被破壞，但大幅度降低自身的物理防禦力與\\n\u3000魔法防禦力。\\n‧對距離最近的1名敵方角色造成大幅度物理傷害。');
insert into unit_enemy_data values (310200, '鯊頭魟', 310200, 0, 16, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】做為統治者君臨於海蝕洞，\\n一種魟鯊類的魔物。將那兇猛的\\n慾望全數投注在捕食上的大惡黨。\\n\\n‧對全體敵人造成特大幅度物理傷害\\n，同時降低物理防禦力\\n\u3000和魔法攻擊力。\\n\\n‧對前方範圍內所有敵人造成\\n\u3000小幅度的魔法傷害，同時\\n\u3000降低其TP與TP上升率。');
insert into unit_enemy_data values (310201, '貪婪鬼鯊', 310201, 0, 16, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】吸收了塔的魔力，暴食的巨大鮫鯊。\\n能用音波干擾五感，吞食變得遲鈍的獵物。\\n\\n‧對全體敵人造成特大幅度的物理傷害，\\n\u3000同時降低物理防禦力和魔法攻擊力。\\n‧對TP最高的1名敵人造成中幅度的魔法傷害，\\n\u3000同時使其麻痺。\\n\u3000並且施加挑釁狀態。\\n‧無視挑釁，對HP最高的1名敵人\\n\u3000造成每秒增加的持續傷害。');
