insert into unit_enemy_data values (310301, '反逆獸', 310300, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】追求愛之力而現身的一種負面魔法生物。\\n以人們心中的愛之力來扭曲世界並且帶來災厄。\\n\\n‧必殺技可以從異次元取出道具來進行攻擊。\\n\u3000隨著道具的不同，效果也會有所變化。\\n\u3000當道具是槌子時，對敵方全體依照其角色\\n\u3000最大HP造成物理比例傷害，\\n\u3000同時大幅度降低物理攻擊力。\\n\u3000當道具是銅鈸時，對敵方全體依照其角色\\n\u3000最大HP造成物理比例傷害，\\n\u3000同時大幅度降低魔法攻擊力。\\n\u3000當道具是炸彈時，對敵方全體依照其角色\\n\u3000最大HP造成物理比例傷害，\\n\u3000並使其陷入詛咒狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧以最前方的敵人為中心，\\n\u3000對範圍內所有敵人造成中幅度的物理傷害，\\n\u3000並將其擊退。\\n‧對固定範圍內的所有敵人造成大幅度的物理傷害');
insert into unit_enemy_data values (310302, '反逆獸', 310300, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】追求愛之力而現身的一種負面魔法生物。\\n以人們心中的愛之力來扭曲世界並且帶來災厄。\\n\\n‧必殺技可以從異次元取出道具來進行攻擊。\\n\u3000隨著道具的不同，效果也會有所變化。\\n\u3000當道具是槌子時，對敵方全體依照其角色\\n\u3000最大HP造成物理比例傷害，\\n\u3000同時大幅度降低物理攻擊力。\\n\u3000當道具是銅鈸時，對敵方全體依照其角色\\n\u3000最大HP造成物理比例傷害，\\n\u3000同時大幅度降低魔法攻擊力。\\n\u3000當道具是炸彈時，對敵方全體依照其角色\\n\u3000最大HP造成物理比例傷害，\\n\u3000並使其陷入詛咒狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧以最前方的敵人為中心，\\n\u3000對範圍內所有敵人造成中幅度的物理傷害，\\n\u3000並將其擊退。\\n‧對固定範圍內的所有敵人造成大幅度的物理傷害');
insert into unit_enemy_data values (310303, '反逆獸', 310300, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】追求愛之力而現身的一種負面魔法生物。\\n以人們心中的愛之力來扭曲世界並且帶來災厄。\\n\\n‧必殺技可以從異次元取出道具來進行攻擊。\\n\u3000隨著道具的不同，效果也會有所變化。\\n\u3000當道具是槌子時，對敵方全體依照其角色\\n\u3000最大HP造成物理比例傷害，\\n\u3000同時大幅度降低物理攻擊力。\\n\u3000當道具是銅鈸時，對敵方全體依照其角色\\n\u3000最大HP造成物理比例傷害，\\n\u3000同時大幅度降低魔法攻擊力。\\n\u3000當道具是炸彈時，對敵方全體依照其角色\\n\u3000最大HP造成物理比例傷害，\\n\u3000並使其陷入詛咒狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧以最前方的敵人為中心，\\n\u3000對範圍內所有敵人造成中幅度的物理傷害，\\n\u3000並將其擊退。\\n‧對固定範圍內的所有敵人造成大幅度的物理傷害\\n‧當反逆獸自身的HP低於50%時，\\n\u3000攻擊將會變得更加兇暴。');
insert into unit_enemy_data values (310304, '反逆獸', 310304, 0, 16, 270, 3000, 1, 4.2, 0, 0, 0, '【MODE1】藉由被闇之力所滲透的愛與慾望，\\n所幻化成實體的負面之魔法生物。\\n毫無慈悲地開始扭曲起了這個世界。\\n\\n‧必殺技可以從異次元取出道具來進行攻擊。\\n\u3000隨著道具的不同，效果也會有所變化。\\n\u3000當道具是槌子時，對敵方全體依照其角色\\n\u3000最大HP造成物理比例傷害，\\n\u3000同時大幅度降低物理攻擊力。\\n\u3000當道具是銅鈸時，對敵方全體依照其角色\\n\u3000最大HP造成物理比例傷害，\\n\u3000同時大幅度降低魔法攻擊力。\\n\u3000當道具是炸彈時，對敵方全體依照其角色\\n\u3000最大HP造成物理比例傷害，\\n\u3000並使其陷入詛咒狀態。\\n‧以最前方的敵人為中心，\\n\u3000對範圍內所有敵人造成中幅度的物理傷害，\\n\u3000並將其擊退。\\n‧對固定範圍內的所有敵人造成大幅度的物理傷害');
insert into unit_enemy_data values (310305, '反逆獸', 310300, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【MODE2】召喚出魔法少女的闇影。\\n以扭曲的負面與絕望之影，\\n對魔法少女們的愛與希望之力提出了考驗。\\n\\n‧它將靜靜地觀望闇影的戰鬥。');
insert into unit_enemy_data values (310306, '反逆獸', 310300, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【MODE3】負面之魔法生物因為憤怒終究解放了\\n真正的力量。只為了將魔法少女的心靈徹底粉碎，\\n並且用扭曲的愛之力將萬物踐踏於地。\\n\\n‧必殺技可以從異次元取出道具來進行攻擊。\\n\u3000隨著道具的不同，效果也會有所變化。\\n\u3000當道具是槌子時，對敵方全體依照其角色\\n\u3000最大HP造成物理比例傷害，\\n\u3000同時大幅度降低物理攻擊力。\\n\u3000當道具是銅鈸時，對敵方全體依照其角色\\n\u3000最大HP造成物理比例傷害，\\n\u3000同時大幅度降低魔法攻擊力。\\n\u3000當道具是炸彈時，對敵方全體依照其角色\\n\u3000最大HP造成物理比例傷害，\\n\u3000並使其陷入詛咒狀態。\\n‧以最前方的敵人為中心，\\n\u3000對範圍內所有敵人造成中幅度的物理傷害，\\n\u3000並將其擊退。\\n‧對固定範圍內的所有敵人造成大幅度的物理傷害\\n‧在戰鬥剛開始時，牠的攻擊\\n\u3000將會變得更加兇暴。');
insert into unit_enemy_data values (310307, '純真魔術師', 310307, 0, 16, 270, 3000, 1, 2.0, 0, 0, 0, '【物理】受塔之魔力引導的純真魔術師。\\n展現的魔術並非餘興節目，而是兇惡的兵器。\\n\\n‧戰鬥開始時，\\n\u3000中幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自純真魔術師所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技可以從異次元取出道具\\n\u3000來進行攻擊。隨著道具的不同，\\n\u3000效果也會有所變化。\\n\u3000當道具是槌子時，對敵方全體依照\\n\u3000其角色最大HP造成物理比例傷害，\\n\u3000同時大幅度降低物理攻擊力。\\n\u3000當道具是銅鈸時，對敵方全體依照\\n\u3000其角色最大HP造成物理比例傷害，\\n\u3000同時大幅度降低魔法攻擊力。\\n\u3000當道具是炸彈時，對敵方全體依照成\\n\u3000其角色最大HP造物理比例傷害，\\n\u3000同時使其陷入暈眩狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧以最前方的敵人為中心，對範圍內的\\n\u3000所有敵人造成中幅度的物理傷害，\\n\u3000同時造成擊退。\\n‧對固定範圍內的所有敵人造成中幅度的\\n\u3000物理傷害。\\n‧當純真魔術師自身的HP低於50％時，\\n\u3000其攻擊將會變得更加兇暴。');
insert into unit_enemy_data values (310400, '真假變色龍', 310400, 0, 11, 270, 1000, 1, 2.4, 0, 0, 0, '【物理】潛伏於雨林中靜候獵物上門\\n的變色龍魔物。儘管個性膽小狡猾，\\n但若生氣了將會以角進行突擊。\\n\\n‧必殺技能夠對全體敵人造成大幅度\\n\u3000的物理傷害，並且大幅度降低\\n\u3000物理防禦力。\\n\\n‧對隨機1名敵人造成\\n\u3000中幅度的物理傷害。\\n\\n‧當真假變色龍暫時擬態於背景時，\\n\u3000將會極大幅度提升迴避，特大幅度\\n\u3000提升行動速度，大幅度降低\\n\u3000物理防禦力。\\n\\n‧對前方的3名角色造成中幅度的物理\\n\u3000傷害。');
insert into unit_enemy_data values (310401, '真假變色龍', 310401, 0, 11, 270, 1000, 1, 2.4, 0, 0, 0, '【物理】潛伏於雨林中靜候獵物上門\\n的變色龍魔物。儘管個性膽小狡猾，\\n但若生氣了將會以角進行突擊。\\n\\n‧必殺技能夠對全體敵人造成大幅度\\n\u3000的物理傷害，並且大幅度降低\\n\u3000物理防禦力。\\n\\n‧對隨機1名敵人造成\\n\u3000中幅度的物理傷害。\\n\\n‧當真假變色龍暫時擬態於背景時，\\n\u3000將會極大幅度提升迴避，特大幅度\\n\u3000提升行動速度，大幅度降低\\n\u3000物理防禦力。\\n\\n‧對前方的3名角色造成中幅度的物理\\n\u3000傷害。');
