insert into unit_enemy_data values (310501, '喧囂獨角兔', 310500, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】將體色換成了亮麗顏色的大兔子魔物。\\n與乍看之下的外表大相逕庭的那歌聲，能夠震撼\\n大地並且動搖冒險者的心靈。\\n\\n‧必殺技能夠對敵方全體依照其角色最大\\n\u3000HP造成物理比例傷害，\\n\u3000並造成擊退。發動技能後，在頭暈目眩\\n\u3000期間內，大幅度降低自身的物理與\\n\u3000魔法防禦力。\\n‧以最前方的角色為中心，在一定範圍內\\n\u3000造成中幅度的物理傷害，同時降低\\n\u3000行動速度。');
insert into unit_enemy_data values (310502, '喧囂獨角兔', 310500, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】將體色換成了亮麗顏色的大兔子魔物。\\n與乍看之下的外表大相逕庭的那歌聲，能夠震撼\\n大地並且動搖冒險者的心靈。\\n\\n‧必殺技能夠對敵方全體依照其角色最大\\n\u3000HP造成物理比例傷害，\\n\u3000並造成擊退。發動技能後，在頭暈目眩\\n\u3000期間內，大幅度降低自身的物理與\\n\u3000魔法防禦力。\\n‧以最前方的角色為中心，在一定範圍內\\n\u3000造成中幅度的物理傷害，同時降低\\n\u3000行動速度。\\n‧對前方的3名角色造成小幅度的物理傷害，\\n\u3000同時大幅度降低物理防禦力。');
insert into unit_enemy_data values (310503, '喧囂獨角兔', 310500, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】將體色換成了亮麗顏色的大兔子魔物。\\n與乍看之下的外表大相逕庭的那歌聲，能夠震撼\\n大地並且動搖冒險者的心靈。\\n\\n‧必殺技能夠對敵方全體依照其角色最大\\n\u3000HP造成物理比例傷害，\\n\u3000並造成擊退。發動技能後，在頭暈目眩\\n\u3000期間內，大幅度降低自身的物理與\\n\u3000魔法防禦力。\\n‧以最前方的角色為中心，在一定範圍內\\n\u3000造成中幅度的物理傷害，同時降低\\n\u3000行動速度。\\n‧對前方的3名角色造成小幅度的物理傷害，\\n\u3000同時大幅度降低物理防禦力。\\n‧當喧囂獨角兔自身的HP不到40%時，\\n\u3000牠的攻擊將會變得更加兇暴。');
insert into unit_enemy_data values (310504, '喧囂獨角兔', 310500, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE1】召喚出new generations\\n的闇影。在森林中遇見的大兔子。\\n為了考驗三人那永不放棄的心是否能夠貫徹，\\n大兔子找出了她們每個人的軟弱。\\n\\n‧對最前方的角色造成物理傷害。');
insert into unit_enemy_data values (310505, '喧囂獨角兔', 310500, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE2】由於森林人氣王的歌聲，魔物們開始\\n大肆喧鬧。使人血脈賁張的野生宴會，\\n現正開幕。\\n\\n‧在打倒了青藍魔像、沉睡之梟、\\n\u3000戳戳鳥後，將能進到下一個MODE。\\n\\n‧喧囂獨角兔將會對最前方的角色造成\\n\u3000物理傷害。');
insert into unit_enemy_data values (310506, '喧囂獨角兔', 310500, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE3】宴會的終場，大兔竭盡了最後\\n之力。近乎為咆哮般震耳欲聾的歌聲，阻撓著三人邁向夢想\\n的道路。\\n\\n‧必殺技能夠對敵方全體依照其角色最大\\n\u3000HP造成物理比例傷害，\\n\u3000同時造成擊退。發動技能後，在頭暈目眩\\n\u3000期間內，大幅度降低自身的物理與\\n\u3000魔法防禦力。\\n‧以最前方的角色為中心，在一定範圍內\\n\u3000造成中幅度物理傷害，同時降低\\n\u3000行動速度。\\n‧對前方的3名敵人造成小幅度的物理傷害，\\n\u3000同時大幅度降低物理防禦力。\\n‧在戰鬥剛開始時，牠的攻擊將會變得\\n\u3000更加兇暴。');
insert into unit_enemy_data values (310507, '強音兔', 310507, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】吟唱世上極為奇妙歌曲的\\n大兔子魔獸。其歌聲並不值得稱讚，\\n反倒是莫大的困擾。\\n\\n‧必殺技會依照角色的最大HP，\\n\u3000對敵方全體造成\\n\u3000物理比例傷害，\\n\u3000並將其擊退。發動技能後，\\n\u3000在頭暈目眩期間內，\\n\u3000特大幅度降低自身的\\n\u3000物理防禦力與魔法防禦力。\\n\\n‧以最前方的角色為中心，\\n\u3000在一定範圍內\\n\u3000造成中幅度的物理傷害，\\n\u3000同時特大幅度降低\\n\u3000其行動速度，並使其暈眩。\\n\\n‧對面前的3名敵人造成\\n\u3000小幅度的物理傷害，\\n\u3000同時大幅度降低其物理防禦力。');
