insert into unit_enemy_data values (310601, '糾結的盆栽小花', 310600, 0, 16, 270, 3000, 1, 1.6, 310600, 310600, 0, '【魔法】不知是魔物還究竟是精靈，是有諸多謎團\\n的巨大生物。保持著那著難以言喻的表情一面散發\\n著謎之花粉，使著市中陷入了混亂。\\n\\n‧必殺技能夠對全體敵人造成大幅度的魔法傷害，\\n\u3000同時降低物理防禦力與魔法防禦力。\\n‧對前方範圍內的敵人造成物理傷害，同時造成\\n\u3000擊退。\\n‧當左右的葉子被破壞後，短時間內會被消滅。\\n‧當左側的葉子被破壞時，將會小幅度降低物理\\n\u3000防禦力與魔法防禦力。\\n‧當本體被破壞時，將會大幅度降低物理防禦力\\n\u3000與魔法防禦力。\\n‧當右側的葉子被破壞時，將會小幅度降低物理\\n\u3000防禦力與魔法防禦力。');
insert into unit_enemy_data values (310602, '糾結的盆栽小花', 310600, 0, 16, 270, 3000, 1, 1.6, 310600, 310600, 0, '【魔法】不知是魔物還究竟是精靈，是有諸多謎團\\n的巨大生物。保持著那著難以言喻的表情一面散發\\n著謎之花粉，使著市中陷入了混亂。\\n\\n‧必殺技能夠對全體敵人造成大幅度的魔法傷害，\\n\u3000同時降低物理防禦力與魔法防禦力。\\n‧對前方範圍內的敵人造成物理傷害，同時造成\\n\u3000擊退。\\n‧召喚小球根。\\n\u3000當小球根將無法被當成攻擊的對象，同時在經過\\n\u3000一段時間後將會自爆，並且對前方範圍內的角色\\n\u3000造成魔法傷害。\\n‧當左右的葉子被破壞後，短時間內會被消滅。\\n‧當左側的葉子被破壞時，將會小幅度降低物理\\n\u3000防禦力與魔法防禦力。\\n‧當本體被破壞時，將會大幅度降低物理防禦力\\n\u3000與魔法防禦力。\\n‧當右側的葉子被破壞時，將會小幅度降低物理\\n\u3000防禦力與魔法防禦力。');
