insert into unit_enemy_data values (310605, '糾結的盆栽小花', 310604, 0, 16, 270, 3000, 1, 1.6, 310600, 310600, 0, '【MODE1】最終極大化了的謎之生物。\\n依舊是保持那難以言喻的表情，並將大瀑布般的\\n淚水灑向於大地，那淚水則妄為地侵蝕著所有人。\\n\\n‧必殺技能夠對全體敵人造成大幅度的魔法傷害，\\n\u3000同時降低物理防禦力與魔法防禦力。\\n‧提升全體敵人的 TP，但同時展開造成\\n\u3000持續魔法傷害的領域。\\n‧當左右的葉子被破壞後，短時間內會被消滅。\\n‧當左側的葉子被破壞時，將會小幅度降低物理\\n\u3000防禦力與魔法防禦力。\\n‧當本體被破壞時，將會大幅度降低物理防禦力\\n\u3000與魔法防禦力。\\n‧當右側的葉子被破壞時，將會小幅度降低物理\\n\u3000防禦力與魔法防禦力。');
insert into unit_enemy_data values (310606, '糾結的盆栽小花', 310604, 0, 16, 270, 3000, 1, 1.6, 310600, 310600, 0, '【MODE2】不知是否是畏懼了冒險者們的抵抗，\\n那兩片大葉與巨大體軀露出了破綻並佇立著。\\n然而，卻還是依舊保持著那難以言喻的表情。\\n\\n‧使全部目標的破壞無效。\\n‧必殺技能夠對敵方全體造成大幅度的\\n\u3000魔法傷害，降低物理防禦力與\\n\u3000魔法防禦力。\\n‧對前方範圍內的敵人造成物理傷害，\\n\u3000同時造成擊退。\\n‧召喚小球根。\\n\u3000小球根無法被當成攻擊的對象，在經過\\n\u3000一段時間後將會自爆，並且對前方範圍內的角色\\n\u3000造成魔法傷害。\\n');
insert into unit_enemy_data values (310607, '糾結的盆栽小花', 310604, 0, 16, 270, 3000, 1, 1.6, 310600, 310600, 0, '【MODE3】那一成不變的表情，直到最後依舊\\n毫無變色，而冒險者對那生物的狂氣感到了\\n不寒而慄。究竟她們最後\\n能否解開這個生物之謎呢……？\\n\\n‧必殺技能夠對全體敵人造成大幅度的魔法傷害，\\n\u3000同時降低物理防禦力與魔法防禦力。\\n‧對前方範圍內的敵人造成物理傷害，同時造成\\n\u3000擊退。\\n‧召喚小球根。\\n\u3000當小球根將無法被當成攻擊的對象，同時在經過\\n\u3000一段時間後將會自爆，並且對前方範圍內的角色\\n\u3000造成魔法傷害。\\n‧當左右的葉子被破壞後，短時間內會被消滅。\\n‧當左側的葉子被破壞時，將會小幅度降低物理\\n\u3000防禦力與魔法防禦力。\\n‧當本體被破壞時，將會大幅度降低物理防禦力\\n\u3000與魔法防禦力。\\n‧當右側的葉子被破壞時，將會小幅度降低物理\\n\u3000防禦力與魔法防禦力。');
