insert into unit_enemy_data values (310705, '好朋友X', 310700, 0, 16, 270, 3000, 1, 1.4, 0, 0, 0, '【MODE2】鋼鐵的球體，以完美的切嚕與傾訴\\n著。「切～嚕～」。那緊緊攫起心靈不輕易鬆手的\\n洗腦話語，確確實實地將世界帶往了切嚕的未來。\\n\\n‧對隨機的 1 名角色造成中幅度的魔法傷害，\\n\u3000同時使其陷入沉默與誘惑狀態。');
insert into unit_enemy_data values (310706, '好朋友X', 310700, 0, 16, 270, 3000, 1, 1.4, 0, 0, 0, '【MODE3】鋼鐵的機關人偶再次聳立於大地。\\n解放了「雙刃之劍‧最終模式」的它，\\n將好朋友社的三人逼向了絕境。\\n\\n‧當自身受到中毒、猛毒、詛咒、火傷等效果時，\\n\u3000將會大幅度降低自身的物理防禦力與魔法\\n\u3000防禦力和行動速度。\\n‧必殺技能夠對敵方全體依照其角色最大HP\\n\u3000造成魔法比例傷害。\\n‧在提升自己的物理攻擊力之後，對全體\\n\u3000敵人造成物理傷害。\\n‧對前方的3名角色依照其最大HP造成魔法比例\\n\u3000傷害，同時降低行動速度。\\n‧在戰鬥剛開始時，牠的攻擊將會變得\\n\u3000更加兇暴。');
insert into unit_enemy_data values (310800, '忍者首領半藏', 310800, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】被惡質監察官所雇用的忍者首領。\\n失去了義的忍者，任憑身心墮落於金與利。然而\\n那依然健在的高超武技，阻擋了眾人的去路。\\n\\n‧在戰鬥開始後，監察官將會登場。而當\\n\u3000監察官受到傷害時，\\n\u3000將馬上特大幅度降低忍者首領半藏的\\n\u3000物理防禦力與魔法防禦力。\\n‧必殺技能夠對敵方全體依照其最大 HP\\n\u3000造成物理的比例傷害。\\n‧變化成大蟾蜍並且吐出毒，\\n\u3000使敵方全體陷入中毒狀態。\\n\u3000此攻擊必定會命中。\\n‧對敵方全體造成中幅度的物理傷害。');
insert into unit_enemy_data values (310801, '忍者首領半藏', 310800, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】被惡質監察官所雇用的忍者首領。\\n失去了義的忍者，任憑身心墮落於金與利。然而\\n那依然健在的高超武技，阻擋了眾人的去路。\\n\\n‧在戰鬥開始後，監察官將會登場。而當\\n\u3000監察官受到傷害時，\\n\u3000將馬上特大幅度降低忍者首領半藏的\\n\u3000物理防禦力與魔法防禦力。\\n‧必殺技能夠對敵方全體依照其最大 HP\\n\u3000造成物理的比例傷害。\\n‧變化成大蟾蜍並且吐出毒，\\n\u3000使敵方全體陷入中毒狀態。\\n\u3000此攻擊必定會命中。\\n‧對敵方全體造成中幅度的物理傷害。');
insert into unit_enemy_data values (310802, '忍者首領半藏', 310800, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】被惡質監察官所雇用的忍者首領。\\n失去了義的忍者，任憑身心墮落於金與利。然而\\n那依然健在的高超武技，阻擋了眾人的去路。\\n\\n‧在戰鬥開始後，監察官將會登場。而當\\n\u3000監察官受到傷害時，\\n\u3000將馬上特大幅度降低忍者首領半藏的\\n\u3000物理防禦力與魔法防禦力。\\n‧必殺技能夠對敵方全體依照其最大 HP\\n\u3000造成物理的比例傷害。\\n‧變化成大蟾蜍並且吐出毒，\\n\u3000使敵方全體陷入中毒狀態。\\n\u3000當自身的 HP 在 30% 以下時，\\n\u3000使全體敵人陷入猛毒的狀態。\\n\u3000此攻擊必定會命中。\\n‧對敵方全體造成中幅度的物理傷害。\\n\u3000當自身的 HP 在 30% 以下時，\\n\u3000同時再對敵方全體造成暈眩。\\n‧當忍者首領半藏自身的 HP 低於30%時，\\n\u3000他的技能效果將會有所變化。');
insert into unit_enemy_data values (310803, '忍者首領半藏', 310800, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】被惡質監察官所雇用的忍者首領。\\n失去了義的忍者，任憑身心墮落於金與利。然而\\n那依然健在的高超武技，阻擋了眾人的去路。\\n\\n‧在戰鬥開始後，監察官將會登場。而當\\n\u3000監察官受到傷害時，\\n\u3000將馬上特大幅度降低忍者首領半藏的\\n\u3000物理防禦力與魔法防禦力。\\n‧必殺技能夠對敵方全體依照其最大 HP\\n\u3000造成物理的比例傷害。\\n‧變化成大蟾蜍並且吐出毒，\\n\u3000使敵方全體陷入中毒狀態。\\n\u3000當自身的 HP 在 30% 以下時，\\n\u3000使全體敵人陷入猛毒的狀態。\\n\u3000此攻擊必定會命中。\\n‧對敵方全體造成中幅度的物理傷害。\\n\u3000當自身的 HP 在 30% 以下時，\\n\u3000同時再對敵方全體造成暈眩。\\n‧當忍者首領半藏自身的 HP 低於30%時，\\n\u3000他的技能效果將會有所變化。');
insert into unit_enemy_data values (310804, '忍者首領半藏', 310801, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【MODE1】召喚出【伊麗莎白牧場】成員們的\\n闇影。利用幻術所製造出來的四農士之幻影，\\n又再次使眾人們的歸所陷入了絕境。\\n\\n‧對面前的 1 名敵人造成物理傷害。');
insert into unit_enemy_data values (310805, '忍者首領半藏', 310800, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【MODE2】以秘傳的通靈之術幻化成了忍蛙，\\n並且將蟾蜍的猛毒噴灑至週遭。人們只要吸入了\\n該猛毒，便將侵蝕全身，最後身體將徐徐地潰爛。\\n\\n‧在戰鬥開始後，監察官將會登場。而當\\n\u3000監察官受到傷害時，將馬上特大幅度降低\\n\u3000忍者首領半藏的物理防禦力與魔法防禦力。\\n‧必殺技能夠對全體敵人依照其最大 HP\\n\u3000造成物理的比例傷害。\\n‧變化成大蟾蜍並且吐出劇毒，使全體敵人陷入\\n\u3000猛毒狀態。此攻擊必定會命中。\\n‧對全體敵人造成中幅度的物理傷害，同時對全體\\n\u3000敵人造成暈眩。');
insert into unit_enemy_data values (310806, '忍者首領半藏', 310800, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【MODE3】他最終取回了身為忍者的尊嚴，\\n並且發揮出了自身真正的力量。\\n那電光一閃的神技，使人在慘叫都喊不了聲\\n就化為了屍骸。\\n\\n‧在戰鬥剛開始時，提升自己的技能威力。\\n‧必殺技能夠對全體敵人依照其最大 HP\\n\u3000造成物理的比例傷害。\\n‧變化成大蟾蜍並且吐出劇毒，使全體敵人陷入\\n\u3000猛毒狀態。此攻擊必定會命中。\\n‧對全體敵人造成中幅度的物理傷害，同時對全體\\n\u3000敵人造成暈眩。');
insert into unit_enemy_data values (310900, '襲擊了蘭德索爾各地的神秘圓盤', 310900, 0, 16, 270, 3000, 1, 0.1, 0, 0, 0, '【物理】突然襲擊了和平的豐年祭的謎之飛行物體。\\n突如其來在蘭德索爾各地現身，進行著破壞與掠奪。\\n\\n‧必殺技能夠對全體敵人造成大幅度的物理傷害。\\n‧對前方的1名角色造成小幅度的物理傷害。\\n‧對剩餘HP較高的1名角色\\n\u3000造成小幅度物理傷害。\\n‧對隨機的1名角色造成小幅度的物理傷害。');
insert into unit_enemy_data values (311000, '城堡多倫多獸', 311000, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】以塔之魔力產生變異而要塞化的巨大魔樹。\\n將大地之力幻化成砲彈，以那堅固的身軀為根基，\\n進行著永無止境的破壞。\\n\\n‧對全體敵人造成大幅度的物理傷害。\\n‧對範圍內的全體敵人造成持續的魔法傷害，\\n\u3000同時展開降低物理防禦力的領域。\\n‧以從後方數來第 2 名的角色為中心，造成\\n\u3000小範圍內中幅度的物理傷害，並且減少其 TP。\\n\u3000並且在那之後，恢復全體夥伴的 TP。');
insert into unit_enemy_data values (311001, '城堡多倫多獸', 311001, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】以塔之魔力產生變異而要塞化的巨大魔樹。\\n將大地之力幻化成砲彈，以那堅固的身軀為根基，\\n進行著永無止境的破壞。\\n\\n‧戰鬥開始時，中幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，在戰鬥中\\n\u3000不會受到防禦力下降技能的影響。\\n‧中幅度降低來自城堡多倫多獸所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會解除敵方全體的護盾，\\n\u3000同時造成大幅度的物理傷害。\\n\u3000此技能所造成的傷害，不會使敵人恢復TP。\\n‧展開能夠特大幅度降低後方範圍內\\n\u3000敵方全體物理防禦力的領域。\\n‧以距離最遠的1名角色為中心，\\n\u3000對小範圍內造成大幅度的魔法傷害，\\n\u3000同時中幅度降低其TP。\\n\u3000之後，依照此技能造成傷害的敵人數量\\n\u3000恢復自身的TP。\\n‧對前方的1名角色造成大幅度的物理傷害。');
insert into unit_enemy_data values (311002, '城堡多倫多獸', 311002, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】以塔之魔力產生變異而要塞化的巨大魔樹。\\n將大地之力幻化成砲彈，以那堅固的身軀為根基，\\n進行著永無止境的破壞。\\n\\n‧戰鬥開始時，中幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，在戰鬥中\\n\u3000不會受到防禦力下降技能的影響。\\n‧中幅度降低來自城堡多倫多獸所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會解除敵方全體的護盾，\\n\u3000同時造成大幅度的物理傷害，並且賦予暈眩狀態。\\n\u3000此技能所造成的傷害，不會使敵人恢復TP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧展開能夠特大幅度降低後方範圍內\\n\u3000敵方全體物理防禦力的領域。\\n‧以距離最遠的1名角色為中心，\\n\u3000對小範圍內造成大幅度的魔法傷害，\\n\u3000同時中幅度降低其TP。\\n\u3000之後，依照此技能造成傷害的敵人數量\\n\u3000恢復自身的TP。\\n‧對前方的1名角色造成大幅度的物理傷害。');
insert into unit_enemy_data values (311100, '黑之王賈巴沃克', 311100, 0, 16, 270, 1000, 1, 2.1, 0, 0, 0, '【物理】為了扭曲世界而從狹界中顯現的黑之王。\\n打算用身上纏繞的暗黑瘴氣蹂躪不可思議之國。\\n\\n‧必殺技為對全體敵人依照其最大HP\\n\u3000造成魔法比例傷害，施加毒狀態，\\n\u3000並將其擊退。\\n\u3000此技能所造成的狀態異常，即使傷害無效\\n\u3000也會施加。\\n‧對前方的2名角色造成中幅度的物理傷害，\\n\u3000同時降低物理防禦力與TP。');
insert into unit_enemy_data values (311101, '黑之王賈巴沃克', 311100, 0, 16, 270, 1000, 1, 2.1, 0, 0, 0, '【物理】為了扭曲世界而從狹界中顯現的黑之王。\\n打算用身上纏繞的暗黑瘴氣蹂躪不可思議之國。\\n\\n‧必殺技為對敵方全體依照其最大HP\\n\u3000造成魔法比例傷害，施加毒狀態，\\n\u3000並將其擊退。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對前方的2名角色造成中幅度的物理傷害，\\n\u3000同時中幅度降低物理防禦力與TP。');
insert into unit_enemy_data values (311102, '黑之王賈巴沃克', 311100, 0, 16, 270, 1000, 1, 2.1, 0, 0, 0, '【物理】為了扭曲世界而從狹界中顯現的黑之王。\\n打算用身上纏繞的暗黑瘴氣蹂躪不可思議之國。\\n\\n‧必殺技為對敵方全體依照其最大HP\\n\u3000造成魔法比例傷害，施加毒狀態，\\n\u3000並將其擊退。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對前方的2名角色造成中幅度的物理傷害，\\n\u3000同時中幅度降低物理防禦力與TP。\\n‧對前方範圍內的角色造成中幅度的魔法傷害，\\n\u3000同時大幅度降低魔法防禦力。\\n\u3000角色HP低於50％的時候，\\n\u3000還會降低行動速度。');
insert into unit_enemy_data values (311103, '黑之王賈巴沃克', 311100, 0, 16, 270, 1000, 1, 2.1, 0, 0, 0, '【物理】為了扭曲世界而從狹界中顯現的黑之王。\\n打算用身上纏繞的暗黑瘴氣蹂躪不可思議之國。\\n\\n‧必殺技為對敵方全體依照其最大HP\\n\u3000造成魔法比例傷害，施加毒狀態，\\n\u3000並將其擊退。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對前方的2名角色造成中幅度的物理傷害，\\n\u3000同時中幅度降低物理防禦力與TP。\\n‧對前方範圍內的角色造成中幅度的魔法傷害，\\n\u3000同時大幅度降低魔法防禦力。\\n\u3000角色HP低於50％的時候，\\n\u3000還會降低行動速度。\\n‧當黑之王賈巴沃克自身的HP低於35％時，\\n\u3000牠的攻擊將會變得更加兇暴。');
insert into unit_enemy_data values (311104, '黑之王賈巴沃克', 311101, 0, 16, 270, 1000, 1, 2.1, 0, 0, 0, '【MODE1】瘴黑之幕纏繞於身，黑之王再次\\n降臨。黑暗與混沌混合的障壁，據說就連\\n救世主的光之一射也能承受。\\n\\n‧展開護盾，使其不會受到敵人造成的任何攻擊。\\n\u3000護盾受到攻擊時會漸漸降低耐久，\\n\u3000受到2400000傷害時護盾\\n\u3000就會被破壞。\\n‧必殺技為對敵方全體依照其最大HP\\n\u3000造成魔法比例傷害，施加中毒狀態，\\n\u3000並將其擊退。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對前方範圍內的角色造成中幅度的魔法傷害，\\n\u3000同時大幅降低魔法防禦力。');
insert into unit_enemy_data values (311105, '黑之王賈巴沃克', 311100, 0, 16, 270, 1000, 1, 2.1, 0, 0, 0, '【MODE2】召喚璃乃、步未、日和的闇影。\\n將心靈的黑暗實體化，揭露自己的弱點。\\n她們能夠取回希望之心嗎？如今正被如此詢問。\\n\\n‧對前方的1名敵人造成物理傷害。');
insert into unit_enemy_data values (311106, '黑之王賈巴沃克', 311100, 0, 16, 270, 1000, 1, 2.1, 0, 0, 0, '【MODE3】黑之王解放終結的力量，要讓世界\\n墮入永恆的黑暗。將書本的頁面盡數染黑，\\n救世主的故事也將迎來終結。\\n\\n‧必殺技為對敵方全體依照其最大HP\\n\u3000造成魔法比例傷害，施加中毒狀態，\\n\u3000並將其擊退。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對前方的2名角色造成中幅度的物理傷害，\\n\u3000同時降低物理防禦力與TP。\\n‧對前方範圍內的角色造成中幅度的魔法傷害，\\n\u3000同時大幅降低魔法防禦力。\\n\u3000角色HP低於50%的時候，\\n\u3000還會大幅降低行動速度。\\n‧在戰鬥剛開始時，\\n\u3000牠的攻擊將會變得更加兇暴。');
insert into unit_enemy_data values (311200, '花朵騎士', 311200, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】擬態成巨大花朵的魔物。\\n不小心被牠的美麗所吸引的獵物，\\n將會面臨盛開的絕望之光。\\n\\n‧對前方的3名角色造成大幅度的\\n\u3000物理傷害，再對全體敵人\\n\u3000造成大幅度物理傷害。\\n\\n‧對前方的2名角色造成小幅度的\\n\u3000物理傷害，並且中幅度提升自己的\\n\u3000物理攻擊力。\\n\\n‧對前方的第1名角色造成中幅度的\\n\u3000物理傷害，同時中幅度降低\\n\u3000物理防禦力。\\n\\n‧對前方的第2名角色造成中幅度的\\n\u3000物理傷害，同時中幅度降低物\\n\u3000理防禦力。');
insert into unit_enemy_data values (311201, '花朵騎士', 311200, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】擬態成巨大花朵的魔物。\\n不小心被牠的美麗所吸引的獵物，\\n將會面臨盛開的絕望之光。\\n\\n‧對前方的3名角色造成大幅度的\\n\u3000物理傷害，再對全體敵人\\n\u3000造成大幅度物理傷害。\\n\\n‧對前方的2名角色造成小幅度的\\n\u3000物理傷害，並且中幅度提升自己的\\n\u3000物理攻擊力。\\n\\n‧對前方的第1名角色造成中幅度的\\n\u3000物理傷害，同時中幅度降低\\n\u3000物理防禦力。\\n\\n‧對前方的第2名角色造成中幅度的\\n\u3000物理傷害，同時中幅度降低物\\n\u3000理防禦力。');
insert into unit_enemy_data values (311202, '花朵騎士', 311202, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】擬態成巨大花朵的魔物。\\n不小心被牠的美麗\\n所吸引的獵物，將會面臨盛開的絕望之光。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧中幅度降低來自花朵騎士\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧必殺技會以距離最近的1名敵人為中心，\\n\u3000造成小範圍的特大幅度物理傷害。\\n\u3000之後再依照此技能給予的傷害量，\\n\u3000對敵方全體造成物理傷害。\\n‧特大幅度提升自身的物理攻擊力與魔法防禦力，\\n\u3000且小幅度恢復TP。\\n‧普通攻擊會以距離最近的1名敵人為中心，\\n\u3000對小範圍內最多2名敵人\\n\u3000造成特大幅度物理傷害。');
insert into unit_enemy_data values (311203, '花朵騎士', 311203, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】擬態成巨大花朵的魔物。\\n不小心被牠的美麗\\n所吸引的獵物，將會面臨盛開的絕望之光。\\n\\n‧戰鬥開始時，\\n\u3000中幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，展\\n\u3000開小幅度降低敵方全體TP上升的領域。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少50％效果。\\n‧中幅度降低來自花朵騎士\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧大幅度降低來自敵方\\n\u3000所有傷害產生的花朵騎士TP恢復量。\\n‧必殺技會以距離最近的1名敵人為中心，\\n\u3000造成小範圍的特大幅度物理傷害。\\n\u3000之後再依照此技能給予的傷害量，\\n\u3000對敵方全體造成物理傷害。\\n\u3000此技能造成的全體傷害將不會恢復TP。\\n‧特大幅度提升自身的物理攻擊力、物理防禦力\\n\u3000與魔法防禦力，且中幅度恢復TP。\\n‧普通攻擊會以距離最近的1名敵人為中心，\\n\u3000對小範圍內最多2名敵人\\n\u3000造成特大幅度物理傷害。');
