insert into unit_enemy_data values (311301, '覺醒‧花心星', 311300, 0, 16, 270, 3000, 2, 2.8, 0, 0, 0, '【魔法】利用心靈空虛的間隙侵入依附的\\n星之精靈。以過剩的自我意識跟純潔的真心\\n迷惑對手。\\n\\n‧必殺技會對敵方全體造成依照花心星保有的\\n\u3000【二心】數量的魔法比例\\n\u3000傷害。\\n\u3000另外花心星剩餘HP未滿50％的時候，\\n\u3000更進一步提升造成的傷害。\\n\u3000技能發動後，花心星保有的\\n\u3000【二心】數量將會歸零。\\n‧以最後方的敵人為中心，對一定範圍內\\n\u3000造成小幅度的魔法傷害，再施加誘惑狀態\\n\u3000並小幅度提升物理攻擊力與魔法攻擊力。\\n\u3000此技能造成傷害的敵人數量\\n\u3000會在一定時間內為花心星增加【二心】。\\n‧擊退前方範圍內的敵人，\\n\u3000造成小幅度的魔法傷害。\\n\u3000並且使其陷入誘惑狀態並小幅度提升\\n\u3000物理攻擊力與魔法攻擊力。\\n\u3000此技能造成傷害的敵人數量\\n\u3000會在一定時間內為花心星增加【二心】。');
insert into unit_enemy_data values (311302, '覺醒‧花心星', 311300, 0, 16, 270, 3000, 2, 3.0, 0, 0, 0, '【魔法】利用心靈空虛的間隙侵入依附的\\n星之精靈。以過剩的自我意識跟純潔的真心\\n迷惑對手。\\n\\n‧必殺技會對敵方全體造成依照花心星保有的\\n\u3000【二心】數量的魔法比例\\n\u3000傷害。\\n\u3000另外花心星剩餘HP未滿50％的時候，\\n\u3000更進一步提升造成的傷害。\\n\u3000技能發動，花心星保有的\\n\u3000【二心】數量將會歸零。\\n‧以最後方的敵人為中心，對一定範圍內\\n\u3000造成小幅度的魔法傷害，再施加誘惑狀態\\n\u3000並小幅度提升物理攻擊力與魔法攻擊力。\\n\u3000此技能造成傷害的敵方數量\\n\u3000會在一定時間內為花心星增加【二心】。\\n‧擊退前方範圍內的敵人，\\n\u3000造成小幅度的魔法傷害。\\n\u3000並且使其陷入誘惑狀態並小幅度提升\\n\u3000物理攻擊力，中幅度提升魔法攻擊力。\\n\u3000此技能造成傷害的敵人數量\\n\u3000會在一定時間內為花心星增加【二心】。');
