insert into unit_enemy_data values (311305, '覺醒‧花心星', 311300, 0, 16, 270, 3000, 2, 3.0, 0, 0, 0, '【MODE2】連耀眼明星都會眼花，對自己\\n光芒的完全自信，這份盲信和少女的思念\\n將整個銀河捲入，並且迷惑一切。\\n\\n‧必殺技對敵方全體造成依照花心星保有的\\n\u3000【二心】數量的魔法比例傷害。\\n\u3000另外花心星剩餘HP未滿60%的時候，\\n\u3000更進一步提升造成的傷害。\\n\u3000造成傷害後，花心星保有的\\n\u3000【二心】數量將會歸零。\\n‧以最前方的敵方為中心，\\n\u3000對一定範圍內造成魔法傷害，\\n\u3000並且使其陷入誘惑狀態\\n\u3000並提升物理攻擊力與魔法攻擊力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n\u3000此技能造成傷害的敵人數量\\n\u3000會在一定時間內為花心星增加【二心】。');
insert into unit_enemy_data values (311306, '覺醒‧花心星', 311300, 0, 16, 270, 3000, 2, 3.0, 0, 0, 0, '【MODE3】在夢想中的後宮夢碎之後，想讓一切\\n歸於虛無而解放了真正的力量。為了讓銀河的\\n星星們全數沉沒，散布著全部的真心。\\n\\n‧必殺技為對敵方全體造成依照花心星保有的\\n\u3000【二心】數量的魔法比例傷害。\\n\u3000另外在戰鬥剛開始之後，\\n\u3000更進一步提升造成的傷害。\\n\u3000造成傷害後，花心星保有的\\n\u3000【二心】數量將會歸零。\\n‧對敵方全體依照角色的最大HP造成\\n\u3000魔法比例傷害。\\n‧以最後方的敵方為中心，\\n\u3000對一定範圍內造成魔法傷害，\\n\u3000並且使其陷入誘惑狀態\\n\u3000並提升物理攻擊力與魔法攻擊力。\\n\u3000此技能造成傷害的敵人數量\\n\u3000會在一定時間內為花心星增加【二心】。\\n‧擊退前方範圍內的敵方，\\n\u3000造成魔法傷害。\\n\u3000並且使其陷入誘惑狀態並提升物理攻擊力與\\n\u3000魔法攻擊力。\\n\u3000此技能造成傷害的敵方數量\\n\u3000會在一定時間內為花心星增加【二心】。');
