insert into unit_enemy_data values (311401, '海岸暴君‧鮫吉', 311400, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】佔領海灘一帶的近海區域的海洋族暴君。\\n對找上門的架從來不曾轉身逃避，\\n絕對不會原諒破壞島嶼的傢伙。\\n\\n‧必殺技會對敵方全體依照其最大HP\\n\u3000造成物理比例傷害，降低物理防禦力，\\n\u3000與提升物理攻擊力。\\n‧對前方範圍內的敵方全體\\n\u3000造成大幅度的物理傷害，\\n\u3000並且降低物理防禦力。\\n‧擊退敵方全體。之後的一定時間內，\\n\u3000在自己身上纏繞雷電，極大幅度提升自己的\\n\u3000物理防禦力與魔法防禦力。\\n\u3000鮫吉受到中毒、猛毒、詛咒、火傷、咒術等\\n\u3000效果時，物理防禦力與魔法防禦力\\n\u3000提升的時間將會縮短。');
insert into unit_enemy_data values (311402, '海岸暴君‧鮫吉', 311400, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】佔領海灘一帶的近海區域的海洋族暴君。\\n對找上門的架從來不曾轉身逃避，\\n絕對不會原諒來地盤搗亂的傢伙。\\n\\n‧必殺技會對敵方全體依照其最大HP\\n\u3000造成物理比例傷害，\\n\u3000降低物理防禦力，\\n\u3000與提升物理攻擊力。\\n‧對前方範圍內的敵方全體\\n\u3000造成大幅度的物理傷害，\\n\u3000降低物理防禦力。\\n‧擊退敵方全體。之後的一定時間內，\\n\u3000在自己身上纏繞雷電，極大幅度提升自身的\\n\u3000物理防禦力與魔法防禦力。\\n\u3000鮫吉受到中毒、猛毒、詛咒、火傷、咒術等\\n\u3000效果時，自身的物理防禦力與魔法防禦力\\n\u3000提升的時間將會縮短。\\n‧當鮫吉自身的HP低於20％時，對敵方全體\\n\u3000依照其最大HP造成物理比例傷害，\\n\u3000施加一定時間內無法使用必殺技\\n\u3000的狀態，並降低魔法攻擊力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n\u3000並且攻擊將會變得更加凶暴。');
insert into unit_enemy_data values (311403, '海岸暴君‧鮫吉', 311400, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】佔領海灘一帶的近海區域的海洋族暴君。\\n對找上門的架從來不曾轉身逃避，\\n絕對不會原諒來地盤搗亂的傢伙。\\n\\n‧必殺技會對敵方全體依照其最大HP\\n\u3000造成物理比例傷害，\\n\u3000大幅度降低物理防禦力，\\n\u3000與提升物理攻擊力。\\n‧對前方範圍內的敵方全體\\n\u3000造成大幅度的物理傷害，\\n\u3000降低物理防禦力。\\n‧擊退敵方全體。之後的一定時間內，\\n\u3000在自己身上纏繞雷電，極大幅度提升自身的\\n\u3000物理防禦力與魔法防禦力。\\n\u3000鮫吉受到中毒、猛毒、詛咒、火傷、咒術等\\n\u3000效果時，自身的物理防禦力與魔法防禦力\\n\u3000提升的時間將會縮短。\\n‧當鮫吉自身的HP低於20％時，對敵方全體\\n\u3000依照其最大HP造成物理比例傷害，\\n\u3000施加一定時間內無法使用必殺技\\n\u3000的狀態，並降低魔法攻擊力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n\u3000並且攻擊將會變得更加凶暴。');
insert into unit_enemy_data values (311404, '海岸暴君‧鮫吉', 311404, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【MODE1】對於搗亂地盤的無禮之徒，解放從\\n數千抗爭中鍛鍊出的鋼鐵肉體內暗藏的力量，\\n以全身全靈的認真對決將其轟飛。\\n\\n‧必殺技能夠對敵方全體依照角色的最大HP\\n\u3000造成物理比例傷害，\\n\u3000降低其物理防禦力，\\n\u3000與提升物理攻擊力。\\n‧對前方範圍內的敵方全體\\n\u3000造成大幅度的物理傷害，\\n\u3000並且大幅度降低其物理防禦力。\\n‧對前方3名角色造成大幅度的物理傷害。並且\\n\u3000也對自身造成物理傷害，一定時間內\\n\u3000極大幅度降低物理防禦力與魔法防禦力。');
insert into unit_enemy_data values (311405, '海岸暴君‧鮫吉', 311405, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【MODE2】集結因為牠壓倒性的毅力和\\n絕望性的暴力而跟隨在牠身後的小弟們。\\n集結海洋族精銳的鬥毆，現在正要開幕。\\n\\n‧鮫吉會對面前的1名敵人進行物理攻擊。\\n‧對前方3名角色造成大幅度的物理傷害。並且\\n\u3000也對自身造成物理傷害，一定時間內\\n\u3000極大幅度降低物理防禦力與魔法防禦力。');
insert into unit_enemy_data values (311406, '海岸暴君‧鮫吉', 311404, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【MODE3】隨著兇猛的吼叫聲，暴君開始了\\n不斷的追擊。直到將敵對自己的人徹底揍扁\\n為止都不會停下，不斷落下的鐵拳如同暴風。\\n\\n‧必殺技能夠對敵方全體依照其最大HP\\n\u3000造成物理比例傷害，\\n\u3000大幅度降低其物理防禦力，\\n\u3000與提升物理攻擊力。\\n‧對前方範圍內的敵方全體\\n\u3000造成大幅度的物理傷害，\\n\u3000並且降低其物理防禦力。\\n‧擊退敵方全體。之後的一定時間內，\\n\u3000在自己身上纏繞雷電，極大幅度提升自身的\\n\u3000物理防禦力與魔法防禦力。\\n\u3000鮫吉受到中毒、猛毒、詛咒、火傷等效果時，\\n\u3000自身的物理防禦力與魔法防禦力提升的時間\\n\u3000將會縮短。\\n‧當鮫吉自身的HP低於10%時，對敵方全體\\n\u3000依照角色的最大HP造成物理比例傷害，\\n\u3000施加一定時間內無法使用必殺技\\n\u3000的狀態，並降低其魔法攻擊力。\\n\u3000此技能所造成的狀態異常，即使傷害無效\\n\u3000也會施加。\\n\u3000並且攻擊將會變得更加激烈且凶暴。');
insert into unit_enemy_data values (311407, '憤怒之顎', 311407, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】因塔之魔力而狂化海洋族狂戰士。\\n猙獰吼叫的同時，不斷揮出兇猛的拳擊。\\n\\n‧必殺技能對全體敵人依照其\\n\u3000最大HP造成物理比例傷害，\u3000\\n\u3000並且大幅度降低其物理防禦力，\\n\u3000同時中幅度提升自身的物理攻擊力。\\n‧以距離最近的1名角色為中心，對小範圍內的\\n\u3000所有敵人造成大幅度的物理傷害，\\n\u3000並且使其物理防禦力大幅度下降。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧擊退敵方全體。之後的一定時間內，\\n\u3000在自己身上纏繞雷電，極大幅度提升自身的\\n\u3000物理防禦力與魔法防禦力。\\n\u3000憤怒之顎受到中毒、猛毒、詛咒、咒術、火傷等\\n\u3000效果時，自身的物理防禦力與魔法防禦力\\n\u3000提升的時間將會縮短。\\n‧憤怒之顎自身的HP低於40％時，\\n\u3000對敵方全體依照其最大HP\\n\u3000造成物理比例傷害，\\n\u3000並施加一定時間內無法使用必殺技的狀態，\\n\u3000同時在戰鬥中大幅度降低魔法攻擊力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n\u3000同時攻擊將會變得更加凶暴。');
