insert into unit_enemy_data values (311501, '失控的純潔之弓', 311500, 0, 16, 270, 3000, 2, 2.4, 0, 0, 0, '【魔法】因為過多的無邪願望而失控的聖弓。\\n若被其箭矢貫穿，將會化為無力的天使。\\n\\n‧必殺技能夠對敵方全體依照其最大HP\\n\u3000造成魔法的比例傷害，再對陷入天使\\n\u3000狀態的角色施加詛咒狀態。\\n\u3000此效果施加的詛咒狀態所造成的傷害\\n\u3000將不會恢復TP。\\n‧對HP最低的3名角色依照最大HP造成\\n\u3000魔法的比例傷害，再對HP低於60％的角色\\n\u3000於一定時間內施加天使狀態。\\n\u3000陷入天使狀態的角色，會變成只能\\n\u3000進行普通攻擊，並且會解除身上的\\n\u3000狀態增益效果。');
insert into unit_enemy_data values (311502, '失控的純潔之弓', 311500, 0, 16, 270, 3000, 2, 2.4, 0, 0, 0, '【魔法】因為過多的無邪願望而失控的聖弓。\\n若被其箭矢貫穿，將會化為無力的天使。\\n\\n‧必殺技會對敵方全體依照其最大HP\\n\u3000造成魔法比例傷害，再對陷入天使\\n\u3000狀態的角色施加詛咒狀態。\\n\u3000此效果施加的詛咒狀態所造成的傷害\\n\u3000將不會恢復TP。\\n‧對HP最低的3名角色依照其最大HP造成\\n\u3000魔法比例傷害，再對HP低於60％的角色\\n\u3000於一定時間內施加天使狀態。\\n\u3000陷入天使狀態的角色，會變成只能\\n\u3000進行普通攻擊，並且會解除身上的\\n\u3000能力提升效果。\\n‧對HP最高的2名角色造成中幅度的魔法傷害，\\n\u3000同時中幅度降低行動速度。\\n‧當純潔之弓自身的HP低於50％時，\\n\u3000小幅度提升魔法攻擊力，\\n\u3000並且攻擊將會變得更加凶暴。');
insert into unit_enemy_data values (311503, '失控的純潔之弓', 311500, 0, 16, 270, 3000, 2, 2.4, 0, 0, 0, '【魔法】因為過多的無邪願望而失控的聖弓。\\n若被其箭矢貫穿，將會化為無力的天使。\\n\\n‧必殺技會對敵方全體依照其最大HP\\n\u3000造成魔法的比例傷害，再對陷入天使\\n\u3000狀態的角色施加詛咒狀態。\\n\u3000此效果施加的詛咒狀態所造成的傷害\\n\u3000將不會恢復TP。\\n‧對HP最低的3名角色依照其最大HP造成\\n\u3000魔法比例傷害，再對HP低於60％的角色\\n\u3000於一定時間內施加天使狀態。\\n\u3000陷入天使狀態的角色，會變成只能\\n\u3000進行普通攻擊，並且會解除身上的\\n\u3000能力提升效果。\\n‧對HP最高的2名角色造成中幅度的魔法傷害，\\n\u3000同時中幅度降低行動速度。\\n‧當純潔之弓自身的HP低於50％時，\\n\u3000中幅度提升魔法攻擊力與TP，\\n\u3000並且攻擊將會變得更加凶暴。');
insert into unit_enemy_data values (311504, '失控的純潔之弓', 311500, 0, 16, 270, 3000, 2, 2.4, 0, 0, 0, '【MODE1】扭曲的心願化成的結晶，令人們\\n陷入瘋狂。這顆結晶能夠封殺所有的進攻。\\n\\n‧在戰鬥開始時，將會召喚出心願扭曲的水晶。\\n‧心願扭曲的水晶在戰鬥中存在的時候，\\n\u3000大幅度提升純潔之弓的物理防禦力及\\n\u3000魔法防禦力。\\n‧心願扭曲的水晶遭到破壞時，\\n\u3000大幅度降低純潔之弓的物理防禦力及\\n\u3000魔法防禦力。\\n‧必殺技能夠對敵方全體依照其最大HP\\n\u3000造成魔法的比例傷害。\\n‧對HP最高的2名角色造成中幅度的魔法傷害，\\n\u3000降低行動速度，\\n\u3000同時降低魔法防禦力。');
insert into unit_enemy_data values (311505, '失控的純潔之弓', 311500, 0, 16, 270, 3000, 2, 2.4, 0, 0, 0, '【MODE2】召喚依里與茜里的闇影。\\n以自身負面姿態，對雙胞胎真正的羈絆進行試煉。\\n\\n‧對HP最低的3名角色依照最大HP造成\\n\u3000魔法的比例傷害，再對HP低於60％的\\n\u3000角色於一定時間內\u3000施加天使狀態。\\n\u3000陷入天使狀態的角色，會變成只能\\n\u3000進行普通攻擊，並且會解除身上的\\n\u3000狀態增益效果。');
insert into unit_enemy_data values (311506, '失控的純潔之弓', 311500, 0, 16, 270, 3000, 2, 2.4, 0, 0, 0, '【MODE3】受到無法實現願望所囚禁，將聖弓\\n的權能臻至極限。解放的力量能夠撼動天地，\\n滅卻一切邪念。\\n\\n‧必殺技能夠對敵方全體依照其最大HP\\n\u3000造成魔法的比例傷害，再對陷入天使\\n\u3000狀態的角色施加詛咒狀態。\\n\u3000此效果施加的詛咒狀態所造成的傷害\\n\u3000將不會恢復TP。\\n‧對HP最低的3名角色依照最大HP造成\\n\u3000魔法的比例傷害，再對HP低於60％的\\n\u3000角色於一定時間內\u3000施加天使狀態。\\n\u3000陷入天使狀態的角色，會變成只能\\n\u3000進行普通攻擊，並且會解除身上的\\n\u3000狀態增益效果。\\n‧對HP最高的2名角色造成中幅度的魔法傷害，\\n\u3000同時降低行動速度，\\n\u3000並且降低魔法防禦力。\\n‧當剩餘時間低於35秒時，提升魔法攻擊力\\n\u3000與TP，攻擊也會變得更加兇暴。');
insert into unit_enemy_data values (311600, '驚叫鬼魂教父', 311600, 0, 16, 270, 3000, 2, 4.0, 0, 0, 0, '【魔法】令人類戰慄不已的鬼怪首領。\\n它唆使手下們，祕密策劃著島嶼的擴大。\\n\\n‧必殺技能對敵方全體依照其初始最大 HP\\n\u3000造成魔法比例傷害。\\n\u3000並使敵方全體的最大HP\\n\u3000減少10％。減少的HP\\n\u3000直到戰鬥結束後會恢復。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (311601, '驚叫鬼魂教父', 311600, 0, 16, 270, 3000, 2, 4.0, 0, 0, 0, '【魔法】令人類戰慄不已的鬼怪首領。\\n它唆使手下們，祕密策劃著島嶼的擴大。\\n\\n‧必殺技能對敵方全體依照其初始最大 HP\\n\u3000造成魔法比例傷害。\\n\u3000並使敵方全體的最大HP\\n\u3000減少10％。減少的HP\\n\u3000直到戰鬥結束後會恢復。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (311602, '驚叫鬼魂教父', 311600, 0, 16, 270, 3000, 2, 4.0, 0, 0, 0, '【魔法】令人類戰慄不已的鬼怪首領。\\n它唆使手下們，祕密策劃著島嶼的擴大。\\n\\n‧必殺技能對敵方全體依照其初始最大HP\\n\u3000造成魔法比例傷害。\\n\u3000並使敵方全體的最大HP\\n\u3000減少10％。減少的HP\\n\u3000直到戰鬥結束後才會恢復。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧當鬼魂教父的HP在40％以下時，\\n\u3000它的攻擊將會變得更加狂暴。');
insert into unit_enemy_data values (311603, '驚叫鬼魂教父', 311600, 0, 16, 270, 3000, 2, 4.0, 0, 0, 0, '【魔法】令人類戰慄不已的鬼怪首領。\\n它唆使手下們，祕密策劃著島嶼的擴大。\\n\\n‧必殺技能對敵方全體依照其初始最大HP\\n\u3000造成魔法比例傷害。\\n\u3000並使敵方全體的最大HP\\n\u3000減少10％。減少的HP\\n\u3000直到戰鬥結束後才會恢復。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧當鬼魂教父的HP在40％以下時，\\n\u3000它的攻擊將會變得更加狂暴。');
insert into unit_enemy_data values (311604, '驚叫鬼魂教父', 311600, 0, 16, 270, 3000, 2, 4.0, 0, 0, 0, '【MODE1】為尋求美味的靈魂，\\n鬼怪們的首領再次現身。\\n它率領殘暴的家族大肆蹂躪人類，\\n街上充斥著極盡恐懼的悲鳴。\\n\\n‧戰鬥開始時，\\n\u3000召喚出 3 隻鬼魂手下。\\n‧每打倒 1 隻鬼魂手下，\\n\u3000便會減少剩餘的 1 隻鬼魂手下。\\n\u3000接著，鬼魂手下又會被再度召喚。\\n‧鬼魂手下會使面前的 1 名敵人\\n\u3000TP 下降，並恢復\\n\u3000鬼魂教父的 TP 。\\n‧必殺技會對全體敵人依照其初始\\n\u3000最大 HP 造成魔法比例傷害，\\n\u3000同時使全體敵人的\\n\u3000最大 HP 減少 10％ 。\\n\u3000減少的 HP 直到戰鬥結束後\\n\u3000才會恢復。');
insert into unit_enemy_data values (311605, '驚叫鬼魂教父', 311600, 0, 16, 270, 3000, 2, 999.0, 0, 0, 0, '【MODE2】肆虐島嶼，為萬物降下制裁。\\n一旦踏入了他的島嶼，\\n便會被蜂擁的魍魎腐蝕生命，\\n體會人間地獄。\\n\\n‧戰鬥開始時，\\n\u3000角色的最大HP減少35％。\\n\u3000減少的HP直到戰鬥結束後才會恢復。\\n‧必殺技會對敵方全體依照其初始\\n\u3000最大HP造成魔法比例傷害，\\n\u3000同時使全體敵人的\\n\u3000最大HP減少10％。\\n\u3000減少的HP直到戰鬥結束後才會恢復。');
insert into unit_enemy_data values (311606, '驚叫鬼魂教父', 311600, 0, 16, 270, 3000, 2, 4.0, 0, 0, 0, '【MODE3】將蓄積之力全部釋放，\\n解放真正的力量。\\n支配這個世界，極盡殘忍之能事，\\n使一切染上地獄般的色彩。\\n\\n‧必殺技會對敵方全體依照其\\n\u3000初始最大HP造成魔法比例傷害，\\n\u3000同時使敵方全體的最大HP減少10％。\\n\u3000減少的HP直到戰鬥結束後才會恢復。\\n‧對自己展開使物理/魔法無效化的護盾。\\n‧當鬼魂教父的HP在40％以下時，\\n\u3000它的攻擊將會變得更加狂暴。');
insert into unit_enemy_data values (311700, '鬼魂兄弟', 311700, 0, 16, 270, 3000, 2, 999.0, 0, 0, 0, '鬼魂教父的左右手。\\n將自身化為盾，保護首領免於一切攻擊。');
insert into unit_enemy_data values (311701, '鬼魂兄弟', 311700, 0, 16, 270, 3000, 2, 999.0, 0, 0, 0, '鬼魂教父的左右手。\\n以千錘百鍊的槍砲，向城鎮發動特攻。\\n血氣方剛地突襲四處逃竄的人們，將其逼至恐懼的深淵。\\n\\n‧鬼魂兄弟無法被視為攻擊對象。');
insert into unit_enemy_data values (311702, '鬼魂兄弟', 311700, 0, 16, 270, 3000, 2, 999.0, 0, 0, 0, '鬼魂教父的左右手。\\n將槍砲們蒐集而來的上等精氣獻給首領。\\n\\n‧鬼魂兄弟無法被視為攻擊對象。\\n‧能夠恢復鬼魂教父的 TP 。\\n\u3000在鬼魂教父陷入無法行動的狀態時，\\n\u3000則不會恢復 TP 。');
insert into unit_enemy_data values (311703, '鬼魂兄弟', 311700, 0, 16, 270, 3000, 2, 999.0, 0, 0, 0, '於最後一刻登場，來勢洶洶地到訪。\\n氣勢十足的最強之盾，擊潰敵對之人的心。');
insert into unit_enemy_data values (311800, '鬼魂兄弟', 311800, 0, 16, 270, 3000, 2, 999.0, 0, 0, 0, '鬼魂教父的左右手。\\n將提取而出的精氣交給首領，\\n負責輔佐的工作。\\n\\n‧能夠小幅度恢復鬼魂教父的TP。');
insert into unit_enemy_data values (311801, '鬼魂兄弟', 311800, 0, 16, 270, 3000, 2, 999.0, 0, 0, 0, '鬼魂教父的左右手。\\n在首領的周邊護衛，不給敵人絲毫可趁之機。在暗地裡偷偷\\n把鎮上的人們逼入絕境。\\n\\n‧鬼魂兄弟無法被視為攻擊對象。');
insert into unit_enemy_data values (311802, '鬼魂兄弟', 311800, 0, 16, 270, 3000, 2, 999.0, 0, 0, 0, '鬼魂教父的左右手。\\n將部下們提取到的精氣獻給首領。\\n\\n‧鬼魂兄弟無法被視為攻擊對象。\\n‧能夠恢復鬼魂教父的 TP 。\\n\u3000在鬼魂教父陷入無法行動的狀態時，\\n\u3000則不會恢復 TP 。');
insert into unit_enemy_data values (311803, '鬼魂兄弟', 311800, 0, 16, 270, 3000, 2, 999.0, 0, 0, 0, '保護家族免於危機，\\n冷靜地看清戰況。到死那一刻為止，都作為首領忠實的左右手，\\n貫徹輔佐的使命。\\n\\n‧能夠恢復鬼魂教父的 TP 。');
insert into unit_enemy_data values (311900, '光明獨角獸', 311900, 0, 13, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】吞食雲霧，而自身的樣貌\\n也如雲霧般難以捉摸。討厭人類，\\n會無情地踐踏接近之人。\\n\\n‧對全體敵人造成大幅度的魔法傷害。\\n\u3000同時對陷入火傷狀態的敵人\\n\u3000增加傷害。\\n\\n‧以物理攻擊力最高的敵人為中心，\\n\u3000對範圍內的敵人造成中幅度的魔法傷害，\\n\u3000並且施加火傷狀態。');
insert into unit_enemy_data values (312000, '黑曜飛龍', 312000, 0, 16, 270, 3000, 1, 2.0, 0, 0, 0, '【物理】寄宿著魔力的黑曜結晶龍。\\n據說其閃耀的美麗身軀是徬徨於冥府之魂的嘆息。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身物理防禦力與魔法防禦力。\\n\u3000因防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自黑曜飛龍的所有傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技會對敵方全體依照其最大HP\\n\u3000造成魔法比例傷害，並且使TP大幅度下降。\\n\u3000此技能所造成的TP下降，\\n\u3000即使傷害無效也會施加。\\n‧使敵方全體的HP吸收特大幅度下降，\\n\u3000同時大幅度降低物理防禦力。\\n‧對物理攻擊力或魔法攻擊力其中一者\\n\u3000最高的1名角色造成大幅度的物理\\n\u3000傷害，並以該角色為中心，\\n\u3000造成小範圍的中幅度物理傷害，\\n\u3000再中幅度降低TP。\\n\u3000此技能所造成的TP下降，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (312001, '黑曜飛龍', 312001, 0, 16, 270, 3000, 1, 2.0, 0, 0, 0, '【物理】寄宿著魔力的黑曜結晶龍。\\n據說其閃耀的美麗身軀\\n是徬徨於冥府之魂的嘆息。\\n\\n‧戰鬥開始時，\\n\u3000中幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧中幅度降低來自黑曜飛龍\\n\u3000所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會對敵方全體\\n\u3000依照其最大HP造成魔法比例傷害，\\n\u3000並大幅度降低其TP。\\n\u3000此技能所造成的TP下降，\\n\u3000即使傷害無效也會施加。\\n‧特大幅度降低敵方全體的HP吸收，\\n\u3000並大幅度降低其物理防禦力。\\n‧對物理攻擊力或魔法攻擊力其中一者\\n\u3000最高的1名角色造成大幅度的物理傷害。\\n\u3000同時以該角色為中心，\\n\u3000造成小範圍的中幅度物理傷害，\\n\u3000並中幅度降低其TP。\\n\u3000此技能所造成的TP下降，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (312002, '黑曜飛龍', 312002, 0, 16, 270, 3000, 1, 2.0, 0, 0, 0, '【物理】寄宿著魔力的黑曜結晶龍。據說其閃耀的美麗身軀\\n是徬徨於冥府之魂的嘆息。\\n\\n‧中幅度降低來自黑曜飛龍\\n\u3000所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會對敵方全體\\n\u3000依照其最大HP造成魔法比例傷害，\\n\u3000並大幅度降低其TP。此技能所造成的TP下降，\\n\u3000即使傷害無效也會施加。\\n‧特大幅度降低敵方全體的HP吸收，\\n\u3000並大幅度降低其物理防禦力。\\n‧對物理攻擊力或魔法攻擊力其中一者最高的\\n\u30001名角色造成大幅度的物理傷害。\\n\u3000同時以該角色為中心，造成小範圍的中幅度物理傷害，\\n\u3000並中幅度降低其TP。此技能\\n\u3000所造成的TP下降，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (312100, '反逆獸 de M', 312100, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【物理】奪去人們夢想的一種負面魔法生物。\\n以陰鬱的黑霧籠罩希望，腐蝕人們的內心。');
insert into unit_enemy_data values (312101, '反逆獸 de M', 312100, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【物理】奪去人們夢想的一種負面魔法生物。\\n以陰鬱的黑霧籠罩希望，腐蝕人們的內心。\\n\\n‧必殺技能解除敵方全體的護盾，\\n\u3000造成中幅度的魔法傷害，同時小幅度降低\\n\u3000其物理防禦力，中幅度降低其魔法防禦力，\\n\u3000並在一定時間內使HP恢復量下降70％。\\n‧對前方的2名角色造成中幅度的物理傷害。');
insert into unit_enemy_data values (312102, '反逆獸 de M', 312100, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【物理】奪去人們夢想的一種負面魔法生物。\\n以陰鬱的黑霧籠罩希望，腐蝕人們的內心。\\n\\n‧必殺技能解除敵方全體的護盾，\\n\u3000造成中幅度的魔法傷害，同時小幅度降低\\n\u3000其物理防禦力，中幅度降低其魔法防禦力，\\n\u3000並在一定時間內使HP恢復量下降70％。\\n‧對前方的2名角色造成中幅度的物理傷害。');
insert into unit_enemy_data values (312103, '反逆獸 de M', 312100, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【物理】奪去人們夢想的一種負面魔法生物。\\n以陰鬱的黑霧籠罩希望，腐蝕人們的內心。\\n\\n‧必殺技能解除敵方全體的護盾，\\n\u3000造成中幅度的魔法傷害，同時中幅度降低其\\n\u3000物理防禦力與魔法防禦力，\\n\u3000並在一定時間內使HP恢復量下降 70％ 。\\n‧對前方的2名角色造成大幅度的物理傷害。\\n‧當反逆獸 de M的HP在15％以下時，\\n\u3000會中幅度提升自身TP，\\n\u3000攻擊也會變得更加狂暴。');
insert into unit_enemy_data values (312104, '反逆獸 de M', 312100, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【MODE1】在鎮上吸收愛與希望的\\n反逆獸。它企圖擊敗魔法少女們，\\n將鎮上所有的光輝之力收歸己用。\\n\\n‧必殺技能解除敵方全體的護盾，\\n\u3000造成大幅度的魔法傷害，同時大幅度降低其\\n\u3000物理防禦力與魔法防禦力，\\n\u3000並在一定時間內使HP恢復量下降70％。\\n‧對最後方的1名敵方角色依照其最大HP\\n\u3000造成物理比例傷害，使其陷入束縛狀態，\\n\u3000並特大幅度降低其TP，\\n\u3000再中幅度提升反逆獸 de M自身的TP。');
insert into unit_enemy_data values (312105, '反逆獸 de M', 312100, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【MODE2】渴望吸收魔法少女們的猛烈攻擊，\\n使自身更加強大的反逆獸。連守護人們的勇氣\\n也將快被它腐蝕殆盡。\\n\\n‧必殺技能解除全體敵人的護盾，\\n\u3000給予大幅度的魔法傷害，同時大幅度降低其\\n\u3000物理防禦力與魔法防禦力，\\n\u3000在一定時間內使ＨＰ恢復量下降 70％\\n‧解除全體敵人的護盾，對全體敵人\\n\u3000依照其最大ＨＰ造成魔法比例傷害，\\n\u3000並使其陷入中毒狀態。');
insert into unit_enemy_data values (312106, '反逆獸 de M', 312100, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【MODE3】反逆獸的負面情感達到最高潮，\\n它扭曲的渴望籠罩了周遭一帶，\\n重挫魔法少女們的信念，企圖使失去守護者的城鎮成為自己的囊中物。\\n\\n‧必殺技能解除敵方全體的護盾，\\n\u3000造成大幅度的魔法傷害，同時大幅度降低其\\n\u3000物理防禦力與魔法防禦力，\\n\u3000並在一定時間內使HP恢復量下降70％。\\n‧對前方2名角色造成大幅度的物理傷害。\\n‧當反逆獸 de M的HP在12％以下時，\\n\u3000攻擊將會變得更加狂暴。');
insert into unit_enemy_data values (312200, '被操控的雪人', 312200, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【物理】肥嘟嘟且眼神銳利的最恐怖雪人。\\n它全身纏繞著狂氣，在聖夜的大街上不停徘徊，\\n使一年一度的特別夜晚變成了黑色聖誕節。');
insert into unit_enemy_data values (312201, '被操控的雪人', 312200, 0, 16, 270, 3000, 1, 4.0, 0, 0, 0, '【物理】肥嘟嘟且眼神銳利的最恐怖雪人。\\n它全身纏繞著狂氣，在聖夜的大街上不停徘徊，\\n使一年一度的特別夜晚變成了黑色聖誕節。\\n\\n‧必殺技能對敵方全體依照其最大HP\\n\u3000造成物理比例傷害，並將其大距離向後擊飛。\\n\u3000同時，當自己的目標沒有被破壞時，\\n\u3000中幅度提升物理防禦力，\\n\u3000小幅度提升魔法防禦力。此技能的效果，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體依照其最大HP造成物理比例傷害。\\n\u3000當自己的目標沒有被破壞時，\\n\u3000額外使其陷入暈眩狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧當剩餘時間不到20秒時，\\n\u3000它將會丟出禮物型炸彈，在一定時間過後，\\n\u3000對敵方全體依照其最大HP造成物理比例傷害。\\n\u3000被操控的雪人也會依照自身剩餘HP\\n\u3000受到物理比例傷害，並特大幅度降低TP。\\n‧當頭部被破壞時，\\n\u3000會小幅度降低物理防禦力與魔法防禦力。\\n‧當軀體被破壞時，\\n\u3000會小幅度降低物理防禦力與魔法防禦力。');
insert into unit_enemy_data values (312202, '被操控的雪人', 312200, 0, 16, 270, 3000, 1, 3.6, 0, 0, 0, '【物理】肥嘟嘟且眼神銳利的最恐怖雪人。\\n它全身纏繞著狂氣，在聖夜的大街上不停徘徊，\\n使一年一度的特別夜晚變成了黑色聖誕節。\\n\\n‧必殺技能對敵方全體依照其最大HP\\n\u3000造成物理比例傷害，並將其大距離向後擊飛。\\n\u3000同時，當自己的目標沒有被破壞時，\\n\u3000中幅度提升物理防禦力，\\n\u3000小幅度提升魔法防禦力。此技能的效果，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體依照其最大HP造成物理比例傷害。\\n\u3000當自己的目標沒有被破壞時，\\n\u3000額外使其陷入暈眩狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧當剩餘時間不到20秒時，\\n\u3000它將會丟出禮物型炸彈，在一定時間過後，\\n\u3000對敵方全體依照其最大HP造成物理比例傷害。\\n\u3000被操控的雪人也會依照自身剩餘HP\\n\u3000受到物理比例傷害，並特大幅度降低TP。\\n‧當頭部被破壞時，\\n\u3000會小幅度降低物理防禦力與魔法防禦力。\\n‧當軀體被破壞時，\\n\u3000會小幅度降低物理防禦力與魔法防禦力。');
insert into unit_enemy_data values (312203, '被操控的雪人', 312200, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【物理】肥嘟嘟且眼神銳利的最恐怖雪人。\\n它全身纏繞著狂氣，在聖夜的大街上不停徘徊，\\n使一年一度的特別夜晚變成了黑色聖誕節。\\n\\n‧必殺技能對敵方全體依照其最大HP\\n\u3000造成物理比例傷害，並將其大距離向後擊飛。\\n\u3000同時，當自己的目標沒有被破壞時，\\n\u3000中幅度提升物理防禦力，\\n\u3000小幅度提升魔法防禦力。\\n\u3000此技能的效果，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體依照其最大HP造成物理比例傷害。\\n\u3000當自己的目標沒有被破壞時，\\n\u3000額外使其陷入暈眩狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧當剩餘時間不到30秒時，\\n\u3000攻擊會變得更加兇暴。\\n‧當剩餘時間不到20秒時，\\n\u3000它將會丟出禮物型炸彈，在一定時間過後，\\n\u3000對敵方全體依照其最大HP造成物理比例傷害。\\n\u3000被操控的雪人也會依照自身剩餘HP\\n\u3000受到物理比例傷害，並特大幅度其降低TP。\\n‧當頭部被破壞時，\\n\u3000會小幅度降低物理防禦力與魔法防禦力。\\n‧當軀體被破壞時，\\n\u3000會小幅度降低物理防禦力與魔法防禦力。');
insert into unit_enemy_data values (312204, '被操控的雪人', 312200, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【MODE1】讓派對會場陷入大混亂的巨大雪人。\\n原本是備受人們喜愛且心地善良的吉祥物。\\n眾人的聲援是攻略它的關鍵。\\n\\n‧必殺技能對敵方全體依照其最大HP\\n\u3000造成物理比例傷害，並將其大距離向後擊飛。\\n\u3000當自己的目標沒有被破壞時，中幅度提升物理防禦力，\\n\u3000小幅度提升魔法防禦力。\\n‧對敵方全體造成大幅度的魔法傷害，\\n\u3000並使其陷入凍結狀態。\\n\u3000同時大幅度降低行動速度與物理防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧當頭部被破壞時，\\n\u3000會小幅度降低物理防禦力與魔法防禦力。\\n‧當軀體被破壞時，\\n\u3000會小幅度降低物理防禦力與魔法防禦力。\\n‧在戰鬥中，被操控的雪人自身\\n\u3000被附加的狀態異常效果或狀態下降數量越多，\\n\u3000能對其造成的傷害量就會上升越多。');
insert into unit_enemy_data values (312205, '被操控的雪人', 312200, 0, 16, 270, 3000, 1, 20.0, 0, 0, 0, '【MODE2】召喚沉醉於聖夜中的三人娘的闇影。\\n那是由巨大雪人做出的虛像。\\n為了挽回名譽，現在正是少女們起身奮戰的時候！\\n\\n‧必殺技能對敵方全體依照其最大HP\\n\u3000造成物理比例傷害，並將其大距離向後擊飛。\\n\u3000當自己的目標沒有被破壞時，中幅度提升物理防禦力，\\n\u3000小幅度提升魔法防禦力。\\n‧對敵方全體依照其最大HP造成物理比例傷害。\\n\u3000當自己的目標沒有被破壞時，\\n\u3000額外使其陷入暈眩狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧當頭部被破壞時，\\n\u3000會小幅度降低物理防禦力與魔法防禦力。\\n‧當軀體被破壞時，\\n\u3000會小幅度降低物理防禦力與魔法防禦力。');
insert into unit_enemy_data values (312206, '被操控的雪人', 312200, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【MODE3】誓要輾殺所有意念而\\n不斷滾動的巨大雪人。向所有孤獨之人，\\n溫柔地獻上爆炎的禮物for you♪\\n\\n‧必殺技能對敵方全體依照其最大HP\\n\u3000造成物理比例傷害，並將其大距離向後擊飛。\\n\u3000當自己的目標沒有被破壞時，中幅度提升物理防禦力，\\n\u3000小幅度提升魔法防禦力。\\n‧對敵方全體依照其最大HP造成物理比例傷害。\\n\u3000當自己的目標沒有被破壞時，\\n\u3000額外使其陷入暈眩狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧當剩餘時間不到30秒時，\\n\u3000攻擊將會變得更加狂暴。\\n‧當剩餘時間不到20秒時，\\n\u3000它將會丟出禮物型炸彈，在一定時間過後，\\n\u3000對敵方全體依照其最大HP造成物理比例傷害。\\n\u3000被操控的雪人也會依照自身剩餘HP\\n\u3000受到物理比例傷害，並特大幅度降低TP。\\n‧當頭部被破壞時，\\n\u3000會小幅度降低物理防禦力與魔法防禦力。\\n‧當軀體被破壞時，\\n\u3000會小幅度降低物理防禦力與魔法防禦力。');
