insert into unit_enemy_data values (312302, '究極金槍鮪', 312302, 0, 16, 270, 3000, 1, 3.3, 0, 0, 0, '【物理】大間的漁夫之間自古流傳的夢幻巨魚。\\n牠會用巨大身驅高速衝撞，使所有船隻葬身海底，\\n但據說牠的肉是世間僅有的極致美味。\\n\\n‧必殺技能對敵方全體依照最大HP\\n\u3000造成物理比例傷害。\\n\u3000並擊退。\\n\u3000此技能所造成的狀態異常，即使傷害無效\\n\u3000也會施加。\\n\u3000依照自身的物理攻擊力\\n\u3000增加造成的傷害比例。\\n‧當剩餘時間低於35秒時，\\n\u3000攻擊將會變得更加兇暴。');
insert into unit_enemy_data values (312303, '究極金槍鮪', 312302, 0, 16, 270, 3000, 1, 1.1, 0, 0, 0, '【物理】大間的漁夫之間自古流傳的夢幻巨魚。\\n牠會用巨大身驅高速衝撞，使所有船隻葬身海底，\\n但據說牠的肉是世間僅有的極致美味。\\n\\n‧必殺技能對敵方全體依照最大HP\\n\u3000造成物理比例傷害。\\n\u3000並擊退。\\n\u3000此技能所造成的狀態異常，即使傷害無效\\n\u3000也會施加。\\n\u3000依照自身的物理攻擊力\\n\u3000增加造成的傷害比例。\\n‧當剩餘時間低於35秒時，\\n\u3000攻擊將會變得更加兇暴。');
insert into unit_enemy_data values (312304, '究極金槍鮪', 312302, 0, 16, 270, 3000, 1, 0.0, 0, 0, 0, '【MODE1】讓眾多漁夫葬身海底的海中之王。\\n閃著金光的魚鰭連大型漁船的龍骨也能輕易折斷，\\n\u3000那幅景象簡直是海面上的解體秀。\\n\\n‧必殺技能對敵方全體依照其角色最大HP\\n\u3000造成物理比例傷害。\\n\u3000並將其擊退。\\n‧對前方範圍內所有敵人造成大幅度的物理傷害，\\n\u3000同時對後方數來2名角色造成\\n\u3000中幅度的物理傷害。\\n\u3000並小幅度提升自身的物理攻擊力。');
insert into unit_enemy_data values (312305, '究極金槍鮪', 312305, 0, 16, 270, 3000, 1, 1.0, 0, 0, 0, '【MODE2】馳騁大海的怪魚變得更加強大。\\n輕輕一游就像在激勵自己似地掀起巨大漩渦，\\n化身將大海一分為二的巨大砲彈衝撞敵人。\\n\\n‧必殺技能對前方的3名角色\\n\u3000造成特大幅度的物理傷害，\\n\u3000並且將其擊退。\\n‧大幅度提升自身的物理攻擊力，中幅度\\n\u3000提升行動速度，小幅度提升TP。');
insert into unit_enemy_data values (312306, '究極金槍鮪', 312302, 0, 16, 270, 3000, 1, 1.0, 0, 0, 0, '【MODE3】不論黝黑的巨大身驅被魚叉射中幾次，\\n也無法阻止這條傳說巨魚的衝刺。\\n與海上男兒們長久的戰鬥即將分出勝負。\\n\\n‧必殺技能對敵方全體依照其角色最大HP\\n\u3000造成物理比例傷害。\\n\u3000並且將其擊退。\\n‧大幅度提升自身的物理攻擊力，中幅度\\n\u3000提升行動速度，大幅度提升TP。\\n‧當剩餘時間不到35秒時，\\n\u3000牠的攻擊將會變得更加狂暴。');
insert into unit_enemy_data values (312400, '寒冰格里芬', 312400, 0, 11, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】張開冰翼\\n威嚇冒險者的大鷲。\\n對於闖入地盤之人，\\n會毫不留情地將其冰凍住，\\n並餵予雛鳥。\\n\\n‧必殺技能對前方小範圍內\\n\u3000造成大幅度物理傷害，\\n\u3000並大幅度向後擊飛，\\n\u3000使其陷入凍結狀態。');
insert into unit_enemy_data values (312401, '寒冰格里芬', 312400, 0, 11, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】張開冰翼\\n威嚇冒險者的大鷲。\\n對於闖入地盤之人，\\n會毫不留情地將其冰凍住，\\n並餵予雛鳥。\\n\\n‧必殺技能對前方小範圍內\\n\u3000造成大幅度物理傷害，\\n\u3000並大幅度向後擊飛，\\n\u3000使其陷入凍結狀態。');
insert into unit_enemy_data values (312500, '厄勒克特拉夫人', 312500, 0, 6, 270, 1000, 2, 1.6, 0, 0, 0, '【魔法】積蓄塔之魔力來放出電擊的怪鳥。\\n會與雷雲一同現身，以如天災般的雷擊葬送敵人。\\n\\n‧對敵方全體造成大幅度魔法傷害，\\n\u3000同時小幅度提升\\n\u3000自身的魔法攻擊力與行動速度。\\n\u3000對進行物理攻擊的敵人造成追加傷害。\\n‧對前方範圍內所有敵人造成中幅度的魔法傷害，\\n\u3000並且中幅度降低物理防禦力與魔法防禦力。\\n‧對物理攻擊力最高的1名敵方角色造成大幅度的\\n\u3000魔法傷害，並使其陷入長時間的麻痺狀態。\\n\u3000自身的HP低於85%時，也會對魔法攻擊力\\n\u3000最高的敵人發動此攻擊。');
insert into unit_enemy_data values (312501, '厄勒克特拉夫人', 312501, 0, 6, 270, 1000, 2, 1.6, 0, 0, 0, '【魔法】積蓄魔力來放出電擊的怪鳥。\\n會與雷雲一同現身，以如天災般的雷擊葬送敵人。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。以此防禦力提升效果\\n\u3000而上升的防禦力，在戰鬥中不會受到防禦力\\n\u3000下降技能的影響。\\n‧承受來自厄勒克特拉夫人所有的傷害，\\n\u3000導致中幅度降低隊伍角色\\n\u3000的TP恢復量。\\n‧必殺技為小幅度提升自身的魔法攻擊力\\n\u3000和魔法防禦力，並對最靠近的1名敵人\\n\u3000造成特大幅度的魔法傷害。');
insert into unit_enemy_data values (312502, '厄勒克特拉夫人', 312502, 0, 6, 270, 1000, 2, 1.6, 0, 0, 0, '【魔法】積蓄魔力來放出電擊的怪鳥。\\n會與雷雲一同現身，\\n以如天災般的雷擊葬送敵人。\\n\\n‧戰鬥開始時，\\n\u3000中幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000展開小幅度降低敵方全體TP上升的領域。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少100％效果。\\n‧中幅度降低來自厄勒克特拉夫人\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧大幅度降低來自敵方所有傷害產生的\\n\u3000厄勒克特拉夫人TP恢復量。\\n‧必殺技可小幅度提升自身的魔法攻擊力\\n\u3000與魔法防禦力，並對距離最近的1名敵人\\n\u3000造成特大幅度的魔法傷害，\\n\u3000同時使其陷入麻痺狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的1名敵人\\n\u3000造成特大幅度的魔法傷害，\\n\u3000並且使其陷入麻痺狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (312503, '厄勒克特拉夫人', 312503, 0, 6, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】積蓄魔力來放出電擊的怪鳥。\\n會與雷雲一同現身，\\n以如天災般的雷擊葬送敵人。\\n\\n‧戰鬥開始時，\\n\u3000中幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000對自己施加TP降低減少50％效果。\\n‧承受來自厄勒克特拉夫人所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技可小幅度提升自身的魔法攻擊力\\n\u3000與魔法防禦力，並對距離最近的1名角色\\n\u3000造成特大幅度的魔法傷害，\\n\u3000同時使其陷入麻痺狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的1名角色\\n\u3000造成特大幅度的魔法傷害，\\n\u3000同時使其陷入麻痺狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (312504, '厄勒克特拉夫人', 312504, 0, 6, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】積蓄魔力來放出電擊的怪鳥。\\n會與雷雲一同現身，以如天災般的雷擊葬送敵人。\\n\\n‧中幅度降低來自厄勒克特拉夫人\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧必殺技會對距離最近的1名敵人\\n\u3000造成特大幅度的魔法傷害，\\n\u3000並使其陷入麻痺狀態。\\n\u3000之後小幅度提升自身的\\n\u3000魔法攻擊力與魔法防禦力直到戰鬥結束。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的1名敵人\\n\u3000造成特大幅度的魔法傷害，\\n\u3000並且使其陷入麻痺狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
