insert into unit_enemy_data values (312601, '角鹿將軍', 312600, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【物理】擁有巨大麋鹿角的大型魔物。牠會以連大岩石\\n都能輕易粉碎的大角，一次掃蕩所有冒險者。\\n\\n‧普通攻擊會以前方1名角色為中心，對範圍內的\\n\u3000角色造成小幅度的物理傷害。\\n‧必殺技會對敵方全體依照其角色最大HP\\n\u3000造成魔法比例傷害。\\n\u3000同時中幅度降低自身的物理與\\n\u3000魔法防禦力。\\n‧對前方範圍內敵人造成中幅度的物理傷害，\\n\u3000並使其麻痺。同時大幅度降低自身的物理與\\n\u3000魔法防禦力。\\n\u3000此技能所造成的狀態異常，即使傷害無效\\n\u3000也會施加。');
insert into unit_enemy_data values (312602, '角鹿將軍', 312600, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【物理】擁有巨大麋鹿角的大型魔物。牠會以連大岩石\\n都能輕易粉碎的大角，一次掃蕩所有冒險者。\\n\\n‧普通攻擊會以前方1名角色為中心，對範圍內的\\n\u3000角色造成小幅度的物理傷害。\\n‧必殺技會對敵方全體依照其角色最大HP\\n\u3000造成魔法比例傷害。\\n\u3000同時中幅度降低自身的物理與\\n\u3000魔法防禦力。\\n‧對前方範圍內敵人造成中幅度的物理傷害，\\n\u3000並使其麻痺。同時大幅度降低自身的物理與\\n\u3000魔法防禦力。\\n\u3000此技能所造成的狀態異常，即使傷害無效\\n\u3000也會施加。\\n‧當剩餘時間不到25秒時，牠的攻擊將會變得\\n\u3000更加兇暴。');
insert into unit_enemy_data values (312603, '角鹿將軍', 312600, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【物理】擁有巨大麋鹿角的大型魔物。牠會以連大岩石\\n都能輕易粉碎的大角，一次掃蕩所有冒險者。\\n\\n‧普通攻擊會以前方1名角色為中心，對範圍內的\\n\u3000角色造成小幅度的物理傷害。\\n‧必殺技會對敵方全體依照其角色最大HP\\n\u3000造成魔法比例傷害。\\n\u3000同時中幅度降低自身的物理與\\n\u3000魔法防禦力。\\n‧對前方範圍內敵人造成中幅度的物理傷害，\\n\u3000並使其麻痺。同時大幅度降低自身的物理與\\n\u3000魔法防禦力。\\n\u3000此技能所造成的狀態異常，即使傷害無效\\n\u3000也會施加。\\n‧當剩餘時間不到25秒時，牠的攻擊將會變得\\n\u3000更加兇暴。');
insert into unit_enemy_data values (312604, '角鹿將軍', 312600, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【MODE1】聽到首領吼叫聲，手下的雄鹿\\n成群結隊地蜂擁而至。抵擋得住捨身的\\n突進嗎？\\n\\n‧戰鬥開始時，召喚3隻狂野角鹿，\\n\u3000並使其擊退。\\n‧每打倒1隻狂野角鹿，剩餘的\\n\u3000狂野角鹿數量減少1。\\n\u3000之後，將會再次召喚狂野角鹿，\\n\u3000並使其擊退。\\n‧普通攻擊會以前方1名角色為中心，對範圍內的\\n\u3000角色造成小幅度的物理傷害。\\n‧必殺技會對敵方全體依照其角色最大HP\\n\u3000造成魔法比例傷害。\\n\u3000同時中幅度降低自身的物理與\\n\u3000魔法防禦力。');
insert into unit_enemy_data values (312605, '角鹿將軍', 312600, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【MODE2】操縱朽木魔物，將冒險者逼入\\n絕境。若是失去共生的朽木，其身也將\\n邁向毀滅。擊敗朽木將成為攻略的關鍵。\\n\\n‧戰鬥開始時，召喚2隻多倫多獸\\n\u3000並將其擊退。\\n‧前方的多倫多獸HP變成0時，小幅度\\n\u3000降低角鹿將軍的物理與魔法\\n\u3000防禦力。\\n‧後方的多倫多獸HP變成0時，極大幅度\\n\u3000降低角鹿將軍的物理與魔法\\n\u3000防禦力。\\n‧普通攻擊會以前方1名角色為中心，對範圍內的\\n\u3000角色造成小幅度的物理傷害。\\n‧必殺技會對敵方全體依照其角色最大HP\\n\u3000造成魔法比例傷害。\\n\u3000同時中幅度降低自身的物理與\\n\u3000魔法防禦力。\\n‧對前方範圍內敵人造成中幅度的物理傷害，並使其陷入\\n\u3000麻痺狀態。同時大幅度降低自身的物理與\\n\u3000魔法防禦力。');
insert into unit_enemy_data values (312606, '角鹿將軍', 312600, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【MODE3】身負重傷的角獸傾盡全力抗戰。\\n賭上性命的突進使身驅化為紫電，以如光一閃\\n瓦解身經百戰的冒險者一行人。\\n\\n‧普通攻擊會以前方1名角色為中心，對範圍內的\\n\u3000角色造成小幅度的物理傷害。\\n‧必殺技會對敵方全體依照其角色最大HP\\n\u3000造成魔法比例傷害。\\n\u3000同時中幅度降低自身的物理與魔法\\n\u3000防禦力。\\n‧對前方範圍內敵人造成中幅度的物理傷害，並使其陷入\\n\u3000麻痺狀態。同時大幅度降低自身的物理與\\n\u3000魔法防禦力。\\n‧當剩餘時間不到25秒時，牠的攻擊將會變得\\n\u3000更加兇暴。');
