insert into unit_enemy_data values (312701, '黑風之精靈', 312700, 0, 16, 270, 3000, 2, 2.7, 0, 0, 0, '【魔法】試探操控者真正實力的漆黑風之精靈。\\n那裡不含有惡意，純粹只為測試懷藏於自己內心的\\n想法與羈絆。\\n\\n‧必殺技會對敵方全體依照黑風之精靈的物理\\n\u3000攻擊力及其角色最大HP造成魔法比例傷害。\\n\u3000並賦予剩餘HP最低的1名角色倒數計時效果，\\n\u30005秒後造成該角色最大HP80％的\\n\u3000魔法比例傷害。\\n\u3000黑風之精靈使用必殺技之後經過一定時間以前\\n\u3000不會再使用必殺技。');
insert into unit_enemy_data values (312702, '黑風之精靈', 312700, 0, 16, 270, 3000, 2, 2.7, 0, 0, 0, '【魔法】試探操控者真正實力的漆黑風之精靈。\\n那裡不含有惡意，純粹只為測試懷藏於自己內心的\\n想法與羈絆。\\n\\n‧必殺技會對敵方全體依照黑風之精靈的物理\\n\u3000攻擊力及其角色最大HP造成魔法比例傷害。\\n\u3000並賦予剩餘HP最低的1名角色倒數計時效果，\\n\u30005秒後造成該角色最大HP80％的\\n\u3000魔法比例傷害。\\n\u3000黑風之精靈使用必殺技之後經過一定時間以前\\n\u3000不會再使用必殺技。\\n‧使敵方全體陷入猛毒狀態，\\n\u3000並大幅度降低物理與魔法攻擊力。\\n‧當剩餘時間不到25秒時，\\n\u3000牠的攻擊將會變得更加兇暴。');
insert into unit_enemy_data values (312703, '黑風之精靈', 312700, 0, 16, 270, 3000, 2, 2.7, 0, 0, 0, '【魔法】試探操控者真正實力的漆黑風之精靈。\\n那裡不含有惡意，純粹只為測試懷藏於自己內心的\\n想法與羈絆。\\n\\n‧必殺技會對敵方全體依照黑風之精靈的物理\\n\u3000攻擊力及其角色最大HP造成魔法比例傷害。\\n\u3000並賦予剩餘HP最低的1名角色倒數計時效果，\\n\u30005秒後造成該角色最大HP80％的\\n\u3000魔法比例傷害。\\n\u3000黑風之精靈使用必殺技之後經過一定時間以前\\n\u3000不會再使用必殺技。\\n‧使敵方全體陷入猛毒狀態，\\n\u3000並大幅度降低物理與魔法攻擊力。\\n‧當剩餘時間不到25秒時，\\n\u3000牠的攻擊將會變得更加兇暴。');
insert into unit_enemy_data values (312704, '黑風之精靈', 312700, 0, 16, 270, 3000, 2, 2.7, 0, 0, 0, '【MODE1】第一道試煉。若是無法看穿漆黑風之精靈\\n暴風的接合之處，將無法主動開拓自己\\n應前往之路。\\n\\n‧對敵方全體造成物理傷害並使其陷入暈眩，\\n\u3000同時將其擊退。\\n\u3000黑風之精靈睡著的期間，會大幅度降低自身的\\n\u3000物理防禦力，若在期間內對其造成100000\\n\u3000以上的傷害，將延長防禦力降低時間，\\n\u3000並降低行動速度。');
insert into unit_enemy_data values (312705, '黑風之精靈', 312705, 0, 16, 270, 3000, 2, 2.7, 0, 0, 0, '【MODE2】第二道試煉。與自己軟弱的心對峙\\n吧。答案隨時在自己心中。現在\\n相信夥伴的力量並挺身對抗吧。\\n\\n‧對前方2名角色造成小幅度的物理傷害，\\n\u3000同時將其擊退，並使其陷入暈眩。');
insert into unit_enemy_data values (312706, '黑風之精靈', 312700, 0, 16, 270, 3000, 2, 2.7, 0, 0, 0, '【MODE3】第三道試煉。最終的試煉為賭上\\n自己的全部向漆黑風之精靈展現真正的想法與\\n羈絆，確認自己的想法吧。\\n\\n‧必殺技會對敵方全體依照漆黑風之精靈\\n\u3000的物理攻擊力及其角色最大HP\\n\u3000造成魔法比例傷害。\\n\u3000並賦予剩餘HP最低的1名角色倒數計時效果，\\n\u30005秒後造成該角色最大HP80％的\\n\u3000魔法比例傷害。\\n‧使敵方全體陷入猛毒狀態，\\n\u3000並大幅度降低物理與魔法攻擊力。\\n‧當剩餘時間不到25秒時，\\n\u3000牠的攻擊將會變得更加兇暴。');
