insert into unit_enemy_data values (312804, '魔物的公主', 312800, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【MODE1】清高的公主派出隨從拒絕\\n對敵。彷彿在掩飾內心的悲嘆。\\n\\n‧必殺技能夠對前方的2名角色依照其最大HP\\n\u3000造成物理比例傷害。\\n‧對面前的1名敵人造成物理傷害。\\n\u3000此攻擊容易受到物理攻擊力提升、降低\\n\u3000的效果影響。\\n‧對前方的3名角色依照其最大HP造成\\n\u3000物理比例傷害。\\n\u3000此攻擊容易受到物理攻擊力提升、降低\\n\u3000的效果影響。');
insert into unit_enemy_data values (312805, '魔物的公主', 312800, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【MODE2】公主解放更多力量。\\n以扭曲的強烈愛慕，阻擋一切並殲滅。\\n\\n‧對敵人攻擊造成的麻痺、暈眩、束縛、凍結、\\n\u3000時間停止、行動速度降低等效果沒有\\n\u3000抗性。\\n‧必殺技能夠對前方的2名角色依照其最大HP\\n\u3000造成物理比例傷害。\\n‧對面前的1名敵人造成物理傷害。\\n\u3000此攻擊容易受到物理攻擊力提升、降低\\n\u3000的效果影響。\\n‧對前方的3名角色依照其最大HP造成\\n\u3000物理比例傷害。\\n\u3000此攻擊容易受到物理攻擊力提升、降低\\n\u3000的效果影響。\\n‧短時間成為無敵狀態，解除敵方全體的能力\\n\u3000提升效果後，在一定時間內\\n\u3000展開戰鬥場地。\\n\\n【戰鬥場地效果】\\n‧降低敵方全體100%的物理攻擊力與\\n\u3000魔法攻擊力後，特大幅度提升物理攻擊力與\\n\u3000魔法攻擊力。\\n‧特大幅度提升魔物的公主的物理攻擊力。');
insert into unit_enemy_data values (312806, '魔物的公主', 312800, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【MODE3】呼應巨大的悲傷，\\n公主的進攻極為激烈。懷著少女的熱情，\\n其悲傷得不到救贖。\\n\\n‧必殺技能夠對前方的2名角色依照其最大HP\\n\u3000造成物理比例傷害。\\n\u3000當成功累積力量時，會提升傷害，\\n\u3000使敵方全體陷入畏懼狀態。\\n\u3000陷入畏懼狀態的角色，在一定時間之內進行\\n\u3000攻擊與HP恢復的必殺技與技能，其效果都將\\n\u3000降低。\\n‧大幅度降低物理防禦力與魔法防禦力後，\\n\u3000在一定時間內，會累積力量。\\n\u3000累積力量後，特大幅度恢復TP。\\n\u3000在累積力量時，當魔物的公主受到600000\\n\u3000以上的傷害時，累積力量會失敗，\\n\u3000大幅度降低TP。 \\n‧對面前的1名敵方角色造成物理傷害。\\n\u3000此攻擊容易受到物理攻擊力提升、降低\\n\u3000的效果影響。\\n‧對前方的3名角色依照其最大HP造成\\n\u3000物理比例傷害。\\n\u3000此攻擊容易受到物理攻擊力提升、降低\\n\u3000的效果影響。\\n‧當魔物的公主自身的HP不到25％之時，\\n\u3000牠的攻擊將會變得更加兇暴。');
insert into unit_enemy_data values (312900, '黑月守護像', 312900, 0, 16, 270, 3000, 1, 2.5, 0, 0, 0, '【物理】受黑月祝福的雕像，守護天上的聖域。\\n將踏入聖域的冒險者埋葬於常世的黑暗中。\\n\\n‧必殺技能夠對敵方全體依照其角色最大\\n\u3000HP造成物理比例傷害。\\n‧對敵方全體造成大幅度的物理傷害，並解除\\n\u3000能力提升效果。此技能的效果，\\n\u3000即使傷害無效也會施加。\\n‧大幅度提升自身的物理攻擊力與魔法\\n\u3000攻擊力。當白陽守護像被討伐時，\\n\u3000會使用此技能。\\n‧當黑月守護像的HP不到50%時，將會大幅度\\n\u3000提升物理攻擊力，中幅度提升物理\\n\u3000防禦力。\\n‧當黑月守護像的目標部位被\\n\u3000破壞時，將會小幅度降低物理防禦力與魔法\\n\u3000防禦力。\\n‧當白陽守護像已被討伐時，當戰鬥\\n\u3000開始時或黑月守護像進行普通攻擊\\n\u3000時，會改變黑月守護像的攻擊\\n\u3000行動。');
insert into unit_enemy_data values (313000, '白陽守護像', 313000, 0, 16, 270, 3000, 2, 2.5, 0, 0, 0, '【魔法】受白陽祝福的雕像，守護天上的聖域。\\n用淨化一切的白雷，對冒險者降下審判。\\n\\n‧必殺技能夠對敵方全體依照其角色最大\\n\u3000HP造成魔法比例傷害。\\n‧對敵方全體造成大幅度的魔法傷害，並解除\\n\u3000能力提升效果。同時大幅度提升白陽守護像的\\n\u3000魔法攻擊力。此技能的效果，\\n\u3000即使傷害無效也會施加。當黑月\\n\u3000守護像被討伐時，會使用\\n\u3000此技能。\\n‧中幅度提升我方全體的物理防禦力與魔法\\n\u3000防禦力。\\n‧當白陽守護像的HP不到50%時，將會特大\\n\u3000幅度提升魔法攻擊力，小幅度提升\\n\u3000魔法防禦力。\\n‧當白陽守護像的目標部位被\\n\u3000破壞時，將會小幅度降低物理防禦力與\\n\u3000魔法防禦力。\\n‧當黑月守護像已被討伐時，當戰鬥\\n\u3000開始時或白陽守護像進行普通攻擊\\n\u3000時，會改變白陽守護像的攻擊\\n\u3000行動。');
