insert into unit_enemy_data values (313101, '爆裂‧時間儀', 313100, 0, 16, 270, 3000, 2, 1.4, 0, 0, 0, '【魔法】帆稀收藏的魔導具超越界限失控\\n的模樣。忘卻原本的職責，變成將冒險者\\n導向混沌的領航員。\\n\\n‧必殺技能夠對敵方全體造成大幅度的\\n\u3000魔法傷害，同時中幅度降低其行動速度，\\n\u3000中幅度降低其魔法防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧中幅度提升自身的魔法攻擊力，\\n\u3000大幅度提升行動速度。\\n‧當左側的目標被破壞時，\\n\u3000將會中幅度降低物理與魔法防禦力。\\n‧當中央下方的目標被破壞時，\\n\u3000將會大幅度降低物理與魔法防禦力。\\n‧當中央上方的目標被破壞時，\\n\u3000將會大幅度降低物理與魔法防禦力。\\n‧當右側的目標被破壞時，\\n\u3000將會特大幅度降低物理與魔法防禦力。');
insert into unit_enemy_data values (313102, '爆裂‧時間儀', 313100, 0, 16, 270, 3000, 2, 1.4, 0, 0, 0, '【魔法】帆稀收藏的魔導具超越界限失控\\n的模樣。忘卻原本的職責，變成將冒險者\\n導向混沌的領航員。\\n\\n‧必殺技能夠對敵方全體造成大幅度的\\n\u3000魔法傷害，同時中幅度降低其行動速度，\\n\u3000中幅度降低其魔法防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧中幅度提升自身的魔法攻擊力，\\n\u3000大幅度提升行動速度。\\n‧當左側的目標被破壞時，\\n\u3000將會中幅度降低物理與魔法防禦力。\\n‧當中央下方的目標被破壞時，\\n\u3000將會大幅度降低物理與魔法防禦力。\\n‧當中央上方的目標被破壞時，\\n\u3000將會大幅度降低物理與魔法防禦力。\\n‧當右側的目標被破壞時，\\n\u3000將會特大幅度降低物理與魔法防禦力。');
insert into unit_enemy_data values (313103, '爆裂‧時間儀', 313100, 0, 16, 270, 3000, 2, 1.4, 0, 0, 0, '【魔法】帆稀收藏的魔導具超越界限失控\\n的模樣。忘卻原本的職責，變成將冒險者\\n導向混沌的領航員。\\n\\n‧必殺技能夠對敵方全體造成大幅度的\\n\u3000魔法傷害，同時中幅度降低其行動速度，\\n\u3000中幅度降低其魔法防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧中幅度提升自身的魔法攻擊力，\\n\u3000大幅度提升行動速度。\\n‧當剩餘時間不到25秒時，\\n\u3000將會大幅度提升自身的TP，\\n\u3000其攻擊將會變得更加兇暴。\\n‧當左側的目標被破壞時，\\n\u3000將會中幅度降低物理與魔法防禦力。\\n‧當中央下方的目標被破壞時，\\n\u3000將會大幅度降低物理與魔法防禦力。\\n‧當中央上方的目標被破壞時，\\n\u3000將會大幅度降低物理與魔法防禦力。\\n‧當右側的目標被破壞時，\\n\u3000將會特大幅度降低物理與魔法防禦力。');
insert into unit_enemy_data values (313104, '爆裂‧時間儀', 313104, 0, 16, 270, 3000, 2, 1.4, 0, 0, 0, '【MODE1】失控的魔導具想將所有東西\\n都留在自身的領域中。不論有多少障礙\\n也不屈服，相信自己並開拓未來。\\n\\n‧使此戰鬥中全部目標的破壞\\n\u3000無效。\\n‧展開護盾，使其不會受到敵人造成的任何攻擊。\\n\u3000護盾受到攻擊時會漸漸降低\\n\u3000耐久，受到一定數量時護盾\\n\u3000就會被破壞。\\n‧必殺技能夠對敵方全體造成大幅度的\\n\u3000魔法傷害，同時中幅度降低其行動速度，\\n\u3000中幅度降低其魔法防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧中幅度提升自身的魔法攻擊力，\\n\u3000並大幅度提升行動速度。');
insert into unit_enemy_data values (313105, '爆裂‧時間儀', 313100, 0, 16, 270, 3000, 2, 1.4, 0, 0, 0, '【MODE2】召喚祈梨與嘉夜的闇影。\\n兩人會在自身的「如果」的姿態中看到什麼。\\n是時候打破虛構並證明自己的道路正確。\\n\\n‧使此戰鬥中全部目標的破壞無效。\\n‧中幅度提升自身的魔法攻擊力，\\n\u3000並大幅度提升行動速度。');
insert into unit_enemy_data values (313106, '爆裂‧時間儀', 313100, 0, 16, 270, 3000, 2, 0.8, 0, 0, 0, '【MODE3】失去了主人的魔導具\\n持續失控。如果在絕望降臨中不斷\\n反抗，就會開啟一條希望之光的道路。\\n\\n‧必殺技能夠對敵方全體造成大幅度的\\n\u3000魔法傷害，同時中幅度降低其行動速度，\\n\u3000中幅度降低其魔法防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧中幅度提升自身的魔法攻擊力，\\n\u3000大幅度提升行動速度。\\n‧當剩餘時間不到25秒時，\\n\u3000將會大幅度提升自身的TP，\\n\u3000其攻擊將會變得更加兇暴。\\n‧當左側的目標被破壞時，\\n\u3000將會中幅度降低物理與魔法防禦力。\\n‧當中央下方的目標被破壞時，\\n\u3000將會大幅度降低物理與魔法防禦力。\\n‧當中央上方的目標被破壞時，\\n\u3000將會大幅度降低物理與魔法防禦力。\\n‧當右側的目標被破壞時，\\n\u3000將會特大幅度降低物理與魔法防禦力。');
