insert into unit_enemy_data values (313201, '無名的藝術', 313200, 0, 16, 270, 3000, 1, 2.0, 0, 0, 0, '【物理】奇形怪狀藝術作品的魔物，表現出了\\n製造者的深淵？其風貌完全無法以一般人的標準\\n理解，製造者說懂的人就懂。\\n\\n‧必殺技能夠對敵方全體依照其角色最大\\n\u3000HP造成魔法比例傷害。\\n\u3000而當該角色的HP低於50％時，\\n\u3000使其陷入火傷狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n\u3000此技能施加的火傷狀態所造成的傷害\\n\u3000將不會提升TP。\\n‧以前方第3名角色為中心，\\n\u3000對範圍內依照其角色\\n\u3000最大HP造成物理比例傷害。\\n\u3000並且對陷入火傷狀態的角色依照其最大\\n\u3000HP造成追加物理比例傷害。');
insert into unit_enemy_data values (313202, '無名的藝術', 313200, 0, 16, 270, 3000, 1, 2.0, 0, 0, 0, '【物理】奇形怪狀藝術作品的魔物，表現出了\\n製造者的深淵？其風貌完全無法以一般人的標準\\n理解，製造者說懂的人就懂。\\n\\n‧必殺技能夠對敵方全體依照其角色最大\\n\u3000HP造成魔法比例傷害。\\n\u3000而當該角色的HP低於50％時，\\n\u3000使其陷入火傷狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n\u3000此技能施加的火傷狀態所造成的傷害\\n\u3000將不會提升TP。\\n‧以前方第3名角色為中心，\\n\u3000對範圍內依照其角色\\n\u3000最大HP造成物理比例傷害。\\n\u3000並且對陷入火傷狀態的角色依照其最大\\n\u3000HP造成追加物理比例傷害。');
insert into unit_enemy_data values (313203, '無名的藝術', 313200, 0, 16, 270, 3000, 1, 2.0, 0, 0, 0, '【物理】奇形怪狀藝術作品的魔物，表現出了\\n製造者的深淵？其風貌完全無法以一般人的標準\\n理解，製造者說懂的人就懂。\\n\\n‧必殺技能夠對敵方全體依照其角色最大\\n\u3000HP造成魔法比例傷害。\\n\u3000而當該角色的HP低於50％時，\\n\u3000使其陷入火傷狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n\u3000此技能施加的火傷狀態所造成的傷害\\n\u3000將不會提升TP。\\n‧以前方第3名角色為中心，\\n\u3000對範圍內依照其角色\\n\u3000最大HP造成物理比例傷害。\\n\u3000並且對陷入火傷狀態的角色\\n\u3000依照其最大HP造成追加物理比例傷害。\\n‧當剩餘時間不到30秒時，將會小幅度提升\\n\u3000自身的TP，其攻擊將會變得更加兇暴。');
insert into unit_enemy_data values (313204, '無名的藝術', 313200, 0, 16, 270, 3000, 1, 2.0, 0, 0, 0, '【MODE1】寄宿著生命的黏土人偶不會停止。\\n內部發出奇形怪狀的魔力，少女們和已抵抗的校舍\\n能承受接連射出的光線嗎？\\n\\n‧必殺技能夠對敵方全體依照其角色最大\\n\u3000HP造成魔法比例傷害。\\n\u3000而當該角色的HP低於50％時，\\n\u3000使其陷入火傷狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n\u3000此技能施加的火傷狀態所造成的傷害\\n\u3000將不會提升TP。\\n‧解除前方範圍內的敵人的護盾，\\n\u3000並依照其角色最大HP造成魔法比例傷害。\\n\u3000並且使其陷入束縛狀態。\\n\u3000但是，當無名的藝術受到\\n\u3000下毒、猛毒、詛咒、火傷等效果時，\\n\u3000不會被施加束縛。\\n‧當自身受到下毒、猛毒、詛咒、火傷時，\\n\u3000特大幅度降低自身的物理、魔法防禦力。');
insert into unit_enemy_data values (313205, '無名的藝術', 313200, 0, 16, 270, 3000, 1, 2.0, 0, 0, 0, '【MODE2】黏土人偶所畫的獨特\\n設計的魔法陣。不要被視覺\\n迷惑，趁其鬆懈時一眨眼將它打扁。\\n\\n‧必殺技會對敵方全體依照其最大\\n\u3000HP造成魔法比例傷害。\\n\u3000而當該角色的HP低於50％時，\\n\u3000使其陷入火傷狀態。\\n\u3000此技能施加的火傷狀態所造成的傷害\\n\u3000將不會提升TP。\\n‧普通攻擊會對前方1名角色\\n\u3000造成中幅度的物理傷害。\\n\u3000無名的藝術所持有的【美感】的數量越多，\\n\u3000傷害也越高。\\n‧以前方第3名角色為中心，\\n\u3000對範圍內角色造成中幅度物理傷害。\\n\u3000無名的藝術所持有的【美感】的數量越多，\\n\u3000傷害也越高。\\n‧戰鬥開始時，會展開魔法陣。\\n\\n【魔法陣效果】\\n‧無名的藝術每秒追加【美感】3個。\\n‧每當無名的藝術受到傷害時，\\n\u3000無名的藝術的【美感】會減少1個。');
insert into unit_enemy_data values (313206, '無名的藝術', 313200, 0, 16, 270, 3000, 1, 2.0, 0, 0, 0, '【MODE3】竭盡殘存的力量，放肆展現最後的\\n瘋狂的黏土人偶。能守護住她們燦爛的\\n學生生活嗎？挺身面對吧，好朋友社！\\n\\n‧必殺技能夠對敵方全體依照其角色最大\\n\u3000HP造成魔法比例傷害。\\n\u3000而當該角色的HP低於50％時，\\n\u3000使其陷入火傷狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n\u3000此技能施加的火傷狀態所造成的傷害\\n\u3000將不會提升TP。\\n‧以前方第3名角色為中心，\\n\u3000對範圍內依照其角色\\n\u3000最大HP造成物理比例傷害。\\n\u3000並且對陷入火傷狀態的角色\\n\u3000依照其最大HP造成追加物理比例傷害。\\n‧當剩餘時間不到30秒時，\\n\u3000將會大幅度提升自身的TP，\\n\u3000其攻擊將會變得更加兇暴。');
