insert into unit_enemy_data values (313301, '究極機械玩具王', 313300, 0, 16, 270, 3000, 1, 3.0, 0, 0, 0, '【物理】因被遺忘而失控的悲慘的玩具之王。\\n三度被孩子們遺忘，使它陷在恐懼與悲傷中難以自拔。\\n來吧！現在正是BB團的奮起之時！\\n\\n‧必殺技會對敵方全體依照其最大HP\\n\u3000造成物理比例傷害，\\n\u3000並特大幅度降低距離最近的1名敵人的\\n\u3000物理攻擊力與魔法攻擊力。\\n\u3000此技能的效果，即使傷害無效也會施加。\\n‧對距離最近的1名敵人依照其最大HP\\n\u3000造成物理比例傷害。\\n‧對物理攻擊力或魔法攻擊力其中一者\\n\u3000最高的2名角色依照其最大HP\\n\u3000造成物理比例傷害。');
insert into unit_enemy_data values (313302, '究極機械玩具王', 313300, 0, 16, 270, 3000, 1, 3.0, 0, 0, 0, '【物理】因被遺忘而失控的悲慘的玩具之王。\\n三度被孩子們遺忘，使它陷在恐懼與悲傷中難以自拔。\\n來吧！現在正是BB團的奮起之時！\\n\\n‧必殺技會對敵方全體依照其最大HP\\n\u3000造成物理比例傷害，\\n\u3000並特大幅度降低距離最近的1名敵人的\\n\u3000物理攻擊力與魔法攻擊力。\\n\u3000此技能的效果，即使傷害無效也會施加。\\n‧對距離最近的1名敵人依照其最大HP\\n\u3000造成物理比例傷害。\\n‧對物理攻擊力或魔法攻擊力其中一者\\n\u3000最高的2名角色依照其最大HP\\n\u3000造成物理比例傷害。');
insert into unit_enemy_data values (313303, '究極機械玩具王', 313300, 0, 16, 270, 3000, 1, 3.0, 0, 0, 0, '【物理】因被遺忘而失控的悲慘的玩具之王。\\n三度被孩子們遺忘，使它陷在恐懼與悲傷中難以自拔。\\n來吧！現在正是BB團的奮起之時！\\n\\n‧當究極機械玩具王自身的HP低於40％時，\\n\u3000它的兩臂會綻放光芒，\\n\u3000攻擊也將變得更加凶暴。\\n‧必殺技會對敵方全體依照最大HP造成物理\\n\u3000比例傷害，並特大幅度降低距離最近的\\n\u30001名敵人的物理攻擊力與魔法攻擊力。\\n\u3000此技能的效果，即使傷害無效也會施加。\\n\u3000在它的兩臂發光時，造成的傷害會增加，\\n\u3000並且對距離最近的1名敵人造成\\n\u3000大幅度的物理傷害。\\n‧對距離最近的1名敵人依照其最大HP\\n\u3000造成物理比例傷害。\\n\u3000在它的兩臂發光時，造成的傷害會增加，\\n\u3000且此技能所造成的傷害將不會恢復TP。\\n‧對物理攻擊力或魔法攻擊力其中一者\\n\u3000最高的2名角色依照其最大HP\\n\u3000造成物理比例傷害。\\n\u3000在它的兩臂發光時，此技能所造成的傷害\\n\u3000將不會恢復TP。');
insert into unit_enemy_data values (313304, '究極機械玩具王', 313300, 0, 16, 270, 3000, 1, 3.0, 0, 0, 0, '【MODE1】小小的英雄阻擋在失控的好友身前\\n究竟能否將它從悲傷之中解救出來呢？\\n與好朋友號一同挺身戰鬥吧，BB團！\\n\\n‧必殺技會對敵方全體依照其最大HP\\n\u3000造成物理比例傷害，\\n\u3000並特大幅度降低面前1名敵人的\\n\u3000物理攻擊力與魔法攻擊力。\\n\u3000此技能的效果，即使傷害無效\\n\u3000也會施加。\\n‧對面前的1名敵人依照其最大HP\\n\u3000造成物理比例傷害。\\n‧對物理攻擊力或魔法攻擊力其中一者\\n\u3000最高的2名角色依照其最大HP\\n\u3000造成物理比例傷害。\\n‧在一定時間內，\\n\u3000好朋友號會作為角色的夥伴現身，\\n\u3000這時究極機械玩具王會陷入無法行動的狀態。\\n\u3000並且，會以好朋友號為中心，\\n\u3000展開使範圍內角色\\n\u3000物理攻擊力、物理暴擊及TP上升率都\\n\u3000特大幅度提升的領域。');
insert into unit_enemy_data values (313305, '究極機械玩具王', 313305, 0, 16, 270, 3000, 1, 3.0, 0, 0, 0, '【MODE2】悲傷的玩具王創造出的虛像。\\n「誰都會害怕正視自己。你並不孤單！」\\n胸懷如此強烈的情感，別認輸啊，BB團！\\n\\n‧召喚出碧、美冬、珠希的闇影。\\n‧對面前的1名敵人依照其最大HP\\n\u3000造成物理比例傷害。');
insert into unit_enemy_data values (313306, '究極機械玩具王', 313300, 0, 16, 270, 3000, 1, 3.0, 0, 0, 0, '【MODE3】悲傷的玩具王的最後抵抗。\\n受被遺忘的寂寞支配的它有著毀滅性的強大力量。\\n將對朋友的心意化作力量，拯救它吧，BB團！\\n\\n‧當究極機械玩具王自身的HP低於15％時，\\n\u3000它的兩臂會綻放光芒，\\n\u3000且物理防禦力與魔法防禦力會特大幅度降低，\\n\u3000攻擊也將變得更加凶暴。\\n‧必殺技會對敵方全體依照其最大HP\\n\u3000造成物理比例傷害，\\n\u3000並特大幅度降低面前1名敵人的\\n\u3000物理攻擊力與魔法攻擊力。\\n\u3000此技能的效果，即使傷害無效\\n\u3000也會施加。\\n\u3000在它的兩臂發光時，造成的傷害會增加，\\n\u3000並且對面前的1名敵人造成\\n\u3000大幅度的物理傷害。\\n‧對面前的1名敵人依照其最大HP\\n\u3000造成物理比例傷害。\\n\u3000在它的兩臂發光時，造成的傷害會增加，\\n\u3000且此技能所造成的傷害\\n\u3000將不會恢復TP。\\n‧對物理攻擊力或魔法攻擊力其中一者\\n\u3000最高的2名角色依照其最大HP\\n\u3000造成物理比例傷害。\\n\u3000在它的兩臂發光時，此技能所造成的傷害\\n\u3000將不會恢復TP。');
insert into unit_enemy_data values (313400, '劍蛇', 313400, 0, 16, 270, 3000, 1, 3.0, 0, 0, 0, '【物理】尾巴藏有紅色邪劍的凶暴大毒蛇。\\n以有毒的氣息使獵物衰弱，再以可一擊斃命的劍送葬敵人。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果上升的防禦\\n\u3000力，在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自劍蛇所有的傷害，導致中幅度降低隊伍\\n\u3000角色的TP恢復量。\\n‧必殺技會對魔法攻擊力或物理攻擊力兩者之中\\n\u3000最高的1名敵人造成固定的物理傷害。當角色\\n\u3000處於中毒狀態時再追加固定的物理傷害。之後\\n\u3000特大幅度降低敵方全體的HP吸收。此技能所\\n\u3000造成的狀態異常，即使傷害無效也會施加。\\n‧對敵方全體造成中幅度的魔法傷害，並施加\\n\u3000中毒狀態。此技能所造成的狀態異常，即使\\n\u3000傷害無效也會施加。\\n‧普通攻擊會對距離最近的1名敵人造成大幅度\\n\u3000的物理傷害，並使其陷入中毒狀態。\\n‧劍蛇施加的中毒所造成的傷害將不會恢復TP。');
insert into unit_enemy_data values (313401, '劍蛇', 313401, 0, 16, 270, 3000, 1, 3.0, 0, 0, 0, '【物理】尾巴藏有紅色邪劍的凶暴大毒蛇。\\n以有毒的氣息使獵物衰弱，再以可一擊斃命的劍送葬敵人。\\n\\n‧戰鬥開始時，中幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果而上升的防禦\\n\u3000力，在戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，展開小幅度降低敵方全體TP上升\\n\u3000的領域。\\n‧戰鬥開始時，對自身施加TP降低減少25％\\n\u3000效果。\\n‧中幅度降低來自劍蛇所有傷害產生的隊伍角色\\n\u3000TP恢復量。\\n‧大幅度降低來自敵方所有傷害產生的劍蛇TP\\n\u3000恢復量。\\n‧必殺技會對物理攻擊力或魔法攻擊力兩者之中\\n\u3000最高的1名敵人造成固定的物理傷害。當角色\\n\u3000處於中毒狀態時再追加固定的物理傷害。之後\\n\u3000特大幅度降低敵方全體的HP吸收。此技能所\\n\u3000造成的狀態異常，即使傷害無效也會施加。\\n\u3000在使用過此技能之後，每當進行了攻擊，劍蛇\\n\u3000在接下來每次對該敵人所造成的傷害，都將會\\n\u3000有所提升。此效果最多累積次數為1次。\\n‧對敵方全體造成中幅度的魔法傷害，施加中毒\\n\u3000狀態。此技能所造成的狀態異常，即使傷害無效\\n\u3000也會施加。\\n‧普通攻擊會對距離最近的1名敵人造成大幅度\\n\u3000的物理傷害，並使其陷入中毒狀態。\\n‧劍蛇施加的中毒所造成的傷害將不會恢復TP。');
insert into unit_enemy_data values (313402, '劍蛇', 313402, 0, 16, 270, 3000, 1, 3.0, 0, 0, 0, '【物理】尾巴藏有紅色邪劍的凶暴大毒蛇。\\n以有毒的氣息使獵物衰弱，\\n再以可一擊斃命的劍送葬敵人。\\n\\n‧中幅度降低來自劍蛇所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會對物理攻擊力或魔法攻擊力\\n\u3000其中一者最高的1名\\n\u3000敵方角色造成固定的物理傷害。\\n\u3000當角色處於中毒狀態時\\n\u3000造成固定的物理傷害，\\n\u3000當角色處於猛毒狀態時\\n\u3000再追加固定的物理傷害。\\n\u3000之後，特大幅度降低敵方全體的HP吸收。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體造成中幅度的魔法傷害。\\n\u3000並且對當中未處於\\n\u3000中毒狀態的敵方施加中毒狀態，\\n\u3000對已處於中毒狀態的敵方施加猛毒狀態。\\n‧普通攻擊會對距離最近的1名敵人\\n\u3000造成大幅度的物理傷害，\\n\u3000同時使其陷入中毒狀態。\\n‧劍蛇施加的中毒所造成的傷害，\\n\u3000將不會恢復TP。');
insert into unit_enemy_data values (313500, '卡里莎', 313500, 0, 16, 270, 3000, 1, 2.0, 0, 0, 0, '隨心所欲操縱魔物，獲得強化的辣鞭的少年。\\n操使紅藍黃三色的史萊姆發動敏捷的猛攻。\\n\\n‧必殺技會依照卡里莎的物理攻擊力\\n\u3000對前方範圍內的敵方\\n\u3000造成物理比例傷害。\\n‧大幅度提升卡里莎的物理攻擊力。\\n‧依照卡里莎的物理攻擊力\\n\u3000對從前方數來第1名角色\\n\u3000造成物理比例傷害。');
insert into unit_enemy_data values (313501, '卡里莎', 313501, 0, 16, 270, 3000, 1, 2.0, 0, 0, 0, '隨心所欲操縱魔物，獲得強化的辣鞭的少年。\\n操使紅藍黃三色的史萊姆發動敏捷的猛攻。\\n\\n‧必殺技會依照卡里莎的物理攻擊力\\n\u3000對前方範圍內的敵方\\n\u3000造成物理比例傷害。\\n‧大幅度提升卡里莎的物理攻擊力。\\n‧依照卡里莎的物理攻擊力\\n\u3000對從前方數來第1名角色\\n\u3000造成物理比例傷害。');
insert into unit_enemy_data values (313600, '蘭法', 313600, 0, 16, 270, 3001, 2, 3.0, 0, 0, 0, '對於天賦異稟的才華與異常的感受力感到懊惱，深沉平靜的歌姬。\\n雖不好鬥爭，但會為了願望及夥伴而奮戰。\\n\\n‧必殺技會對前方範圍內的敵方造成中幅度的\\n\u3000魔法傷害，並使其陷入結晶化狀態。\\n\u3000此技能所造成的狀態異常，即使傷害無效\\n\u3000也會施加。\\n\u3000處於結晶化狀態會無法行動，\\n\u3000並受到持續傷害。\\n\u3000完全恢復HP或經過一定時間後，\\n\u3000結晶化狀態就會解除。\\n‧對前方範圍內的敵方施加猛毒狀態。\\n\u3000對處於結晶化狀態的敵方造成大幅度的\\n\u3000魔法追加傷害。\\n‧對從前方數來第1名角色造成小幅度的魔法傷害。');
insert into unit_enemy_data values (313601, '蘭法', 313601, 0, 16, 270, 3001, 2, 3.0, 0, 0, 0, '對於天賦異稟的才華與異常的感受力感到懊惱，深沉平靜的歌姬。\\n雖不好鬥爭，但會為了願望及夥伴而奮戰。\\n\\n‧必殺技會對前方範圍內的敵方造成中幅度的\\n\u3000魔法傷害，並使其陷入結晶化狀態。\\n\u3000此技能所造成的狀態異常，即使傷害無效\\n\u3000也會施加。\\n\u3000處於結晶化狀態會無法行動，\\n\u3000並受到持續傷害。\\n\u3000完全恢復HP或經過一定時間後，\\n\u3000結晶化狀態就會解除。\\n‧對前方範圍內的敵方施加猛毒狀態。\\n\u3000對處於結晶化狀態的敵方造成大幅度的\\n\u3000魔法追加傷害。\\n‧對從前方數來第1名角色造成小幅度的魔法傷害。');
insert into unit_enemy_data values (313700, '美空', 313700, 0, 16, 270, 3000, 1, 2.0, 0, 0, 0, '總是樂觀開朗，和任何人都能當朋友的活潑少女。\\n喜歡使用轉輪機槍，隻身對敵人施展飽和攻擊。\\n\\n‧戰鬥開始時，召喚怪奇怪靈。\\n\u3000召喚時擊退前方範圍內的\\n\u3000敵方全體。\\n\u3000怪奇怪靈會對從前方數來2名角色造成大幅度的\\n\u3000物理傷害。\\n‧必殺技會對敵方全體\\n\u3000造成物理比例傷害。\\n\u3000並且大幅度提升怪奇怪靈的物理攻擊力\\n\u3000與行動速度。\\n‧恢復怪奇怪靈的HP。\\n‧對從前方數來第1名角色造成\\n\u3000小幅度的物理傷害。');
insert into unit_enemy_data values (313701, '美空', 313701, 0, 16, 270, 3000, 1, 2.0, 0, 0, 0, '總是樂觀開朗，和任何人都能當朋友的活潑少女。\\n喜歡使用轉輪機槍，隻身對敵人施展飽和攻擊。\\n\\n‧戰鬥開始時，召喚怪奇怪靈。\\n\u3000召喚時擊退前方範圍內的\\n\u3000敵方全體。\\n\u3000怪奇怪靈會對從前方數來2名角色造成大幅度的\\n\u3000物理傷害。\\n‧必殺技會對敵方全體\\n\u3000造成物理比例傷害。\\n\u3000並且大幅度提升怪奇怪靈的物理攻擊力\\n\u3000與行動速度。\\n\u3000此技能造成的傷害會減少\\n\u3000TP恢復量。\\n‧恢復怪奇怪靈的HP。\\n‧對從前方數來第1名角色造成\\n\u3000小幅度的物理傷害。');
insert into unit_enemy_data values (313702, '美空', 313702, 0, 16, 270, 3000, 2, 2.0, 0, 0, 0, '【魔法】美空冒險最後的難關。「如果沒辦法擊退\\n的話……就把所有人──都殺光光哦♪」\\n\\n‧必殺技會對敵方全體依照其最大HP造成魔法\\n\u3000比例傷害。\\n‧對前方第1名、第2名、第3名的敵人造成\\n\u3000小幅度的魔法傷害。\\n‧中幅度降低敵方全體的魔法防禦力，並且施加\\n\u3000火傷狀態。當敵方的HP低於50％時，施加\\n\u3000混亂狀態。');
insert into unit_enemy_data values (313703, '美空', 313702, 0, 16, 270, 3000, 2, 2.0, 0, 0, 0, '【魔法】美空冒險最後的難關。「如果沒辦法擊退\\n的話……就把所有人──都殺光光哦♪」\\n\\n‧必殺技會對敵方全體依照其最大HP造成魔法\\n\u3000比例傷害。\\n‧對前方第1名、第2名、第3名的敵人造成\\n\u3000小幅度的魔法傷害。\\n‧中幅度降低敵方全體的魔法防禦力，並且施加\\n\u3000火傷狀態。當敵方的HP低於50％時，施加\\n\u3000混亂狀態。');
insert into unit_enemy_data values (313704, '美空', 313702, 0, 16, 270, 3000, 2, 2.0, 0, 0, 0, '【魔法】美空冒險最後的難關。「如果沒辦法擊退\\n的話……就把所有人──都殺光光哦♪」\\n\\n‧必殺技會對敵方全體依照其最大HP造成魔法\\n\u3000比例傷害。\\n‧對前方第1名、第2名、第3名的敵人造成\\n\u3000小幅度的魔法傷害。\\n‧中幅度降低敵方全體的魔法防禦力，並且施加\\n\u3000火傷狀態。當敵方的HP低於50％時，施加\\n\u3000混亂狀態。');
insert into unit_enemy_data values (313705, '美空', 313705, 0, 16, 270, 3000, 2, 2.0, 0, 0, 0, '【MODE1】從口金包裡召喚出了瘋狂的娃娃。\\n那令人毛骨悚然的身軀，既能充當美空的盾，\\n也能充當美空的矛。「Go～Go～怪奇怪靈★」\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果而上升的防禦\\n\u3000力，在戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，召喚怪奇怪靈。召喚時，將敵方\\n\u3000全體擊退。怪奇怪靈會對前方2名角色造成\\n\u3000大幅度的物理傷害。\\n‧中幅度降低來自美空所有傷害產生的隊伍角色\\n\u3000TP恢復量。\\n‧必殺技會對敵方全體依照其最大HP造成魔法\\n\u3000比例傷害。同時，大幅度提升怪奇怪靈的物理\\n\u3000攻擊力與行動速度。\\n‧小幅度恢復怪奇怪靈的HP。\\n‧對前方第1名、第2名、第3名的敵人造成\\n\u3000小幅度的魔法傷害。');
insert into unit_enemy_data values (313706, '美空', 313706, 0, 16, 270, 3000, 2, 2.0, 0, 0, 0, '【MODE2】出現的是莉莉等人的闇影。\\n落下的刀刃，迫使人面對令人厭惡的過去。\\n然而不擊敗的話，就無法前進……！\\n\\n‧戰鬥開始時，中幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果而上升的\\n\u3000防禦力，在戰鬥中不會受到防禦力下降技能的\\n\u3000影響。\\n‧中幅度降低來自美空所有傷害產生的隊伍角色\\n\u3000TP恢復量。\\n‧對前方第1名、第2名、第3名的敵人造成\\n\u3000小幅度的魔法傷害。');
insert into unit_enemy_data values (313707, '美空', 313705, 0, 16, 270, 3000, 2, 2.0, 0, 0, 0, '【MODE3】能夠阻止戰鬥的只有黑天使。\\n為了守護重要之物，為了作為冒險者踏出新的\\n一步……以毫不迷惘的翅膀橫掃巨大的邪惡吧！\\n\\n‧戰鬥開始時，中幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果而上升的防禦\\n\u3000力，在戰鬥中不會受到防禦力下降技能的影響。\\n‧中幅度降低來自美空所有傷害產生的隊伍角色\\n\u3000TP恢復量。\\n‧必殺技會對敵方全體依照其最大HP造成魔法\\n\u3000比例傷害。\\n‧對前方第1名、第2名、第3名的敵人造成\\n\u3000小幅度的魔法傷害。\\n‧中幅度降低敵方全體的魔法防禦力，並且施加\\n\u3000火傷狀態。當敵方的HP低於50％時，施加\\n\u3000混亂狀態。此技能施加的火傷所造成的傷害，\\n\u3000將不會恢復TP。');
insert into unit_enemy_data values (313800, '阿澤爾德', 313800, 0, 16, 270, 3000, 1, 1.5, 0, 0, 0, '總是暗藏好幾招殺手鐧的大胃王紳士。\\n操縱萬能人偶並運用謀略將敵人逼至絕境。\\n\\n‧必殺技會依照阿澤爾德的物理攻擊力\\n\u3000對敵方全體造成物理比例傷害。\\n\u3000並且大幅度降低阿澤爾德的物理防禦力\\n\u3000與魔法防禦力。\\n‧召喚萬能人偶並擊退敵方全體。\\n\u3000萬能人偶會對從前方數來第1名角色造成\\n\u3000小幅度的物理傷害。\\n\u3000萬能人偶的HP低於一定數值時，\\n\u3000大幅度降低阿澤爾德的物理防禦力\\n\u3000與魔法防禦力。\\n‧對從前方數來第1名角色造成小幅度的物理傷害。');
insert into unit_enemy_data values (313801, '阿澤爾德', 313801, 0, 16, 270, 3000, 1, 1.5, 0, 0, 0, '總是暗藏好幾招殺手鐧的大胃王紳士。\\n操縱萬能人偶並運用謀略將敵人逼至絕境。\\n\\n‧必殺技會依照阿澤爾德的物理攻擊力\\n\u3000對敵方全體造成物理比例傷害。\\n\u3000並且大幅度降低阿澤爾德的物理防禦力\\n\u3000與魔法防禦力。\\n‧召喚萬能人偶並擊退敵方全體。\\n\u3000萬能人偶會對從前方數來第1名角色造成\\n\u3000小幅度的物理傷害。\\n\u3000萬能人偶的HP低於一定數值時，\\n\u3000大幅度降低阿澤爾德的物理防禦力\\n\u3000與魔法防禦力。\\n‧對從前方數來第1名角色造成小幅度的物理傷害。');
insert into unit_enemy_data values (313900, '贊恩', 313900, 0, 16, 270, 3000, 1, 1.0, 313900, 313900, 0, '橫掃千軍、萬夫莫敵，沉默寡言的劍士。\\n一經覺醒，龍將飛升至更高的境界。\\n\\n‧必殺技會依照贊恩的\\n\u3000物理攻擊力與剩餘HP造成\\n\u3000物理比例傷害。\\n\u3000此技能造成的傷害\\n\u3000會減少TP恢復量。\\n‧對敵方全體造成中幅度的物理傷害，\\n\u3000並使其暈眩。\\n‧大幅度提升贊恩的TP上升、行動速度、\\n\u3000物理攻擊力。\\n\u3000每次發動，此技能的效果\\n\u3000都會提升。\\n\u3000第3次發動時再提升TP1000。\\n‧對從前方數來第1名角色造成\\n\u3000中幅度的物理傷害，並小幅度擊飛。');
