insert into unit_enemy_data values (314001, '擅長的作業', 314001, 0, 16, 270, 3000, 1, 1.0, 0, 0, 0, '一開始先來做完比較擅長的作業吧。\\n不能在這裡一蹶不振。\\n暑假作業還有很多很多喔！！\\n\\n‧在戰鬥開始後，\\n\u3000大量的作業會從天而降，\\n\u3000對敵方全體造成中幅度物理傷害。\\n\u3000此技能造成的傷害，使角色無法恢復TP。\\n‧在戰鬥中，每當經過一定時間，\\n\u3000怪物會蜂擁而來。');
insert into unit_enemy_data values (314002, '有些棘手的作業', 314002, 0, 16, 270, 3000, 1, 1.0, 0, 0, 0, '不擅長的科目的作業，任何人都會遇到。\\n往往在不知不覺間一直拖延，\\n結果累積了一大堆沒寫，真是吃足苦頭了。\\n不可以停在這裡止步不前！\\n\\n‧在戰鬥開始後，大量的作業會從天而降，\\n\u3000對敵方全體造成中幅度物理傷害。此技能\\n\u3000所造成的傷害，會使角色無法恢復TP。\\n‧在戰鬥中，每當經過一定時間，\\n\u3000怪物會蜂擁而來。');
insert into unit_enemy_data values (314003, '非常棘手的作業', 314003, 0, 16, 270, 3000, 1, 1.0, 0, 0, 0, '討人厭的作業，光看就讓人頭昏。\\n強忍住丟下手中的筆衝到外頭去的衝動，\\n踏實地向前邁進就是獲勝的關鍵！\\n\\n‧在戰鬥開始後，大量的作業會從天而降，\\n\u3000對敵方全體造成中幅度物理傷害。\\n\u3000此技能所造成的傷害，\\n\u3000會使角色無法恢復TP。\\n‧在戰鬥中，每當經過一定時間，\\n\u3000怪物會蜂擁而來。');
