insert into unit_enemy_data values (314101, '女王歌唱水母', 314100, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】在大海上像花一般搖盪，唱出悠揚歌聲的\\n歌唱水母女王。外表裝飾得美麗動人，\\n裡頭卻隱藏著帶有猛毒的體液。\\n\\n‧必殺技會依照角色的最大HP對敵方全體造成\\n\u3000魔法比例傷害。對處於猛毒狀態的敵方\\n\u3000依照最大HP追加魔法比例傷害。\\n\u3000並且對敵方全體施加中毒狀態，\\n\u3000對已處於中毒或猛毒狀態的敵方施加猛毒狀態。\\n‧對距離最近的1名敵方造成大幅度的魔法傷害。\\n‧對敵方全體造成小幅度的魔法傷害，\\n\u3000並施加中毒狀態。\\n\u3000敵方處於猛毒狀態時使其暈眩。\\n‧女王歌唱水母施加的中毒及猛毒\\n\u3000所造成的傷害不會恢復角色的TP。');
insert into unit_enemy_data values (314102, '女王歌唱水母', 314100, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】在大海上像花一般搖盪，唱出悠揚歌聲的\\n歌唱水母女王。外表裝飾得美麗動人，\\n裡頭卻隱藏著帶有猛毒的體液。\\n\\n‧必殺技會依照角色的最大HP對敵方全體造成\\n\u3000魔法比例傷害。對處於猛毒狀態的敵方\\n\u3000依照最大HP追加魔法比例傷害。\\n\u3000並且對敵方全體施加中毒狀態，\\n\u3000對已處於中毒或猛毒狀態的敵方施加猛毒狀態。\\n‧對距離最近的1名敵方造成大幅度的魔法傷害。\\n‧對敵方全體造成小幅度的魔法傷害，\\n\u3000並施加中毒狀態。\\n\u3000敵方處於猛毒狀態時使其暈眩。\\n‧女王歌唱水母施加的中毒及猛毒\\n\u3000所造成的傷害不會恢復角色的TP。');
insert into unit_enemy_data values (314103, '女王歌唱水母', 314100, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】在大海上像花一般搖盪，唱出悠揚歌聲的\\n歌唱水母女王。外表裝飾得美麗動人，\\n裡頭卻隱藏著帶有猛毒的體液。\\n\\n‧必殺技會依照角色的最大HP對敵方全體造成\\n\u3000魔法比例傷害。對處於猛毒狀態的敵方\\n\u3000依照最大HP追加魔法比例傷害。\\n\u3000並且對敵方全體施加中毒狀態，\\n\u3000對已處於中毒或猛毒狀態的敵方施加猛毒狀態。\\n‧對距離最近的1名敵方造成大幅度的魔法傷害。\\n‧對敵方全體造成小幅度的魔法傷害，\\n\u3000並施加中毒狀態。\\n\u3000敵方處於猛毒狀態時使其暈眩。\\n‧女王歌唱水母施加的中毒及猛毒\\n\u3000所造成的傷害不會恢復角色的TP。\\n‧當剩餘時間不到25秒時，攻擊將會變得\\n\u3000更加兇暴。');
insert into unit_enemy_data values (314104, '女王歌唱水母', 314104, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE1】襲擊而來的無數魔物。\\n其歌聲轟然迴響，危害著舞台。為了守護\\n粉絲的笑容，少女們唱歌跳舞並勇敢奮戰。\\n\\n‧在戰鬥開始時，召喚3隻歌唱水母，\\n\u3000擊退敵方全體。\\n‧每打倒1隻歌唱水母，剩餘的歌唱水母\\n\u3000數量會減少1。\\n\u3000之後會再次召喚歌唱水母，擊退敵方全體。\\n‧歌唱水母會對剩餘HP最高的1名敵方角色\\n\u3000造成中幅度的魔法傷害，並施加中毒狀態。\\n‧必殺技會依照角色的HP\\n\u3000對敵方全體造成魔法比例傷害。\\n\u3000對處於猛毒狀態的敵方傷害會提升。\\n\u3000並且對敵方施加中毒狀態，對已處於中毒\\n\u3000或猛毒狀態的敵方施加猛毒狀態。\\n‧對面前的1名敵方角色造成大幅度的魔法傷害。\\n‧女王歌唱水母與歌唱水母施加的中毒及猛毒\\n\u3000所造成的傷害不會恢復角色的TP。');
insert into unit_enemy_data values (314105, '女王歌唱水母', 314105, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE2】出現在眼前的是\\n過去的自己。成長後的結果，少女們超越了過去，\\n將那每一瞬間的光芒都化成了歌聲。\\n\\n‧召喚慈樂之音的闇影。\\n‧對面前的1名敵方角色造成大幅度的魔法傷害。');
insert into unit_enemy_data values (314106, '女王歌唱水母', 314104, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE3】優雅的花紛飛，解放\\n內在的魔性。將劇毒的雨聲化成旋律，\\n歌姬就此演唱最後的一首歌。\\n\\n‧必殺技會依照角色的HP\\n\u3000對敵方全體造成魔法比例傷害。\\n\u3000對處於猛毒狀態的敵方傷害會提升。\\n\u3000並且對敵方施加中毒狀態，\\n\u3000對已處於中毒或猛毒狀態的敵方\\n\u3000施加猛毒狀態。\\n‧對面前的1名敵方角色造成大幅度的魔法傷害。\\n‧對敵方全體造成小幅度的魔法傷害，\\n\u3000並施加中毒狀態。\\n\u3000敵方處於猛毒狀態時使其暈眩。\\n‧女王歌唱水母施加的中毒及猛毒\\n\u3000所造成的傷害不會恢復角色的TP。\\n‧當剩餘時間不到25秒時，攻擊將會變得\\n\u3000更加兇暴。');
