insert into unit_enemy_data values (314201, '基古魯博士', 314200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】露出劣根性的大江戶怪醫。\\n打算靠著在研究中所獲得的究極肉體，\\n向世界進行報復。\\n\\n‧必殺技會依照基古魯博士的物理攻擊力，\\n\u3000對敵方全體造成物理比例傷害，\\n\u3000並使其陷入中毒狀態。\\n‧召喚惡魔燒瓶並擊退\\n\u3000敵方全體。\\n‧惡魔燒瓶每當經過一定的時間，\\n\u3000將中幅度提升基古魯博士的物理攻擊力，\\n\u3000並且在特大幅度恢復TP後，當場消失。\\n\u3000但是當惡魔燒瓶的HP低於一定數值時，\\n\u3000將特大幅度提升敵方全體的\\n\u3000物理攻擊力與魔法攻擊力，\\n\u3000並且在特大幅度恢復TP後，當場消失。\\n\u3000同時基古魯博士下次所召喚出的\\n\u3000惡魔燒瓶將強化1個階段。\\n‧對物理攻擊力或魔法攻擊力\\n\u3000其中一者最高的1名敵方角色\\n\u3000造成小幅度的物理傷害，\\n\u3000同時中幅度降低其行動速度。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (314202, '基古魯博士', 314200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】露出劣根性的大江戶怪醫。\\n打算靠著在研究中所獲得的究極肉體，\\n向世界進行報復。\\n\\n‧必殺技會依照基古魯博士的物理攻擊力，\\n\u3000對敵方全體造成物理比例傷害，\\n\u3000並使其陷入中毒狀態。\\n‧召喚惡魔燒瓶並擊退\\n\u3000敵方全體。\\n‧惡魔燒瓶每當經過一定的時間，\\n\u3000將中幅度提升基古魯博士的物理攻擊力，\\n\u3000並且在特大幅度恢復TP後，當場消失。\\n\u3000但是當惡魔燒瓶的HP低於一定數值時，\\n\u3000將特大幅度提升敵方全體的\\n\u3000物理攻擊力與魔法攻擊力，\\n\u3000並且在特大幅度恢復TP後，當場消失。\\n\u3000同時基古魯博士下次所召喚出的\\n\u3000惡魔燒瓶將強化1個階段。\\n‧對物理攻擊力或魔法攻擊力\\n\u3000其中一者最高的1名敵方角色\\n\u3000造成小幅度的物理傷害，\\n\u3000同時中幅度降低其行動速度。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (314203, '基古魯博士', 314200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】露出劣根性的大江戶怪醫。\\n打算靠著在研究中所獲得的究極肉體，\\n向世界進行報復。\\n\\n‧必殺技會依照基古魯博士的物理攻擊力，\\n\u3000對敵方全體造成物理比例傷害，\\n\u3000並使其陷入中毒狀態。\\n‧召喚惡魔燒瓶並擊退\\n\u3000敵方全體。\\n‧惡魔燒瓶每當經過一定的時間，\\n\u3000將特大幅度提升基古魯博士的物理攻擊力，\\n\u3000並且在特大幅度恢復TP後，當場消失。\\n\u3000但是當惡魔燒瓶的HP低於一定數值時，\\n\u3000將特大幅度提升敵方全體的\\n\u3000物理攻擊力與魔法攻擊力，\\n\u3000並且在特大幅度恢復TP後，\\n\u3000當場消失。同時基古魯博士下次所\\n\u3000召喚出的惡魔燒瓶將強化1個階段。\\n‧對物理攻擊力或魔法攻擊力\\n\u3000其中一者最高的1名敵方角色\\n\u3000造成小幅度的物理傷害，\\n\u3000同時中幅度降低其行動速度。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧當基古魯博士自身的HP低於30％時，\\n\u3000其攻擊將會變得更加狂暴。');
insert into unit_enemy_data values (314204, '基古魯博士', 314204, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE1】藉著迷幻藥所看到的自我虛像。\\n秉持著真誠的醫道，擊碎阻擋在眼前的\\n實體化邪魔歪道。\\n\\n‧召喚深月、雪的闇影及魔物。\\n‧對面前的1名角色造成中幅度的物理傷害。');
insert into unit_enemy_data values (314205, '基古魯博士', 314205, 0, 16, 270, 3000, 1, 0.0, 0, 0, 0, '【MODE2】從魔手釋放出的妖光在地上疾馳。\\n被其魔性所吞噬的實驗品們，\\n忘我地瘋狂咆哮。\\n\\n‧召喚魔物，擊退敵方全體。\\n‧每打倒1隻被召喚出來的魔物，\\n\u3000剩餘的目標數量就會減少1個。\\n\u3000之後再次召喚魔物出來，\\n\u3000將擊退敵方全體。\\n‧特大幅度提升基古魯博士以外我方全體的\\n\u3000物理防禦力與魔法防禦力。\\n\u3000基古魯博士的HP在戰鬥開始後，\\n\u3000當減少2000以上及4000以上時，\\n\u3000其外貌就會改變，\\n\u3000此技能效果將降低。');
insert into unit_enemy_data values (314206, '基古魯博士', 314200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE3】肉體的進化提升到極限。\\n進化的最後，野心的最後，打算以這隻魔手\\n掌握世界的怪醫憤而怒吼。\\n\\n‧必殺技會依照基古魯博士的物理攻擊力\\n\u3000對敵方全體造成物理比例傷害，\\n\u3000並使其陷入中毒狀態。\\n‧召喚惡魔燒瓶並擊退敵方全體。\\n‧惡魔燒瓶每當經過一定的時間，\\n\u3000將特大幅度提升基古魯博士的物理攻擊力、\\n\u3000物理防禦力與魔法防禦力，\\n\u3000並在特大幅度恢復TP後，當場消失。\\n\u3000但是當惡魔燒瓶的HP低於一定數值時，\\n\u3000將中幅度提升隊伍全體的物理攻擊力與\\n\u3000魔法攻擊力，直到戰鬥結束，\\n\u3000並且在特大幅度恢復TP後，當場消失。\\n\u3000同時基古魯博士下次所召喚出的\\n\u3000惡魔燒瓶將強化1個階段。\\n‧對物理攻擊力或魔法攻擊力\\n\u3000其中一者最高的1名敵方角色\\n\u3000造成特大幅度的物理傷害，\\n\u3000同時中幅度降低其行動速度。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧當基古魯博士自身的HP低於30％時，\\n\u3000攻擊將會變得更加激烈且凶暴。');
