insert into unit_enemy_data values (314301, '哀暴小棉花糖', 314301, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】在孤單不安之中，成為有錢人的道具，\\n悲傷情緒失控，解放出真正力量的魔犬。\\n除非毀滅一切，否則牠的失控絕不會停止。\\n\\n‧必殺技會解除敵方全體的護盾，\\n\u3000造成大幅度的物理傷害。同時小幅度提升\\n\u3000自己的物理攻擊力。\\n‧對面前的1名敵人造成小幅度的物理傷害。\\n‧大幅度降低距離最近的3名角色的\\n\u3000物理/魔法攻擊力，並大幅度降低\\n\u3000物理防禦力直到戰鬥結束。\\n‧小幅度提升自身的物理攻擊力。');
insert into unit_enemy_data values (314302, '哀暴小棉花糖', 314301, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】在孤單不安之中，成為有錢人的道具，\\n悲傷情緒失控，解放出真正力量的魔犬。\\n除非毀滅一切，否則牠的失控絕不會停止。\\n\\n‧必殺技會解除敵方全體的護盾，\\n\u3000造成大幅度的物理傷害。同時小幅度提升\\n\u3000自己的物理攻擊力。\\n‧對面前的1名敵人造成小幅度的物理傷害。\\n‧大幅度降低距離最近的3名角色的\\n\u3000物理/魔法攻擊力，並大幅度降低\\n\u3000物理防禦力直到戰鬥結束。\\n‧小幅度提升自身的物理攻擊力。');
insert into unit_enemy_data values (314303, '哀暴小棉花糖', 314300, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】在孤單不安之中，成為有錢人的道具，\\n悲傷情緒失控，解放出真正力量的魔犬。\\n除非毀滅一切，否則牠的失控絕不會停止。\\n\\n‧必殺技會解除敵方全體的護盾，\\n\u3000造成中幅度的物理傷害。\\n\u3000同時小幅度提升自己的物理攻擊力。\\n\u3000此攻擊容易受到\\n\u3000物理攻擊力提升、降低的效果影響。\\n‧對面前的1名敵人造成中幅度的物理傷害。\\n\u3000此攻擊容易受到\\n\u3000物理攻擊力提升、降低的效果影響。\\n‧大幅度降低距離最近的3名角色的\\n\u3000物理/魔法攻擊力，並大幅度降低物理防禦力\\n\u3000直到戰鬥結束。\\n‧中幅度提升自身的物理攻擊力。\\n‧當剩餘時間不到30秒時，大幅度降低\\n\u3000敵方全體的物理防禦力，\\n\u3000大幅度提升自身的物理/魔法防禦力，\\n\u3000同時攻擊將會變得更加激烈且凶暴。');
insert into unit_enemy_data values (314304, '哀暴小棉花糖', 314304, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE1】從異界誤闖進來的幽靈，\\n像照鏡子般地幻化成所有人並阻擋在前方。\\n萬聖節拯救隊能否順利取勝，拯救世界？\\n\\n‧必殺技會解除敵方全體的護盾，\\n\u3000造成大幅度的物理傷害。同時小幅度提升\\n\u3000自身的物理攻擊力。此攻擊容易受到\\n\u3000物理攻擊力提升、降低的效果影響。\\n‧對面前的1名角色造成中幅度的物理傷害。');
insert into unit_enemy_data values (314305, '哀暴小棉花糖', 314305, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE2】失控後轉眼間如球體般縮成一團，\\n擋下各種攻擊。即便是使出全力的一擊，\\n在其這般姿態面前也宛如輕撫一般。\\n\\n‧戰鬥開始時，極大幅度提升\\n\u3000自己的物理/魔法防禦力。\\n‧在戰鬥中，哀暴小棉花糖不會行動。');
insert into unit_enemy_data values (314306, '哀暴小棉花糖', 314300, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE3】完全解放身為超特級妖魔的\\n靈力，以足以摧毀世界平衡的氣勢打鬧的\\n小棉花糖。你能夠擁抱牠全部的愛嗎？\\n\\n‧必殺技會解除敵方全體的護盾，\\n\u3000造成中幅度物理傷害。且小幅度提升\\n\u3000自身的物理攻擊力。此攻擊容易受到\\n\u3000物理攻擊力提升、降低的效果影響。\\n‧對面前的1名敵人造成中幅度的物理傷害。\\n\u3000此攻擊容易受到\\n\u3000物理攻擊力提升、降低的效果影響。\\n‧大幅度降低距離最近的3名角色的\\n\u3000物理/魔法攻擊力，\\n\u3000並中幅度降低物理防禦力\\n\u3000直到戰鬥結束。\\n‧小幅度提升自身的物理攻擊力。\\n‧當剩餘時間不到30秒時，\\n\u3000中幅度降低敵方全體的物理防禦力，\\n\u3000中幅度提升自身的物理/魔法防禦力，\\n\u3000且攻擊將會變得更加激烈且凶暴。');
