insert into unit_enemy_data values (314404, '反逆獸‧三重', 314405, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE1】被囚禁在黑暗漩渦中，眼看就要\\n失去希望的魔法少女們──當時耳邊所聽到的，\\n是來自重要之人的聲援。振作起來吧，魔法少女！\\n\\n‧必殺技會使前方的3名角色之中，\\n\u3000HP未達75％的角色陷入誘惑狀態。\\n\u3000之後，對前方的3名角色\\n\u3000依照其最大HP造成魔法比例傷害。\\n\u3000對處於誘惑狀態的敵方傷害會提升。\\n‧戰鬥開始時，使敵方全體陷入詛咒狀態。\\n\u3000此技能施加的詛咒所造成的傷害不會恢復TP。\\n\u3000同時，不論是打算以任何效果進行HP的恢復，\\n\u3000都將降低原本恢復量的60％。\\n‧對前方的2名角色造成小幅度魔法比例傷害。');
insert into unit_enemy_data values (314405, '反逆獸‧三重', 314405, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE2】再次儲備黑暗力量的仇敵。然而，\\n開始失控的不單只是敵人而已──！？\\n已經沒有人能夠阻止這3個人了！\\n\\n‧展開護盾，使其不會受到敵人造成的任何攻擊。\\n\u3000此護盾與傷害量無關，而是每當受到攻擊時，\\n\u3000便會漸漸的降低其耐久度。\\n\u3000當受到490次的攻擊後，護盾將會被破壞。\\n‧對前方的2名角色造成小幅度的魔法傷害。');
insert into unit_enemy_data values (314406, '反逆獸‧三重', 314400, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE3】戰鬥終於進入最高潮。為了親愛的\\n學生、弟弟和虐待狂先生，抱著愛、希望及黑暗，\\n勇往向前吧，黑暗三人組！\\n\\n‧必殺技會使前方的3名角色之中，\\n\u3000HP未達80％的角色陷入誘惑狀態。\\n\u3000之後，對前方的3名角色\\n\u3000依照其最大HP造成魔法比例傷害。\\n\u3000對處於誘惑狀態的敵方傷害會提升。\\n‧召喚反逆獸的屬下，擊退敵方全體。\\n\u3000反逆獸的屬下每當經過一定的時間，\\n\u3000在使面前的1名敵人陷入誘惑狀態後，\\n\u3000當場消失。\\n\u3000但是當反逆獸的屬下的HP低於一定數值時，\\n\u3000在特大幅度提升隊伍整體的物理攻擊力\\n\u3000與魔法攻擊力後，當場消失。\\n‧對前方的2名角色造成小幅度的魔法傷害。\\n\u3000同時對自己展開物理無效的護盾。\\n‧對前方的2名角色\\n\u3000依照其最大HP造成魔法比例傷害。\\n‧反逆獸‧三重自身的HP低於30％時，\\n\u3000其攻擊將會變得更加激烈且凶暴。');
