insert into unit_enemy_data values (314603, '邪牛神哞猛', 314600, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】獲得吉祥物，吸收心願力量，獰猛狂暴的\\n魔物牛。沉浸於憤怒的猛牛會破壞周圍一切願望。\\n\\n‧必殺技會對敵方全體依照邪牛神哞猛的物理攻擊\\n\u3000力及其角色最大HP造成物理比例傷害。並使其\\n\u3000陷入火傷狀態。\\n‧普通攻擊會對面前的1名敵人依照邪牛神哞猛\\n\u3000的物理攻擊力及其角色最大HP造成物理比例\\n\u3000傷害。\\n‧對魔法攻擊力最高的1名敵人造成物理比例\\n\u3000傷害，並將其擊退，同時使其陷入暈眩狀態。\\n‧對前面數來第1、2名角色造成大幅度的物理\\n\u3000傷害。此技能發動後，大幅度提升自身的物理\\n\u3000攻擊力，並大幅度降低物理/魔法防禦力。\\n‧當剩餘時間不到30秒時，牠的攻擊將會變得\\n\u3000更加兇暴。');
insert into unit_enemy_data values (314604, '邪牛神哞猛', 314600, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE1】渴望力量的魔物率領著小弟\\n再次踏上掠奪之路。打倒來犯的\\n亡命之徒們，保護御節料理的材料到底。\\n\\n‧戰鬥開始時，小幅度提升自身的\\n\u3000物理防禦力及魔法防禦力。\\n\u3000因防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n\u3000承受來自邪牛神哞猛及高等獸人所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧戰鬥開始時，召喚3隻高等獸人。\\n\u3000每當打倒1隻高等獸人，\\n\u3000剩餘高等獸人數減少1，\\n\u3000之後將再次召喚高等獸人。\\n‧對面前1名敵人造成小幅度的魔法傷害，\\n\u3000並使其物理防禦力大幅度下降。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (314605, '邪牛神哞猛', 314605, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE2】猛牛爆發強韌的肉體\\n若是正面接下怪臂使出的毆打，\\n肉體和其內的煩惱都將全數被擊飛。\\n\\n‧對敵人攻擊造成的麻痺、暈眩、束縛、凍結、\\n\u3000時間停止、行動速度降低等效果沒有\\n\u3000抗性。\\n‧戰鬥開始時，小幅度提升自身的\\n\u3000物理防禦力及魔法防禦力。\\n\u3000因防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n\u3000承受來自邪牛神哞猛所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技會對敵方全體依照邪牛神哞猛\\n\u3000的物理攻擊力及其角色最大HP造成\\n\u3000物理比例傷害，並使其陷入火傷狀態。\\n‧普通攻擊會對面前1名敵人依照邪牛神哞猛\\n\u3000的物理攻擊力及其角色最大HP造成\\n\u3000物理比例傷害。\\n‧對前面數來第1、2名角色造成大幅度的\\n\u3000物理傷害。此技能發動後，大幅度提升自身的\\n\u3000物理攻擊力，並大幅度降低物理/魔法防禦力。\\n‧解除敵方全體的能力提升效果，\\n並展開維持一定時間的戰鬥領域。\\n【戰鬥場地效果】\\n‧使敵方全體的物理攻擊力及\\n\u3000魔法攻擊力100％下降後，\\n\u3000極大幅度提升其物理攻擊力\\n及魔法攻擊力。\\n‧使邪牛神哞猛的物理攻擊力\\n100％下降後，\\n\u3000極大幅度提升其物理攻擊力。');
insert into unit_enemy_data values (314606, '邪牛神哞猛', 314605, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE3】身受重創的猛牛\\n依然呼吸粗重地攔阻在前。\\n打倒滿心執念的麻煩魔物，奪回謹賀新年！\\n\\n‧戰鬥開始時，小幅度提升自身的\\n\u3000物理防禦力及魔法防禦力。\\n\u3000因防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n\u3000承受來自邪牛神哞猛所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技會對敵方全體依照邪牛神哞猛的\\n\u3000物理攻擊力及其角色最大HP\\n\u3000造成物理比例傷害，\\n\u3000並使其陷入火傷狀態。\\n‧對魔法攻擊力最高的1名敵人\\n\u3000造成物理比例傷害。\\n\u3000造成傷害將其擊退的同時\\n\u3000使之陷入暈眩狀態。\\n‧普通攻擊會對面前1名敵人依照邪牛神哞猛的\\n\u3000物理攻擊力及其角色最大HP\\n\u3000造成物理比例傷害。\\n‧對前面數來第1、2名角色造成大幅度的\\n\u3000物理傷害。此技能發動後，\\n\u3000大幅度提升自身的物理攻擊力，\\n\u3000並大幅度降低物理/魔法防禦力。\\n‧當剩餘時間不到30秒時，\\n\u3000牠的攻擊將會變得更加兇暴。');
