insert into unit_enemy_data values (314804, '超載力量', 314804, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE1】從容器內滿溢而出的過載力量\\n所創造出的假想【美食殿堂】。\\n以這再次連結的羈絆超越自己吧！\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身物理防禦力及魔法防禦力。\\n\u3000因防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自超載力量所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量，\\n\u3000並且召喚貪吃佩可、可可蘿、凱留，\\n\u3000以及雪菲的闇影。\\n‧對前方數來第1名的角色造成大幅度的物理傷害。\\n‧若超載水晶球的目標被破壞，\\n\u3000自身的物理防禦力和魔法防禦力\\n\u3000中幅度下降。');
insert into unit_enemy_data values (314805, '超載力量', 314805, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE2】力量滿溢的球塊變換脈動，\\n展開殲滅的強襲。心念之刃\\n能否突破劍雨而上？\\n超越自身全力，展現應行之路吧！\\n\\n‧戰鬥開始時，超載水晶球將啟動，\\n\u3000解放本體更進一步的力量，\\n\u3000特大幅度降低敵方全體的HP吸收，\\n\u3000攻擊將變得更強力更激烈。\\n‧戰鬥開始時，\\n\u3000小幅度提升自身物理防禦力及魔法防禦力。\\n\u3000因防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自超載力量所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技會對敵方全體造成特大幅度的\\n\u3000物理傷害。\\n‧對敵方全體造成中幅度的物理傷害，\\n\u3000並使其物理防禦力小幅度下降。\\n\u3000同時若超載水晶球的目標尚未被破壞，\\n\u3000提升技能效果。此技能所造成的效果，\\n\u3000即使傷害無效也會施加。\\n‧若超載水晶球的目標被破壞，\\n\u3000將會大幅度降低自身的物理防禦力\\n\u3000和魔法防禦力。');
insert into unit_enemy_data values (314806, '超載力量', 314804, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE3】滿溢的另一道力量改形換貌，\\n化作蒼藍劍閃如流星般灑落。以來路和連結\\n的羈絆之證，邁向與我繫起的未來！\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身物理防禦力及魔法防禦力。\\n\u3000因防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自超載力量所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技會對敵方全體\\n\u3000成大幅度的物理傷害。\\n\u3000同時若超載水晶球的目標尚未被破壞，\\n\u3000提升造成的傷害。\\n‧對敵方全體造成中幅度的物理傷害，\\n\u3000並使其物理防禦力中幅度下降。\\n‧同時若超載水晶球的目標尚未被破壞，\\n\u3000提升造成的傷害與技能效果。\\n\u3000此技能所造成的效果，\\n\u3000即使傷害無效也會施加。\\n‧對前方數來第1名的角色\\n\u3000造成大幅度的物理傷害。\\n‧若超載水晶球的目標被破壞，\\n\u3000自身的物理防禦力和魔法防禦力中幅度下降。');
insert into unit_enemy_data values (314901, '哥布林領主＆哥布林小兵', 314901, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【MODE1】盤踞在沙瀑一帶的哥布林族首領。\\n會帶領手下一起攻擊抵達據點的老練冒險者。\\n\\n▼MODE1突破條件\\n造成傷害，使哥布林領主＆\\n哥布林小兵的HP降至8880萬以下。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，使用傷害上限技能，\\n\u3000在一次行動中造成的傷害值超過一定值時，\\n\u3000會根據造成的傷害值減少傷害量。\\n‧承受來自哥布林領主＆哥布林小兵所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技會對敵方全體依照其最大HP\\n\u3000造成物理比例傷害，並且大距離向後擊飛。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體依照其最大HP\\n\u3000造成物理比例傷害。\\n‧當裝甲車上的哥布林小兵的目標部位被破壞時，\\n\u3000會當場消失，並且會小幅度降低哥布林領主＆\\n\u3000哥布林小兵的物理防禦力與魔法防禦力。\\n‧當裝甲車的核心目標部位被破壞時，\\n\u3000將會小幅度降低哥布林領主＆哥布林小兵\\n\u3000的物理防禦力與魔法防禦力。\\n‧當哥布林領主＆哥布林小兵的目標部位全都被\\n\u3000破壞時，將會特大幅度降低物理防禦力\\n\u3000與魔法防禦力，並且會改變攻擊行動。\\n‧變更行動後，核心會開始累積能量，\\n\u3000每當經過一定時間，\\n\u3000將會賦予自身持續恢復TP的狀態。\\n\u3000在累積能量期間，受到一定的傷害時，\\n\u3000技能將會被迫中斷，並陷入火傷狀態。\\n‧當戰鬥的時間結束時，\\n\u3000使敵方全體無法再繼續戰鬥。');
insert into unit_enemy_data values (315001, '哥布林領主', 315001, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【MODE2】因裝甲車被破壞成廢物而暴怒的首領。\\n安排哥布林族的強者，作為冒險者的下一道屏障，\\n不允許被輕易突破。\\n\\n▼MODE2突破條件\\n造成傷害，使哥布林王者和哥布林大師的HP歸零\\n\\n‧戰鬥開始時，讓自己處於無敵狀態，\\n\u3000不會受到敵人造成的任何攻擊。\\n‧承受來自哥布林領主所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技能夠對敵方全體依照其剩餘HP\\n\u3000造成物理比例傷害。\\n‧當戰鬥的時間結束時，\\n\u3000使敵方全體無法再繼續戰鬥。');
insert into unit_enemy_data values (315002, '哥布林領主', 315002, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【MODE3】君臨沙瀑的哥布林族首領。\\n將手下被殺的憤怒寄託於武器，\\n以壓倒性的力量將眼前的一切化為塵土。\\n\\n▼MODE3突破條件\\n造成傷害，使哥布林領主的HP歸零。\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，使用傷害上限技能，\\n\u3000在一次行動中造成的傷害值超過一定值時，\\n\u3000會根據造成的傷害值減少傷害量。\\n‧承受來自哥布林領主所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技會對敵方全體依照其剩餘HP造成物理\\n\u3000比例傷害，並大幅度提升自身的物理攻擊力。\\n\u3000造成的傷害程度會隨著自身的物理攻擊力而增加\\n‧對敵方全體造成大幅度的物理傷害，\\n\u3000並且使敵人暈眩。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對前方範圍內的敵人造成大幅度的物理傷害，\\n\u3000並且大距離向後擊飛。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對前方的1名角色依照其角色最大HP\\n\u3000造成物理比例傷害。\\n‧當剩餘HP低於1872萬時，\\n\u3000會特大幅度提升物理攻擊力和行動速度，\\n\u3000同時攻擊將會變得更加激烈且凶暴。\\n‧當戰鬥的時間結束時，\\n\u3000使敵方全體無法再繼續戰鬥。');
