insert into unit_enemy_data values (315101, '仇鼠‧怒髮衝冠的啾二', 315100, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】老鼠家族的偉大老大。\\n在牧場吞下敗仗後，遁逃到了育幼院。\\n如今時機成熟，為了奪回故鄉而展開復仇。\\n\\n‧必殺技會對敵方全體造成小幅度的物理傷害，\\n\u3000並中幅度降低物理防禦力。\\n\u3000當復仇之火燃燒時，物理防禦力降低會消失的\\n\u3000同時，造成的傷害會提升。\\n‧以前方數來第1名的角色為中心，對範圍內的\\n\u3000敵人中幅度降低物理防禦力與魔法防禦力。\\n‧中幅度提升我方全體的物理攻擊力，\\n\u3000並特大幅度恢復自身的TP。\\n‧對前方數來第1名角色造成小幅度的物理傷害。\\n‧當仇鼠‧赤手空拳的啾三已被討伐時，\\n\u3000當戰鬥開始時或仇鼠‧怒髮衝冠的啾二\\n\u3000進行普通攻擊時，會燃起復仇之火，\\n\u3000同時攻擊將會變得更加激烈且凶暴。');
insert into unit_enemy_data values (315102, '仇鼠‧怒髮衝冠的啾二', 315100, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】老鼠家族的偉大老大。\\n在牧場吞下敗仗後，遁逃到了育幼院。\\n如今時機成熟，為了奪回故鄉而展開復仇。\\n\\n‧必殺技會對敵方全體造成小幅度的物理傷害，\\n\u3000並中幅度降低物理防禦力。\\n\u3000當復仇之火燃燒時，物理防禦力降低會消失的\\n\u3000同時，造成的傷害會提升。\\n‧以前方數來第1名的角色為中心，對範圍內的\\n\u3000敵人大幅度降低物理防禦力與魔法防禦力。\\n‧中幅度提升我方全體的物理攻擊力，\\n\u3000並特大幅度恢復自身的TP。\\n‧對前方數來第1名角色造成小幅度的物理傷害。\\n‧當仇鼠‧赤手空拳的啾三已被討伐時，\\n\u3000當戰鬥開始時或仇鼠‧怒髮衝冠的啾二\\n\u3000進行普通攻擊時，會燃起復仇之火，\\n\u3000同時攻擊將會變得更加激烈且凶暴。');
insert into unit_enemy_data values (315103, '仇鼠‧怒髮衝冠的啾二', 315100, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】老鼠家族的偉大老大。\\n在牧場吞下敗仗後，遁逃到了育幼院。\\n如今時機成熟，為了奪回故鄉而展開復仇。\\n\\n‧必殺技會對敵方全體造成小幅度的物理傷害，\\n\u3000並中幅度降低物理防禦力。\\n\u3000當復仇之火燃燒時，物理防禦力降低會消失的\\n\u3000同時，造成的傷害會提升。\\n‧以前方數來第1名的角色為中心，對範圍內的\\n\u3000敵人大幅度降低物理防禦力與魔法防禦力。\\n‧中幅度提升我方全體的物理攻擊力，\\n\u3000並特大幅度恢復自身的TP。\\n‧對前方數來第1名角色造成小幅度的物理傷害。\\n‧當仇鼠‧赤手空拳的啾三已被討伐時，\\n\u3000當戰鬥開始時或仇鼠‧怒髮衝冠的啾二\\n\u3000進行普通攻擊時，會燃起復仇之火，\\n\u3000同時攻擊將會變得更加激烈且凶暴。');
insert into unit_enemy_data values (315104, '仇鼠‧怒髮衝冠的啾二', 315104, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE1】老大的威名再次威震牧場。\\n老鼠小弟擠滿了會場，\\n為一家總動員的抗爭拉開了序幕。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自仇鼠‧怒髮衝冠的啾二所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧在戰鬥開始時，召喚3隻老鼠小弟，\\n\u3000並擊退敵方全體。\\n‧每打倒1隻老鼠小弟，\\n\u3000剩餘的老鼠小弟數量會減少1。\\n\u3000之後會再次召喚老鼠小弟，擊退敵方全體。\\n‧對前方數來第1名的角色造成\\n\u3000小幅度的物理傷害。');
insert into unit_enemy_data values (315105, '仇鼠‧怒髮衝冠的啾二', 315104, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE2】老鼠小弟接連被打倒，\\n重演過去的慘劇。為了捲土重來，\\n扭轉頹勢，老大親自拿起了武器。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自仇鼠‧怒髮衝冠的啾二所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技會對敵方全體造成小幅度的物理傷害，\\n\u3000並小幅度降低物理防禦力。\\n‧對前方數來第1名角色造成小幅度的物理傷害。');
insert into unit_enemy_data values (315106, '仇鼠‧怒髮衝冠的啾二', 315104, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE3】赤鼠的怒吼響徹會場，\\n說要守護家族的生活。被社會所排斥的悲哀魔物，\\n為了家族的存亡，拚命地揮動著拳頭。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自仇鼠‧怒髮衝冠的啾二所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技會對敵方全體造成小幅度的物理傷害，\\n\u3000並小幅度降低物理防禦力。\\n\u3000當復仇之火燃燒時，物理防禦力降低\\n\u3000會消失的同時，造成的傷害會提升。\\n‧以前方數來第1名的角色為中心，對範圍內\\n\u3000的敵人中幅度降低物理防禦力與魔法防禦力。\\n‧中幅度提升我方全體的物理攻擊力，\\n\u3000並特大幅度恢復自身的TP。\\n‧對前方數來第1名角色造成小幅度的物理傷害。\\n‧當仇鼠‧赤手空拳的啾三已被討伐時，\\n\u3000當戰鬥開始時或仇鼠‧怒髮衝冠的啾二\\n\u3000進行普通攻擊時，會燃起復仇之火，\\n\u3000同時攻擊將會變得更加激烈且凶暴。');
