insert into unit_enemy_data values (315201, '仇鼠‧赤手空拳的啾三', 315200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】老鼠家族的可靠義長子。\\n過去單靠一只拳頭就能展現其男子氣概的\\n勇猛鬥鼠，為了拯救家族的危機，\\n染指了閃耀之物並投身於抗爭之中。\\n\\n‧必殺技會以前方數來第1名的角色為中心，\\n\u3000對範圍內造成大幅度的物理傷害。\\n‧對前方數來第1名的角色\\n\u3000造成中幅度的物理傷害，\\n\u3000並且中幅度降低物理防禦力。\\n‧對前方數來第1名的角色\\n\u3000造成小幅度的物理傷害。');
insert into unit_enemy_data values (315202, '仇鼠‧赤手空拳的啾三', 315200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】老鼠家族的可靠義長子。\\n過去單靠一只拳頭就能展現其男子氣概的\\n勇猛鬥鼠，為了拯救家族的危機，\\n染指了閃耀之物並投身於抗爭之中。\\n\\n‧必殺技會以前方數來第1名的角色為中心，\\n\u3000對範圍內造成大幅度的物理傷害。\\n‧對前方數來第1名的角色\\n\u3000造成中幅度的物理傷害，\\n\u3000並且中幅度降低物理防禦力。\\n‧對前方數來第1名的角色\\n\u3000造成小幅度的物理傷害。');
insert into unit_enemy_data values (315203, '仇鼠‧赤手空拳的啾三', 315200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】老鼠家族的可靠義長子。\\n過去單靠一只拳頭就能展現其男子氣概的\\n勇猛鬥鼠，為了拯救家族的危機，\\n染指了閃耀之物並投身於抗爭之中。\\n\\n‧必殺技會以前方數來第1名的角色為中心，\\n\u3000對範圍內造成大幅度的物理傷害。\\n‧對前方數來第1名的角色\\n\u3000造成中幅度的物理傷害，\\n\u3000並且中幅度降低物理防禦力。\\n‧對前方數來第1名的角色\\n\u3000造成小幅度的物理傷害。');
insert into unit_enemy_data values (315204, '仇鼠‧赤手空拳的啾三', 315204, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '老鼠家族首屈一指的武鬥派。家族再次總動員，\\n對紛爭感到熱血沸騰，雙眼發出好戰的光芒。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自仇鼠‧赤手空拳的啾三所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧對前方數來第1名的角色造成\\n\u3000小幅度的物理傷害。');
insert into unit_enemy_data values (315205, '仇鼠‧赤手空拳的啾三', 315204, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '擅長狂暴劈砍，家族的特攻隊長。經常站在前線，\\n用牠的背影引導著年輕一輩。一刀入魂，\\n注入殊死的氣勢，一擊削掉對手的鎧甲。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自仇鼠‧赤手空拳的啾三所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技會以前方數來第1名的角色為中心，\\n\u3000對範圍內造成大幅度的物理傷害。\\n‧對前方數來第1名角色造成小幅度的物理傷害，\\n\u3000並中幅度降低物理防禦力。\\n‧在戰鬥中當仇鼠‧赤手空拳的啾三\\n\u3000自身的HP降至50％以下時，\\n\u3000會特大幅度降低自身的TP，\\n\u3000陷入暈眩，無法行動。\\n\u3000並且無法被視為攻擊對象。\\n‧戰鬥開始時，\\n\u3000當仇鼠‧赤手空拳的啾三自身的HP降至\\n\u300050％以下時，會陷入暈眩，無法行動。');
insert into unit_enemy_data values (315206, '仇鼠‧赤手空拳的啾三', 315206, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '既被害怕又被仰慕的一家大哥。為了家族，\\n即使凋零也在所不惜的熱血義長子，\\n用全神貫注的一刀開拓家族的未來。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自仇鼠‧赤手空拳的啾三所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技會以前方數來第1名的角色為中心，\\n\u3000對範圍內造成大幅度的物理傷害。\\n‧對前方數來第1名角色造成小幅度的物理傷害，\\n\u3000並且中幅度降低物理防禦力。\\n‧對前方數來第1名角色造成小幅度的物理傷害。');
insert into unit_enemy_data values (315300, '噬魂的兇蛇', 315300, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】藉由塔之魔力顯現的兇殘靈蛇。\\n以怨念滿盈之顎，啖噬冒險者之魂。\\n\\n‧噬魂凶蛇將會依照所保持著的【吸魂】數量，\\n\u3000進而使普通攻擊和各種技能等等的效果\\n\u3000受到強化。\\n‧每次的普通攻擊，都會追加6個【吸魂】。\\n‧必殺技會對敵方全體依照所保持著的【吸魂】\\n\u3000數量造成魔法比例傷害，\\n\u3000並且追加5個【吸魂】。\\n‧對敵方全體造成小幅度的魔法傷害再減少TP，\\n\u3000並且展開降低TP上升的領域。\\n\u3000此領域的TP減少量效果值，\\n\u3000將會依照噬魂凶蛇所保持著的【吸魂】\\n\u3000數量多寡而有所強化。\\n‧攻擊時噬魂凶蛇會追加1個【吸魂】，\\n\u3000召喚2隻分身，並且將敵方全體小幅度擊飛。\\n‧戰鬥開始時，將會中幅度提升自身的物理攻擊力\\n\u3000與魔法攻擊力，但每當受到傷害時，\\n\u3000將會減少噬魂凶蛇的1個【吸魂】。\\n‧當剩餘HP降至30%以下時，將會大幅度提升\\n\u3000魔法攻擊力，並且追加20個【吸魂】。');
