insert into unit_enemy_data values (315404, '貪婪暴君‧賭場王', 315404, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE1】無數發狂的遊戲機襲擊而來。\\n轉盤所揭示的賭博結局是生還是死……？\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自貪婪暴君‧賭場王\\n\u3000與角子機所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技會對前方數來第1名的角色\\n\u3000依照貪婪暴君‧賭場王的物理攻擊力\\n\u3000與其角色最大HP造成物理比例傷害。\\n‧戰鬥開始時，召喚3台角子機並擊退敵方全體。\\n\u3000每當經過一定的時間，將會繼續召喚角子機。\\n\u3000角子機會前方數來第1名的角色造成中幅度的\\n\u3000物理傷害。當角子機的HP低於一定數值時，\\n\u3000將會大幅度降低我方全體的\\n\u3000物理防禦力與魔法防禦力。\\n‧對從前方數來第1名的角色\\n\u3000造成小幅度的物理傷害。');
insert into unit_enemy_data values (315405, '貪婪暴君‧賭場王', 315405, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE2】傲慢的惡棍用「龍之卵」的力量\\n擊退少女們，他的臉上露出嘲笑的表情。\\n抱持不屈不撓的決心，突破光怪陸離的障壁。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧展開護盾，使其不會受到敵人造成的任何攻擊。\\n\u3000此護盾與傷害量無關，而是每當受到攻擊時，\\n\u3000便會漸漸的降低其耐久度。\\n\u3000當受到300次的攻擊後，\\n\u3000護盾將會被破壞。');
insert into unit_enemy_data values (315406, '貪婪暴君‧賭場王', 315406, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE3】賭場王解放祕寶真正的力量，\\n企圖以膨脹的私慾埋葬妨礙者，怪盜少女啊，\\n如今正是粉碎暴君野心之時！\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧中幅度降低來自貪婪暴君‧賭場王所有傷害\\n\u3000產生的隊伍角色TP恢復量。\\n‧必殺技會對前方數來第1名的角色依照\\n\u3000貪婪暴君‧賭場王的物理攻擊力與最大HP，\\n\u3000造成物理比例傷害。\\n‧展開護盾，極大幅度提升貪婪暴君‧賭場王\\n\u3000自身的物理/魔法防禦力。\\n‧當貪婪暴君‧賭場王自身的HP降至\\n\u300050%以下時，攻擊將變得更強力更激烈。\\n‧擲出骰子，提升貪婪暴君‧賭場王自身的\\n\u3000物理攻擊力。提升物理攻擊力的效果會根據\\n\u3000骰子的點數而產生變化。\\n\u3000點數為1時，小幅度提升物理攻擊力。\\n\u3000點數為2和3時，中幅度提升物理攻擊力。\\n\u3000點數為4和5時，大幅度提升物理攻擊力。\\n\u3000點數為6時，極大幅度提升物理攻擊力。\\n\u3000當貪婪暴君‧賭場王的HP在50％以下時，\\n\u3000會使用該技能。\\n‧對前方數來第1名的角色\\n\u3000造成大幅度的物理傷害。');
insert into unit_enemy_data values (315500, '石像鬼', 315500, 0, 16, 270, 3000, 1, 2.0, 0, 0, 0, '【物理】持續遵循逝去主人命令的砂岩雕像。\\n以愈發熾烈的攻擊將冒險者逼入絕境。\\n\\n‧必殺技會對敵方全體造成小幅度的物理傷害。\\n\u3000此技能所造成的傷害將不會使敵人恢復TP。\\n‧對最靠近的1名敵人造成小幅度的物理傷害。');
