insert into unit_enemy_data values (315902, '石像鬼‧魔幻', 315900, 0, 16, 270, 3000, 2, 2.0, 0, 0, 0, '【魔法】持續遵循逝去主人命令的魔礦石雕像。\\n以愈發熾烈的攻擊將冒險者逼入絕境。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧必殺技會對敵方全體造成中幅度的魔法傷害。\\n\u3000此技能所造成的傷害將不會使敵人恢復TP。\\n‧對最靠近自身的3名敵人\\n\u3000造成小幅度的魔法傷害，\\n\u3000並小幅度提升自身的魔法攻擊力。\\n‧中幅度降低敵方全體的魔法防禦力。\\n‧對最靠近的1名敵人造成中幅度的魔法傷害。');
insert into unit_enemy_data values (315903, '石像鬼‧魔幻', 315900, 0, 16, 270, 3000, 2, 2.0, 0, 0, 0, '【魔法】持續遵循逝去主人命令的魔礦石雕像。\\n以愈發熾烈的攻擊將冒險者逼入絕境。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧必殺技會對敵方全體造成大幅度的魔法傷害。\\n\u3000此技能所造成的傷害將不會使敵人恢復TP。\\n‧對最靠近自身的3名敵人\\n\u3000造成中幅度的魔法傷害，\\n\u3000並中幅度提升自身的魔法攻擊力。\\n‧大幅度降低敵方全體的魔法防禦力。\\n‧對最靠近的1名敵人造成大幅度的魔法傷害。\\n‧當石像鬼‧魔幻自身的HP低於50％時，\\n\u3000攻擊將會變得更加凶暴。');
insert into unit_enemy_data values (315904, '石像鬼‧魔幻', 315903, 0, 16, 270, 3000, 2, 2.0, 0, 0, 0, '【魔法】持續遵循逝去主人命令的魔礦石雕像。\\n以愈發熾烈的攻擊將冒險者逼入絕境。\\n\\n‧戰鬥開始時，\\n\u3000中幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧必殺技會對敵方全體造成特大幅度的魔法傷害。\\n\u3000此技能所造成的傷害將不會使敵人恢復TP。\\n‧對最靠近自身的3名敵人\\n\u3000造成大幅度的魔法傷害，\\n\u3000並中幅度提升自身的魔法攻擊力。\\n‧大幅度降低敵方全體的魔法防禦力。\\n‧對最靠近的1名敵人造成大幅度的魔法傷害。\\n‧當石像鬼‧魔幻自身的HP低於50％時，\\n\u3000攻擊將會變得更加凶暴。\\n');
