insert into unit_enemy_data values (316004, '受解放的寶藏守護者', 316004, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE1】面對入侵者，自動人偶\\n再次開始活動。為達成使命，超越機體耐久\\n極限發射出的光線，將眼前的阻礙完全焚燒殆盡。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧中幅度降低來自寶藏守護者所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技能對敵方全體依照其角色最大\\n\u3000HP造成物理比例傷害，\\n\u3000同時大幅度提升自身的物理攻擊力。\\n‧以前方的1名敵人為中心，\\n\u3000對小範圍內所有敵人\\n\u3000造成大幅度的物理傷害，\\n\u3000並大幅度降低其物理防禦力。\\n‧對距離最近的3名敵人\\n\u3000造成大幅度的物理傷害。\\n\u3000並且也對自身造成大幅度的物理傷害，\\n\u3000一定時間內極大幅度降低\\n\u3000物理防禦力與魔法防禦力，\\n\u3000並且停止運作。\\n‧對距離最近的1名敵人\\n\u3000造成大幅度的物理傷害。');
insert into unit_enemy_data values (316005, '受解放的寶藏守護者', 316005, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE2】呼應自動人偶的咆哮，頑強的\\n魔物們從陰暗的岩窟深處現身。詭異迴響著的堅硬\\n岩石嘎吱聲，使冒險者陷入恐懼。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧中幅度降低來自寶藏守護者、青藍魔像以及\\n\u3000守門者所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧以距離最近的1名敵人為中心，\\n\u3000對小範圍內所有敵人造成大幅度的物理傷害，\\n\u3000並大幅度降低其物理防禦力。\\n‧對距離最近的1名敵人造成大幅度的物理傷害。');
insert into unit_enemy_data values (316006, '受解放的寶藏守護者', 316004, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE3】一直守護著財寶的軀體，\\n解放了它最後的力量。\\n以必殺一擊打倒喪失限制器的傀儡吧！\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧中幅度降低來自寶藏守護者所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技能對敵方全體依照其角色最大\\n\u3000HP造成物理比例傷害，\\n\u3000同時大幅度提升自身的物理攻擊力。\\n‧以距離最近的1名敵人為中心，\\n\u3000對小範圍內所有敵人造成大幅度的物理傷害，\\n\u3000並大幅度降低其物理防禦力。\\n‧極大幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000寶藏守護者受到中毒、猛毒、詛咒、火傷、\\n\u3000咒術等效果時，自身的物理防禦力與魔法防禦力\\n\u3000提升的時間將會縮短，且從極大幅度提升\\n\u3000變更為小幅度提升。\\n‧當寶藏守護者自身的HP低於30％時，\\n\u3000大幅度提升自身的物理攻擊力，\\n\u3000攻擊變得更加兇暴。之後，\\n\u3000對敵方全體依照其角色最大\\n\u3000HP造成物理比例傷害，\\n\u3000並施加一定時間內無法使用必殺技的狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的1名敵人\\n\u3000造成大幅度的物理傷害。');
insert into unit_enemy_data values (316100, '雨靈', 316100, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】由塔之魔力匯聚形成的冷酷水靈。\\n其狂風暴雨殘忍地將冒險者吞沒。\\n\\n‧必殺技會對敵方全體依照其最大HP\\n\u3000造成魔法比例傷害，\\n\u3000並將其向後擊飛。\\n\u3000同時對前方範圍內的敵方全體\\n\u3000依照其最大HP造成魔法比例\\n\u3000追加傷害。\\n‧對前方範圍內的敵方全體造成中幅度的魔法傷害\\n\u3000同時小幅度擊飛。並且對距離最近的1名敵人\\n\u3000造成中幅度的魔法追加傷害。\\n‧對距離最遠的1名敵人造成中幅度的魔法傷害，\\n\u3000並且小幅度的將其拉近。\\n‧當雨靈自身的HP低於40％時，\\n\u3000特大幅度提升自身的魔法防禦力，\\n\u3000並且攻擊變得更加凶暴。');
