insert into unit_enemy_data values (316203, '阿斯特賴亞牛肝菌', 316200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】使用偽裝蘑菇引誘獵物的恐怖妖怪蕈菇。\\n食用了蘑菇的人會陷入幻覺，\\n在不知不覺間成為大自然的食物。\\n\\n‧必殺技會依照自身的物理攻擊力與敵人的最大HP，\\n\u3000對敵方全體造成物理比例傷害，\\n\u3000並使HP低於40％的角色敵人誘惑狀態。\\n\u3000此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧以自身為中心，使範圍內的敵人陷入誘惑狀態，\\n\u3000並大幅度提升物理攻擊力與魔法攻擊力，\\n\u3000直到戰鬥結束。\\n‧當自身的HP低於50％時，\\n\u3000攻擊將會變得更加凶暴。\\n‧對前方的第1名敵人造成大幅度的物理傷害。');
insert into unit_enemy_data values (316204, '阿斯特賴亞牛肝菌', 316204, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE1】帶領著數量驚人的僕從，\\n妖怪蕈菇逐漸加強了攻勢。成為捕食者的異類異形\\n們的反擊，現在揭開了序幕。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自手下所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧在戰鬥開始時，召喚4隻手下，\\n\u3000並擊退敵方全體。\\n\u3000手下會對前方數來第1名的角色\\n\u3000造成中幅度的物理傷害。\\n\u3000每打倒1隻手下，剩餘的手下數量會減少1個。\\n\u3000之後會再次召喚手下，擊退敵方全體。');
insert into unit_enemy_data values (316205, '阿斯特賴亞牛肝菌', 316205, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE2】威猛的大自然不僅僅使出幻覺，\\n僕從發動了強烈的麻痺攻擊。\\n其威力甚至使得獵物連根手指頭都難以動彈。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧中幅度降低來自阿斯特賴亞牛肝菌所有傷害\\n\u3000產生的隊伍角色TP恢復量。\\n‧必殺技會依照自身的物理攻擊力與敵人的\\n\u3000最大HP，對敵方全體造成物理比例傷害。\\n‧對前方的第1名敵人造成大幅度的物理傷害。\\n‧在手下收集孢子時，\\n\u3000阿斯特賴亞牛肝菌的物理防禦力與魔法防禦力\\n\u3000將會特大幅度降低。之後手下會釋放孢子，\\n\u3000使前方3名敵人陷入麻痺狀態。\\n\u3000在手下收集孢子時，\\n\u3000當阿斯特賴亞牛肝菌受到700000以上的傷害時，\\n\u3000會使阿斯特賴亞牛肝菌與手下暈眩，\\n\u3000物理防禦力與魔法防禦力將會特大幅度降低。');
insert into unit_enemy_data values (316206, '阿斯特賴亞牛肝菌', 316206, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE3】以彼此性命做為賭注的狩獵時間\\n接近尾聲。究竟是獵人，又或者是獵物呢。\\n對翠綠的戰場散播所剩的孢子。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧中幅度降低來自阿斯特賴亞牛肝菌所有傷害\\n\u3000產生的隊伍角色TP恢復量。\\n‧必殺技會依照自身的物理攻擊力與敵人的\\n\u3000最大HP，對敵方全體造成物理比例傷害，\\n\u3000並使HP低於40％的敵人陷入誘惑狀態。\\n\u3000此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧以自身為中心，使範圍內的敵人陷入誘惑狀態，\\n\u3000並大幅度提升物理攻擊力與魔法攻擊力，\\n\u3000直到戰鬥結束。\\n‧當自身的HP低於50%時，\\n\u3000攻擊將會變得更加凶暴。\\n‧對前方的第1名敵人造成大幅度的物理傷害。');
