insert into unit_enemy_data values (316301, '海將海馬指揮官', 316300, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】身強體壯、操持三叉戟的海獸。\\n因曾被人類捕捉，為洩憤而到處作亂，\\n讓海灘蒙上一層恐懼陰影。\\n\\n‧必殺技會對前方數來第3名角色\\n\u3000造成大幅度的魔法傷害，並以前方數來\\n\u3000第3名角色為中心，使小範圍的敵人\\n\u3000陷入暈眩。此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧大幅度提升自身的魔法攻擊力，\\n\u3000並且中幅度恢復TP。\\n‧對前方數來第3名角色造成\\n\u3000中幅度的物理傷害。');
insert into unit_enemy_data values (316302, '海將海馬指揮官', 316300, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】身強體壯、操持三叉戟的海獸。\\n因曾被人類捕捉，為洩憤而到處作亂，\\n讓海灘蒙上一層恐懼陰影。\\n\\n‧必殺技會對前方數來第3名角色\\n\u3000造成大幅度的魔法傷害，並以前方數來\\n\u3000第3名角色為中心，使小範圍的敵人\\n\u3000陷入暈眩。此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧大幅度提升自身的魔法攻擊力，\\n\u3000並且中幅度恢復TP。\\n‧對前方數來第3名角色造成\\n\u3000中幅度的物理傷害。');
insert into unit_enemy_data values (316303, '海將海馬指揮官', 316300, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】身強體壯、操持三叉戟的海獸。\\n因曾被人類捕捉，為洩憤而到處作亂，\\n讓海灘蒙上一層恐懼陰影。\\n\\n‧必殺技會對前方數來第3名角色\\n\u3000造成大幅度的魔法傷害，並以前方數來\\n\u3000第3名角色為中心，使小範圍的敵人\\n\u3000陷入暈眩。此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧大幅度提升自身的魔法攻擊力，\\n\u3000並且中幅度恢復TP。\\n‧對前方數來第3名角色造成\\n\u3000中幅度的物理傷害。\\n‧海將海馬指揮官的HP在50％以下時\\n\u3000攻擊會更趨凶暴，相對地，\\n\u3000物理防禦力會大幅度降低。');
insert into unit_enemy_data values (316304, '海將海馬指揮官', 316304, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE1】海將力大無窮，三叉戟一揮\\n便會立刻產生激流朝人們襲來。\\n鼓起勇氣，對抗猛烈的水流吧！\\n\\n‧戰鬥開始時，小幅度提升自身的\\n\u3000物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自海將海馬指揮官所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技會無視挑釁，對前方數來\\n\u3000第3名角色造成大幅度的魔法傷害，\\n\u3000並以前方數來第3名角色為中心，使小範圍的\\n\u3000敵人陷入暈眩。此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體造成中幅度的魔法傷害，\\n\u3000同時大幅度降低魔法防禦力，並且大幅度擊飛。\\n\u3000隨後大幅度恢復自身的TP。\\n‧無視挑釁，對前方數來第3名角色\\n\u3000造成中幅度的物理傷害。');
insert into unit_enemy_data values (316305, '海將海馬指揮官', 316305, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE2】海將的怒氣使海岸上蒸騰出海市蜃樓，\\n從中冒出少女們的幻影無情襲來。為了讓這個夏天\\n的美好回憶有始有終，努力突破這場試煉吧！\\n\\n‧戰鬥開始時，小幅度提升自身的\\n\u3000物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自海將海馬指揮官所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n\u3000召喚日和、怜、優衣的闇影。\\n‧對前方數來第3名角色\\n\u3000造成中幅度的物理傷害。');
insert into unit_enemy_data values (316306, '海將海馬指揮官', 316305, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE3】憤怒達到極限的海將，使出\\n渾身解數展開猛攻。為了找回和平，\\n將深藏心中的愛與友情化作力量來擊潰強敵吧！\\n\\n‧戰鬥開始時，小幅度提升自身的\\n\u3000物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自海將海馬指揮官所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技會對前方數來第3名角色\\n\u3000造成大幅度的魔法傷害，並以前方數來\\n\u3000第3名角色為中心，使小範圍的敵人\\n\u3000陷入暈眩。此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧大幅度提升自身的魔法攻擊力，\\n\u3000並且中幅度恢復TP。\\n‧對前方數來第3名角色\\n\u3000造成中幅度物理傷害。\\n‧海將海馬指揮官的HP低於50時\\n\u3000攻擊會更趨凶暴，相對地，\\n\u3000物理防禦力會大幅度降低。');
