insert into unit_enemy_data values (316401, '月光之門', 316400, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】隨著月亮的盈虧變化而出現，連接兩個\\n不同世界的門。引導小繆進入此門，\\n讓牠回到原本的世界吧！\\n\\n‧必殺技會對敵方全體依照\\n\u3000月光之門的【月之光印】數量\\n\u3000造成魔法比例傷害。\\n‧將敵方全體擊退，同時中幅度\\n\u3000降低行動速度，並且對敵方全體\\n\u3000依照月光之門的【月之光印】數量\\n\u3000造成魔法比例傷害。\\n\u3000隨後在月光之門上賦予10個\\n\u3000【月之光印】。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體依照月光之門的【月之光印】數量\\n\u3000造成小幅度魔法傷害，\\n\u3000並且以月光之門為中心，對範圍內的\\n\u3000敵方全體依照月光之門的【月之光印】數量\\n\u3000造成小幅度魔法傷害。\\n‧【月之光印】最多可賦予50個。');
insert into unit_enemy_data values (316402, '月光之門', 316400, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】隨著月亮的盈虧變化而出現，連接兩個\\n不同世界的門。引導小繆進入此門，\\n讓牠回到原本的世界吧！\\n\\n‧必殺技會對敵方全體依照\\n\u3000月光之門的【月之光印】數量\\n\u3000造成魔法比例傷害。\\n‧將敵方全體擊退，同時中幅度\\n\u3000降低行動速度，並且對敵方全體\\n\u3000依照月光之門的【月之光印】數量\\n\u3000造成魔法比例傷害。\\n\u3000隨後在月光之門上賦予10個\\n\u3000【月之光印】。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體依照月光之門的【月之光印】數量\\n\u3000造成小幅度魔法傷害，\\n\u3000並且以月光之門為中心，對範圍內的\\n\u3000敵方全體依照月光之門的【月之光印】數量\\n\u3000造成小幅度魔法傷害。\\n‧【月之光印】最多可賦予50個。');
insert into unit_enemy_data values (316403, '月光之門', 316400, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】隨著月亮的盈虧變化而出現，連接兩個\\n不同世界的門。引導小繆進入此門，\\n讓牠回到原本的世界吧！\\n\\n‧必殺技會對敵方全體依照\\n\u3000月光之門的【月之光印】數量\\n\u3000造成魔法比例傷害。\\n‧將敵方全體擊退，同時中幅度\\n\u3000降低行動速度，並且對敵方全體\\n\u3000依照月光之門的【月之光印】數量\\n\u3000造成魔法比例傷害。\\n\u3000隨後在月光之門上賦予10個\\n\u3000【月之光印】。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體依照月光之門的【月之光印】數量\\n\u3000造成小幅度魔法傷害，\\n\u3000並且以月光之門為中心，對範圍內的\\n\u3000敵方全體依照月光之門的【月之光印】數量\\n\u3000造成小幅度魔法傷害。\\n‧剩餘體力低於50％時，中幅度提升\\n\u3000月光之門的物理防禦力與魔法防禦力，\\n\u3000並且在月光之門上賦予20個\\n\u3000【月之光印】，改變行動模式。\\n‧【月之光印】最多可賦予50個。');
insert into unit_enemy_data values (316404, '月光之門', 316406, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE1】對一同度過一個夏天的小繆生起\\n惋惜之情的【小小甜心】的闇影。\\n會殘酷地擋住通往門的去路。\\n\\n‧戰鬥開始時，小幅度提升月光之門的\\n\u3000物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。');
insert into unit_enemy_data values (316405, '月光之門', 316405, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE2】被門中滲透出來的另一個世界的魔力\\n所強化的海中魔物。壓倒性的魔力洪流，\\n為少女們帶來一場更嚴酷的試煉。\\n\\n‧戰鬥開始時，小幅度提升月光之門的\\n\u3000物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果\\n\u3000而上升的防禦力，在戰鬥中\\n\u3000不會受到防禦力下降技能的影響。\\n‧中幅度降低來自\\n\u3000此戰鬥中之怪物所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧中幅度提升月光之門以外的我方的\\n\u3000物理攻擊力與魔法攻擊力。\\n‧月光之門受到傷害時，\\n\u3000會極大幅度降低月光之門以外的我方的\\n\u3000物理防禦力與魔法防禦力。');
insert into unit_enemy_data values (316406, '月光之門', 316404, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE3】連接兩個世界的最後一道門。\\n懷抱著無可取代的夏日回憶，將我們這位\\n小小朋友引導至輝煌的月光彼端吧！\\n\\n‧戰鬥開始時，小幅度提升月光之門的\\n\u3000物理防禦力與魔法防禦力。以此防禦力\\n\u3000提升效果而上升的防禦力，在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧承受來自月光之門所有的傷害，\\n\u3000導致中幅度降低隊伍角色的\\n\u3000TP恢復量。\\n‧必殺技會對敵方全體依照\\n\u3000月光之門的【月之光印】數量\\n\u3000造成魔法比例傷害。\\n‧將敵方全體擊退，同時中幅度\\n\u3000降低行動速度，並且對敵方全體\\n\u3000依照月光之門的【月之光印】數量\\n\u3000造成魔法比例傷害。\\n\u3000隨後在月光之門上賦予15個\\n\u3000【月之光印】。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體依照月光之門的【月之光印】數量\\n\u3000造成中幅度魔法傷害，\\n\u3000並且以月光之門為中心，對範圍內的\\n\u3000敵方全體依照月光之門的【月之光印】數量\\n\u3000造成大幅度魔法傷害。\\n‧剩餘體力低於40％時，中幅度提升\\n\u3000月光之門的物理防禦力與魔法防禦力，\\n\u3000並且在月光之門上賦予40個\\n\u3000【月之光印】，改變行動模式。\\n‧【月之光印】最多可賦予70個。');
