insert into unit_enemy_data values (316501, '哀執的植物羊', 316500, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】居於邊境的大樹，傳說中的植物羊。\\n期盼能與老人重逢，然而漫長的等待將其\\n逼入絕境，使之化為拒絕他人的魔獸。\\n\\n‧哀執的植物羊每秒追加3個【憂鬱之棘】。\\n‧每當哀執的植物羊受到傷害時，\\n\u3000哀執的植物羊的【憂鬱之棘】會減少1個。\\n‧必殺技會解除敵方全體的護盾，\\n\u3000並依照哀執的植物羊的【憂鬱之棘】數量\\n\u3000對敵方全體造成物理比例傷害。\\n‧對剩餘HP比例最少的3名敵方角色\\n\u3000依照哀執的植物羊的【憂鬱之棘】數量\\n\u3000造成物理比例傷害。 \\n‧對前方的3名角色\\n\u3000依照哀執的植物羊的【憂鬱之棘】數量\\n\u3000造成物理比例傷害。 ');
insert into unit_enemy_data values (316502, '哀執的植物羊', 316500, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】居於邊境的大樹，傳說中的植物羊。\\n期盼能與老人重逢，然而漫長的等待將其\\n逼入絕境，使之化為拒絕他人的魔獸。\\n\\n‧哀執的植物羊每秒追加3個【憂鬱之棘】。\\n‧每當哀執的植物羊受到傷害時，\\n\u3000哀執的植物羊的【憂鬱之棘】會減少1個。\\n‧必殺技會解除敵方全體的護盾，\\n\u3000並依照哀執的植物羊的【憂鬱之棘】數量\\n\u3000對敵方全體造成物理比例傷害。\\n‧對剩餘HP比例最少的3名敵方角色\\n\u3000依照哀執的植物羊的【憂鬱之棘】數量\\n\u3000造成物理比例傷害。 \\n‧對前方的3名角色\\n\u3000依照哀執的植物羊的【憂鬱之棘】數量\\n\u3000造成物理比例傷害。 ');
insert into unit_enemy_data values (316503, '哀執的植物羊', 316500, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】居於邊境的大樹，傳說中的植物羊。\\n期盼能與老人重逢，然而漫長的等待將其\\n逼入絕境，使之化為拒絕他人的魔獸。\\n\\n‧哀執的植物羊每秒追加4個【憂鬱之棘】。\\n‧每當哀執的植物羊受到傷害時，\\n\u3000哀執的植物羊的【憂鬱之棘】會減少1個。\\n‧必殺技會解除敵方全體的護盾，\\n\u3000並依照哀執的植物羊的【憂鬱之棘】數量\\n\u3000對敵方全體造成物理比例傷害。\\n‧對剩餘HP比例最少的3名敵方角色\\n\u3000依照哀執的植物羊的【憂鬱之棘】數量\\n\u3000造成物理比例傷害。 \\n‧對前方的3名角色\\n\u3000依照哀執的植物羊的【憂鬱之棘】數量\\n\u3000造成物理比例傷害。 \\n‧剩餘體力低於25％時，\\n\u3000賦予哀執的植物羊35個【憂鬱之棘】。');
insert into unit_enemy_data values (316504, '哀執的植物羊', 316504, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE1】那野獸躲在名為孤獨與猜疑的被毛之中，\\n像是要守護牠一般，森林中的魔物們\\n阻擋在冒險者面前。制服魔物開闢道路。\\n\\n‧戰鬥開始時，小幅度提升哀執的植物羊的\\n\u3000物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果\\n\u3000而上升的防禦力，在戰鬥中\\n\u3000不會受到防禦力下降技能的影響。\\n‧中幅度降低來自哀執的植物羊與\\n\u3000其怪物同伴所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧哀執的植物羊不會受到\\n\u3000環境效果所造成的傷害。\\n‧對剩餘HP比例最少的3名敵人\\n\u3000造成物理比例傷害。');
insert into unit_enemy_data values (316505, '哀執的植物羊', 316505, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE2】哀傷的羊隻使被毛的鎧甲硬化，\\n拒絕所有事物。無論是刀刃、拳擊、魔法\\n還是伸出的援手都一概拒絕。\\n\\n‧戰鬥開始時，中幅度提升哀執的植物羊的\\n\u3000物理防禦力，並小幅度提升魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自哀執的植物羊所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技能解除敵方全體的護盾，\\n\u3000造成物理比例傷害。哀執的植物羊受到\\n\u3000中毒、猛毒、詛咒、火傷、咒術等效果時，\\n\u3000將降低造成的傷害。\\n‧對剩餘HP比例最少的3名敵方角色造成\\n\u3000大幅度物理比例傷害。哀執的植物羊受到\\n\u3000中毒、猛毒、詛咒、火傷、咒術等效果時，\\n\u3000降低造成的傷害。\\n‧當哀執的植物羊受到中毒、猛毒、詛咒、火傷、\\n\u3000咒術等效果時，特大幅度降低\\n\u3000哀執的植物羊的物理與魔法防禦力。');
insert into unit_enemy_data values (316506, '哀執的植物羊', 316506, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE3】在和老旅行者的約定與怯懦之心之間\\n擺盪的矛盾，化為憂鬱之棘產生狂氣。\\n直面其狂暴威勢，將情感傳遞給該獸。\\n\\n‧戰鬥開始時，小幅度提升\\n\u3000哀執的植物羊的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧中幅度降低來自哀執的植物羊與\\n\u3000其怪物同伴所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧哀執的植物羊每秒\\n\u3000追加4個【憂鬱之棘】。\\n‧每當哀執的植物羊受到傷害時，\\n\u3000哀執的植物羊的【憂鬱之棘】\\n\u3000會減少1個。\\n‧必殺技會解除敵方全體的護盾，\\n\u3000並依照哀執的植物羊的【憂鬱之棘】數量\\n\u3000對敵方全體造成物理比例傷害。\\n‧對剩餘HP比例最少的3名敵人，\\n\u3000依照哀執的植物羊的【憂鬱之棘】數量\\n\u3000造成物理比例傷害。\\n‧對前方3名角色，依照哀執的植物羊的\\n\u3000【憂鬱之棘】數量造成物理比例傷害。\\n‧剩餘體力低於25％時，\\n\u3000賦予哀執的植物羊20個【憂鬱之棘】。');
insert into unit_enemy_data values (316600, '哥布林騎兵', 316600, 0, 6, 270, 1000, 1, 1.5, 0, 0, 0, '【物理】人龍一體戰鬥的哥布林騎手。\\n以敏捷速度馳騁空中，\\n襲擊地面上的敵人。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧特大幅度降低來自哥布林騎兵\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧必殺技會對除了召喚物、分身以外距離\\n\u3000最遠的3名敵人\\n\u3000造成特大幅度物理傷害，\\n\u3000使其陷入暈眩狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對除了召喚物、分身以外\\n\u3000距離最遠的2名敵人造\\n\u3000成小幅度的物理傷害，\\n\u3000大幅度降低其物理防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧普通攻擊可無視挑釁，\\n\u3000對距離最遠的1名敵人\\n\u3000造成中幅度的物理傷害。\\n\u3000此技能造成的傷害將不會恢復TP。');
insert into unit_enemy_data values (316601, '哥布林騎兵', 316601, 0, 6, 270, 1000, 1, 1.5, 0, 0, 0, '【物理】人龍一體戰鬥的哥布林騎手。\\n以敏捷速度馳騁空中，襲擊地面上的敵人。\\n\\n‧戰鬥開始時，中幅度提升自身的物理防禦力，\\n\u3000並小幅度提升自己的魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，展開小幅度降低敵方全體TP\\n\u3000上升的領域。\\n‧戰鬥開始時，對自身施加TP降低減少50％\\n\u3000效果。\\n‧特大幅度降低來自哥布林騎兵所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧大幅度降低來自敵方所有傷害產生的哥\\n\u3000布林騎兵TP恢復量。\\n‧必殺技會對除了召喚物、分身以外距離最\\n\u3000遠的3名敵人造成特大幅度物理傷害，使\\n\u3000其陷入暈眩狀態。此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對除了召喚物、分身以外距離最遠的2名敵人造\\n\u3000成小幅度的物理傷害，大幅度降低其物理防\\n\u3000禦力。此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧普通攻擊可無視挑釁，對距離最遠的1名敵人\\n\u3000造成中幅度的物理傷害。此技能造\\n\u3000成的傷害將不會恢復TP。');
insert into unit_enemy_data values (316602, '哥布林騎兵', 316602, 0, 6, 270, 1000, 1, 1.5, 0, 0, 0, '【物理】人龍一體戰鬥的哥布林騎手。\\n以敏捷速度馳騁空中，襲擊地面上的敵人。\\n\\n‧特大幅度降低來自哥布林騎兵所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會對除了召喚物、分身以外\\n\u3000遠距離最遠的3名敵人\\n\u3000造成大幅度物理傷害，使其陷入暈眩狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對除了召喚物、分身以外\\n\u3000距離最遠的2名敵人\\n\u3000造成小幅度的物理傷害，\\n\u3000大幅度降低其物理防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧普通攻擊可無視挑釁，\\n\u3000對距離最遠的1名敵人\\n\u3000造成中幅度的物理傷害。\\n\u3000此技能造成的傷害\\n\u3000將不會恢復TP。');
