insert into unit_enemy_data values (316701, '瘋狂直播主‧輝美', 316700, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】認為自己的存在就該是絕對，狂氣重疊\\n的直播主。受慾望支配，對懷有純粹情感的人們\\n刀刃相向。\\n\\n‧戰鬥開始時，賦予瘋狂直播主‧輝美5個【讚】。\\n‧必殺技能夠對面前的1名敵人造成特大幅度的\\n\u3000物理傷害。依照瘋狂直播主‧輝美的【讚】數\\n\u3000提升傷害量。同時賦予15個【讚】。\\n‧特大幅度降低瘋狂直播主‧輝美的物理防禦力\\n\u3000與魔法防禦力，並賦予瘋狂直播主‧輝美10個\\n\u3000【讚】。同時依照【讚】數提升自身的物理\\n\u3000攻擊力與TP。\\n‧對前方2名角色造成大幅度的物理傷害。並賦予\\n\u3000瘋狂直播主‧輝美5個【讚】。\\n‧當瘋狂直播主‧輝美受到中毒、猛毒、詛咒、\\n\u3000火傷、咒術等效果時，減少瘋狂直播主‧輝美\\n\u30005個【讚】。');
insert into unit_enemy_data values (316702, '瘋狂直播主‧輝美', 316700, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】認為自己的存在就該是絕對，狂氣重疊\\n的直播主。受慾望支配，對懷有純粹情感的人們\\n刀刃相向。\\n\\n‧戰鬥開始時，賦予瘋狂直播主‧輝美5個【讚】。\\n‧必殺技能夠對面前的1名敵人造成特大幅度的\\n\u3000物理傷害。依照瘋狂直播主‧輝美的【讚】數\\n\u3000提升傷害量。同時賦予15個【讚】。\\n‧特大幅度降低瘋狂直播主‧輝美的物理防禦力\\n\u3000與魔法防禦力，並賦予瘋狂直播主‧輝美10個\\n\u3000【讚】。同時依照【讚】數提升自身的物理\\n\u3000攻擊力與TP。\\n‧對前方2名角色造成大幅度的物理傷害。並賦予\\n\u3000瘋狂直播主‧輝美5個【讚】。\\n‧當瘋狂直播主‧輝美受到中毒、猛毒、詛咒、\\n\u3000火傷、咒術等效果時，減少瘋狂直播主‧輝美\\n\u30005個【讚】。');
insert into unit_enemy_data values (316703, '瘋狂直播主‧輝美', 316700, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】認為自己的存在就該是絕對，狂氣重疊\\n的直播主。受慾望支配，對懷有純粹情感的人們\\n刀刃相向。\\n\\n‧戰鬥開始時，賦予瘋狂直播主‧輝美5個【讚】。\\n‧必殺技能夠對面前的1名敵人造成特大幅度的\\n\u3000物理傷害。依照瘋狂直播主‧輝美的【讚】數\\n\u3000提升傷害量。同時賦予15個【讚】。\\n‧特大幅度降低瘋狂直播主‧輝美的物理防禦力\\n\u3000與魔法防禦力，並賦予瘋狂直播主‧輝美10個\\n\u3000【讚】。同時依照【讚】數提升自身的物理\\n\u3000攻擊力與TP。\\n‧對前方2名角色造成大幅度的物理傷害。並賦予\\n\u3000瘋狂直播主‧輝美5個【讚】。\\n‧當自身的HP低於50％時，大幅度提升瘋狂\\n\u3000直播主‧輝美的物理攻擊力與行動速度。\\n‧當瘋狂直播主‧輝美受到中毒、猛毒、詛咒、\\n\u3000火傷、咒術等效果時，減少瘋狂直播主‧輝美\\n\u30005個【讚】。');
insert into unit_enemy_data values (316704, '瘋狂直播主‧輝美', 316704, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE1】受慾望支配的敵手，在舞台上放火，\\n使表演越演越烈。宛如在諭示其前途，舞台隆隆\\n燃燒。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果上升的防禦\\n\u3000力，在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自瘋狂直播主‧輝美所有的傷害，導致\\n\u3000中幅度降低隊伍角色的TP恢復量。\\n‧戰鬥開始時，賦予瘋狂直播主‧輝美99個\\n\u3000【讚】。\\n‧必殺技能夠對面前的1名敵人造成特大幅度的\\n\u3000物理傷害。依照瘋狂直播主‧輝美的【讚】數\\n\u3000提升傷害量。\\n‧對前方2名角色造成大幅度的物理傷害。\\n‧戰鬥開始時，展開戰鬥場地。\\n\\n【戰鬥場地效果】\\n‧特大幅度降低瘋狂直播主‧輝美的物理防禦力\\n\u3000與魔法防禦力。\\n‧特大幅度降低所有隊伍角色的物理防禦力、\\n\u3000魔法防禦力與HP吸收，每秒造成小幅度的\\n\u3000物理傷害。');
insert into unit_enemy_data values (316705, '瘋狂直播主‧輝美', 316705, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE2】由於演出的殘酷，敵方連非人者\\n都納入夥伴。為了斥退逼近的威脅，使瘋狂的\\n舞台落幕──快，到直播主的身邊吧！\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果而上升的\\n\u3000防禦力，在戰鬥中不會受到防禦力下降技能\\n\u3000的影響。\\n‧承受來自瘋狂直播主‧輝美所有的傷害，導致\\n\u3000中幅度降低隊伍角色的TP恢復量。\\n‧瘋狂直播主‧輝美不會受到環境效果所造成的\\n\u3000傷害。\\n‧召喚智、鈴、七七香的闇影。\\n‧對前方2名角色造成大幅度的物理傷害。');
insert into unit_enemy_data values (316706, '瘋狂直播主‧輝美', 316705, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE3】舞台演出進入尾聲，受想留在台上\\n\u3000的欲望糾纏的魔物。貫徹己身信念，擊敗敵人吧。\\n為了守護人們的笑容──以及那份「喜歡」的心情。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果上升的防禦\\n\u3000力，在戰鬥中不會受到防禦力下降技能的影響。\\n\u3000‧承受來自瘋狂直播主‧輝美所有的傷害，導致\\n\u3000中幅度降低隊伍角色的TP恢復量。\\n‧戰鬥開始時，賦予瘋狂直播主‧輝美10個\\n\u3000【讚】。\\n‧必殺技能夠對面前的1名敵人造成特大幅度的\\n\u3000物理傷害。依照瘋狂直播主‧輝美的【讚】數\\n\u3000提升傷害量。同時賦予15個【讚】。\\n‧特大幅度降低瘋狂直播主‧輝美的物理防禦力\\n\u3000與魔法防禦力，並賦予瘋狂直播主‧輝美10個\\n\u3000【讚】。同時依照【讚】數提升自身的物理\\n\u3000攻擊力與TP。\\n‧對前方2名角色造成大幅度的物理傷害，並賦予\\n\u3000瘋狂直播主‧輝美5個【讚】。\\n‧當自身的HP低於50％時，大幅度提升瘋狂\\n\u3000直播主‧輝美的物理攻擊力與行動速度。\\n‧當瘋狂直播主‧輝美受到中毒、猛毒、詛咒、\\n\u3000火傷、咒術等效果時，減少瘋狂直播主‧輝美\\n\u30005個【讚】。');
insert into unit_enemy_data values (316800, '黑之王賈巴沃克', 316800, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】因彌勒再次從狹界中顯現的黑之王。\\n企圖用暗黑的瘴氣蹂躪世界。唯有希望的力量\\n能一阻黑之王。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果\\n\u3000而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧必殺技會對敵方全體造成大幅度物理傷害，\\n\u3000施加中毒狀態。\\n‧對前方的3名角色造成中幅度的物理傷害，\\n\u3000大幅度降低物理防禦力與TP。\\n\u3000此技能的效果即使傷害無效也會施加。\\n‧對敵方全體造成中幅度物理傷害，\\n\u3000大幅度降低物理防禦力。\\n\u3000且角色HP未達50％時，中幅度降低行動速度。\\n\u3000此技能的效果即使傷害無效也會施加。\\n‧當黑之王賈巴沃克自身的HP低於40％時，\\n\u3000牠的攻擊將會變得更加兇暴。\\n‧當各目標被破壞時，將各自大幅度降低物理與\\n\u3000魔法防禦力。');
insert into unit_enemy_data values (316900, '失控的純潔之弓', 316900, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】接收彌勒的弩級之力，再次失控的拉菲的\\n聖弓。若被其箭矢貫穿，將會化為無力的天使，\\n生命受扭曲的詛咒侵蝕。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果而上升的防禦\\n\u3000力，在戰鬥中不會受到防禦力下降技能的影響。\\n‧必殺技會對敵方全體造成大幅度魔法傷害，\\n\u3000大幅度降低魔法防禦力。\\n\u3000對陷入天使狀態的角色施加詛咒狀態。\\n\u3000此技能的效果，即使傷害無效也會施加。\\n‧對敵方全體造成中幅度魔法傷害，對HP未達\\n\u300070%的角色施加一定時間的天使狀態。\\n\u3000陷入天使狀態的角色，會變成只能使用普通\\n\u3000攻擊，並解除身上的能力提升效果。\\n\u3000恢復HP會解除天使狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對前方3名角色造成中幅度魔法傷害，\\n\u3000並使其暈眩。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧當失控的純潔之弓自身的HP低於40%時，\\n\u3000牠的攻擊將會變得更加兇暴。');
insert into unit_enemy_data values (317000, '幻影男爵', 317000, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】因不祥的詛咒束縛受幽冥招引的亡親\\n之靈。原應昇天的靈魂因彌勒的法術化為龐大的\\n魔力，對礙事者將毫不留情痛下毒手。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，在戰鬥中\\n\u3000不會受到防禦力下降技能的影響。\\n‧必殺技會對距離最近的1名敵方角色造成大幅度\\n\u3000魔法傷害，且大幅度降低魔法防禦力。\\n\u3000此技能的效果，即使傷害無效也會施加。\\n‧對距離最遠的1名敵方角色造成中幅度物理\\n\u3000傷害，並將其拉至最前方名角色的位置，同時\\n\u3000造成暈眩。\\n\u3000此技能所造成的狀態異常，即使傷害無效也會\\n\u3000施加。\\n‧對距離最近的1名敵方角色造成中幅度魔法\\n\u3000傷害。\\n\u3000對前方3名角色造成中幅度魔法傷害並擊退。\\n\u3000此技能所造成的狀態異常，即使傷害無效也會\\n\u3000施加。\\n‧當幻影男爵自身的HP低於40%時，牠的攻擊\\n\u3000將會變得更加兇暴。');
insert into unit_enemy_data values (317100, '天塔帕斯', 317100, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】被暴食少女當成生猛食材的憤怒軟體\\n魔物。刻在身上的齒痕象徵著魔獸的決心。懷著\\n彌勒的力量，為一雪前恥而暴怒發狂。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，在戰鬥中\\n\u3000不會受到防禦力下降技能的影響。\\n‧必殺技會對敵方全體依照其最大HP造成物理\\n\u3000比例傷害。\\n‧對敵方全體依照其最大HP造成物理比例傷害，\\n\u3000大幅度降低物理防禦力，使其麻痺。\\n\u3000此技能的效果或所造成的狀態異常，即使傷害\\n\u3000無效也會施加。\\n‧對面前1名敵方角色造成大幅度物理傷害。\\n‧對面前1名敵方角色依照其最大HP造成物理\\n\u3000比例傷害。\\n‧當天塔帕斯自身的HP低於40%時，牠的攻擊\\n\u3000將會變得更加兇暴。');
insert into unit_enemy_data values (317200, '豪紳', 317200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】獲得「拯救者」授予力量，搖身一變的前\\n貴族議會議長。運使黑暗力量，用鐵腕發動挾帶\\n憎恨的單體攻擊，從面前敵人開始依序粉碎敵陣。\\n‧戰鬥開始時，小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果而上升的防禦\\n\u3000力，在戰鬥中不會受到防禦力下降技能的影響。\\n‧必殺技會對面前1名敵方角色造成極大幅度物理\\n\u3000傷害。\\n‧對面前1名敵方角色造成大幅度物理傷害，\\n\u3000並擊退。\\n\u3000此技能所造成的狀態異常，即使傷害無效也會\\n\u3000施加。\\n‧大幅度提升自身的物理攻擊力。\\n‧對面前1名敵方角色造成大幅度物理傷害，\\n\u3000中幅度降低物理防禦力。\\n\u3000此技能的效果，即使傷害無效也會施加。\\n‧當豪紳自身的HP低於50%時，牠的攻擊將會\\n\u3000變得更加兇暴。');
insert into unit_enemy_data values (317300, '阿爾瑪', 317300, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】自遠古起守護著迷宮的防衛兵器。因彌勒\\n的黑暗力量變得凶惡的守護之力，將化為粉碎一切\\n的災厄，攔阻在世界前方。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果而上升的防禦\\n\u3000力，在戰鬥中不會受到防禦力下降技能的影響。\\n‧必殺技會對敵方全體造成大幅度物理傷害。\\n‧對敵方全體造成中幅度魔法傷害，大幅度降低\\n\u3000物理防禦力與魔法防禦力。\\n\u3000此技能的效果，即使傷害無效也會施加。\\n‧對面前1名敵方角色造成大幅度物理傷害。\\n‧當阿爾瑪自身的HP低於40％時，\\n\u3000牠的攻擊將會變得更加兇暴。');
insert into unit_enemy_data values (317400, '奧拉姆', 317400, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】誕生於遠古起守護著迷宮的防衛兵器。\\n古代兵器的詛咒力量因彌勒的魔能獲得解放，其\\n帶來的災禍洶湧不止，毫不留情地襲向人們。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果而上升的防禦\\n\u3000力，在戰鬥中不會受到防禦力下降技能的影響。\\n‧必殺技會對敵方全體造成大幅度魔法傷害。\\n‧對敵方全體造成中幅度物理傷害，中幅度降低\\n\u3000物理攻擊力與魔法攻擊力。\\n\u3000此技能的效果，即使傷害無效也會施加。\\n‧對敵方全體造成中幅度魔法傷害，\\n\u3000造成短時間暈眩。\\n\u3000HP未達40％時，造成長時間暈眩。\\n\u3000此技能的效果，即使傷害無效也會施加。\\n‧當奧拉姆自身的HP低於70％時，\\n\u3000牠的攻擊將會變得更加兇暴。');
insert into unit_enemy_data values (317500, '好朋友X', 317500, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】鋼鐵的機關巨人再度來襲，古怪藝術與\\n災厄的結合之作。企圖以無情的詛咒再度破壞學徒\\n們的自我。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果而上升的防禦\\n\u3000力，在戰鬥中不會受到防禦力下降技能的影響。\\n‧必殺技會對敵方全體造成大幅度魔法傷害，\\n\u3000施加火傷狀態。\\n\u3000處於中毒狀態時火傷傷害會增加。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧中幅度提升自身的物理攻擊力後，對敵方全體\\n\u3000造成中幅度物理傷害。\\n\u3000且解除敵方全體的護盾。\\n‧對敵方全體造成中幅度魔法傷害。\\n\u3000處於中毒狀態時特大幅度降低行動速度。\\n\u3000此技能的效果，即使傷害無效也會施加。\\n‧當好朋友X自身的HP低於40%時，\\n\u3000牠的攻擊將會變得更加兇暴。');
insert into unit_enemy_data values (317600, '無名的藝術', 317600, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】對人類而言過於前衛的奇異藝術再度\\n降臨，毒舌機器人與災厄的結合之作。作者的才華\\n如今會成為毀滅世界的爪牙嗎……？\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果而上升的防禦\\n\u3000力，在戰鬥中不會受到防禦力下降技能的影響。\\n‧必殺技會對敵方全體造成大幅度魔法傷害，\\n\u3000施加中毒狀態。\\n‧對敵方全體造成大幅度物理傷害。\\n\u3000處於中毒狀態時再造成額外傷害。\\n‧當無名的藝術自身的HP低於40％時，\\n\u3000特大幅度提升我方全體的TP，\\n\u3000且牠的攻擊將會變得更加兇暴。');
insert into unit_enemy_data values (317700, '彌勒', 317700, 0, 16, 270, 3000, 2, 1.6, 317700, 317700, 0, '【MODE1】幽禁人們的靈魂，引導世界邁向\\n末日的虛偽救濟者。以強力的魔法與散播狀態異常\\n的特殊法術，消滅違抗者的生命。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧必殺技會對敵方全體依照其剩餘HP造成魔法\\n\u3000比例傷害。\\n\u3000且展開每秒小幅度降低敵方全體的TP，每秒\\n\u3000小幅度提升彌勒的TP的領域。\\n‧對敵方全體造成中幅度的魔法傷害，\\n\u3000並擊退。\\n\u3000此技能所造成的狀態異常，即使傷害無效也會\\n\u3000施加。\\n‧對敵方全體造成中幅度魔法傷害，施加黑焰\\n\u3000狀態。\\n\u3000處於黑焰狀態的角色每秒受到傷害。黑焰造成的\\n\u3000傷害每秒提升10％。\\n\u3000此技能所造成的狀態異常，即使傷害無效也會\\n\u3000施加。');
