insert into unit_enemy_data values (317800, '彌勒', 317800, 0, 16, 270, 3000, 2, 1.6, 317700, 317700, 0, '【MODE2】趁著為世界帶來末日的火勢，\\n救濟者將發動秘技。透過魔力無效化，\\n時時刻刻削弱攻勢，擊潰人們之間的羈絆。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，大幅度降低敵方全體的HP吸收、\\n\u3000TP上升、物理攻擊力、魔法攻擊力、\\n\u3000物理防禦力、魔法防禦力直到戰鬥結束\\n‧必殺技會對敵方全體依照其剩餘HP\\n\u3000造成魔法比例傷害。\\n‧對敵方全體造成中幅度的魔法傷害，\\n\u3000並擊退。此技能所造成的\\n\u3000狀態異常，即使傷害無效也會施加。');
insert into unit_enemy_data values (317900, '彌勒', 317900, 0, 16, 270, 3000, 2, 1.6, 317700, 317700, 0, '【MODE3】解放真正的力量，魔力失控的\\n彌勒。魔法威力更加激烈，殲滅所有阻礙\\n與神邂逅的敵人。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧必殺技會對敵方全體造成剩餘HP99％\\n\u3000的魔法比例傷害。\\n‧對敵方全體造成大幅度魔法傷害，\\n\u3000中幅度降低魔法防禦力，\\n\u3000解除其護盾。\\n‧大幅度提升自身的魔法攻擊力與TP。\\n‧當彌勒自身的HP低於1600萬時，改變\\n\u3000行動模式，攻擊變得更加兇暴。');
insert into unit_enemy_data values (318000, '黑曜魔像', 318000, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】為撕裂和平而安排的堅硬魔物。\\n其漆黑發亮的身體不會被任何人粉碎，\\n擁有壓倒性的重量與力量。\\n\\n‧必殺技會減少敵方全體的最大HP15％，\\n\u3000造成小幅度物理傷害。\\n\u3000減少的HP直到戰鬥結束後才會解除。\\n\u3000此技能造成的傷害不會恢復TP，此技能\\n\u3000所造成的狀態異常，即使傷害無效也會施加。\\n‧小幅度恢復自身的TP。\\n‧對敵方全體造成中幅度物理傷害，\\n\u3000小幅度降低物理防禦力直到戰鬥結束。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對前方數來第1名角色造成中幅度物理傷害。');
insert into unit_enemy_data values (318001, '黑曜魔像', 318000, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】為撕裂和平而安排的堅硬魔物。\\n其漆黑發亮的身體不會被任何人粉碎，\\n擁有壓倒性的重量與力量。\\n\\n‧必殺技會減少敵方全體的最大HP15％，\\n\u3000造成小幅度物理傷害。\\n\u3000減少的HP直到戰鬥結束後才會解除。\\n\u3000此技能造成的傷害不會恢復TP，此技能\\n\u3000所造成的狀態異常，即使傷害無效也會施加。\\n‧小幅度恢復自身的TP。\\n‧對敵方全體造成中幅度物理傷害，\\n\u3000小幅度降低物理防禦力直到戰鬥結束。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對前方數來第1名角色造成中幅度物理傷害。');
insert into unit_enemy_data values (318002, '黑曜魔像', 318000, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】為撕裂和平而安排的堅硬魔物。\\n其漆黑發亮的身體不會被任何人粉碎，\\n擁有壓倒性的重量與力量。\\n\\n‧必殺技會減少敵方全體的最大HP\\n\u300015％，造成小幅度物理傷害。\\n\u3000減少的HP直到戰鬥結束後才會解除。\\n\u3000此技能造成的傷害不會恢復TP，\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧中幅度恢復自身的TP。\\n‧對敵方全體造成中幅度物理傷害，\\n\u3000小幅度降低物理防禦力直到戰鬥結束。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對前方數來第1名角色造成大幅度物理傷害。');
insert into unit_enemy_data values (318003, '黑曜魔像', 318000, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】為撕裂和平而安排的堅硬魔物。\\n其漆黑發亮的身體不會被任何人粉碎，擁有\\n壓倒性的重量與力量。\\n\\n‧必殺技會減少敵方全體的最大HP15％，造成\\n\u3000小幅度物理傷害。減少的HP直到戰鬥結束後\\n\u3000才會解除。此技能造成的傷害不會恢復TP，\\n\u3000此技能所造成的狀態異常，即使傷害無效也會\\n\u3000施加。\\n‧中幅度恢復自身的TP。\\n‧對敵方全體造成中幅度物理傷害，小幅度降低\\n\u3000物理防禦力直到戰鬥結束。此技能所造成的狀態\\n\u3000異常，即使傷害無效也會施加。\\n‧對前方數來第1名角色造成大幅度物理傷害。\\n‧當剩餘體力低於40％時中幅度提升自身的\\n\u3000物理攻擊力，攻擊變得更加兇暴。');
insert into unit_enemy_data values (318004, '黑曜魔像', 318004, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE1】第一測驗是岩石魔物與虛假的自身。\\n恐懼周圍目光的幼虎已不復存在。\\n跨越陰影，成為英雄吧。\\n\\n‧戰鬥開始時，中幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果而上升的\\n\u3000防禦力，在戰鬥中不會受到防禦力下降技能的\\n\u3000影響。\\n‧黑曜魔像不會受到環境效果所造成的傷害。\\n‧對前方數來第1名角色造成中幅度的物理傷害。\\n');
insert into unit_enemy_data values (318005, '黑曜魔像', 318004, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE2】第二測驗是任何兵器都能阻擋的\\n最強鎧甲。然而，正攻法不代表一切。為了成就\\n大義，使盡渾身解數擊潰敵人吧。\\n\\n‧戰鬥開始時，大幅度提升自身的物理防禦力，\\n\u3000中幅度提升魔法防禦力。以此防禦力提升效果\\n\u3000而上升的防禦力，在戰鬥中不會受到防禦力下降\\n\u3000技能的影響。\\n‧中幅度降低來自黑曜魔像所有傷害產生的隊伍\\n\u3000角色TP恢復量。\\n‧必殺技會減少敵方全體的最大HP15％，造成\\n\u3000小幅度物理傷害。減少的HP直到戰鬥結束\\n\u3000才會解除。此技能造成的傷害不會恢復TP，\\n\u3000此技能所造成的狀態異常，即使傷害無效也會\\n\u3000施加。\\n‧對敵方全體造成中幅度物理傷害，小幅度降低\\n\u3000物理防禦力直到戰鬥結束。此技能所造成的\\n\u3000狀態異常，即使傷害無效也會施加。\\n‧對前方數來第1名的角色造成中幅度物理傷害。\\n‧在戰鬥中，黑曜魔像自身被附加的狀態異常效果\\n\u3000及能力下降效果數量越多，能對其造成的傷害量\\n\u3000就會上升越多。');
insert into unit_enemy_data values (318006, '黑曜魔像', 318004, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE3】第三測驗是落幕之時。被逼入絕境\\n的魔物會死命反撲，試圖壓毀萬物。\\n和平的使者啊，用你的雙手開創未來。\\n\\n‧戰鬥開始時，中幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果而上升的\\n\u3000防禦力，在戰鬥中不會受到防禦力下降技能的\\n\u3000影響。\u3000\\n‧大幅度降低來自黑曜魔像所有傷害產生的隊伍\\n\u3000角色TP恢復量。\\n‧必殺技會減少敵方全體的最大HP15％，造成\\n\u3000小幅度物理傷害。減少的HP直到戰鬥結束才會\\n\u3000解除。此技能造成的傷害不會恢復TP，此技能\\n\u3000所造成的狀態異常，即使傷害無效也會施加。\\n‧中幅度恢復自身的TP。\\n‧對敵方全體造成中幅度物理傷害，小幅度降低\\n\u3000物理防禦力直到戰鬥結束。此技能所造成的\\n\u3000狀態異常，即使傷害無效也會施加。\\n‧對從前方數來第1名的角色造成大幅度的物理\\n\u3000傷害。\\n‧當剩餘體力低於11％時中幅度提升自身的\\n\u3000物理攻擊力，攻擊變得更加兇暴。');
