insert into unit_enemy_data values (318101, '汙穢的湯之神‧陽卵', 318100, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】由於「神醉」的力量而放開拘束的溫泉之神。\\n釋放了其身積累的罪惡與汙穢，\\n使尾牙會場陷入巨大的混亂漩渦之中。\\n\\n‧必殺技會對敵方全體造成特大幅度的魔法傷害，\\n\u3000並施加詛咒狀態。\\n\u3000對已處於詛咒狀態的敵方改施加咒術狀態。\\n\u3000咒術所產生的持續傷害，\\n\u3000將會隨著每秒進而增加。\\n‧對敵方全體造成小幅度的魔法傷害，\\n\u3000並施加詛咒狀態。\\n‧對靠近自身的3名敵方角色\\n\u3000造成中幅度物理傷害，\\n\u3000當敵人的HP未滿30％時，造成混亂狀態。\\n‧對靠近自身的3名敵方角色\\n\u3000造成中幅度物理傷害。\\n‧汙穢的湯之神・陽卵所造成的詛咒或咒術傷害，\\n\u3000將不會恢復TP。');
insert into unit_enemy_data values (318102, '汙穢的湯之神‧陽卵', 318100, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】由於「神醉」的力量而放開拘束的溫泉之神。\\n釋放了其身積累的罪惡與汙穢，\\n使尾牙會場陷入巨大的混亂漩渦之中。\\n\\n‧必殺技會對敵方全體造成特大幅度的魔法傷害，\\n\u3000並施加詛咒狀態。\\n\u3000對已處於詛咒狀態的敵方改施加咒術狀態。\\n\u3000咒術所產生的持續傷害，\\n\u3000將會隨著每秒進而增加。\\n‧對敵方全體造成小幅度的魔法傷害，\\n\u3000並施加詛咒狀態。\\n‧對靠近自身的3名敵方角色\\n\u3000造成中幅度物理傷害，\\n\u3000當敵人的HP未滿30％時，造成混亂狀態。\\n‧對靠近自身的3名敵方角色\\n\u3000造成中幅度物理傷害。\\n‧汙穢的湯之神・陽卵所造成的詛咒或咒術傷害，\\n\u3000將不會恢復TP。');
insert into unit_enemy_data values (318103, '汙穢的湯之神‧陽卵', 318100, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】由於「神醉」的力量而放開拘束的溫泉之神。\\n釋放了其身積累的罪惡與汙穢，\\n使尾牙會場陷入巨大的混亂漩渦之中。\\n\\n‧必殺技會對敵方全體造成特大幅度的魔法傷害，\\n\u3000並施加詛咒狀態。\\n\u3000對已處於詛咒狀態的敵方改施加咒術狀態。\\n\u3000咒術所產生的持續傷害，\\n\u3000將會隨著每秒進而增加。\\n‧對敵方全體造成小幅度的魔法傷害，\\n\u3000並施加詛咒狀態。\\n‧對靠近自身的3名敵方角色\\n\u3000造成中幅度物理傷害，\\n\u3000當敵人的HP未滿30％時，造成混亂狀態。\\n‧對靠近自身的3名敵方角色\\n\u3000造成中幅度物理傷害。\\n‧當汙穢的湯之神‧陽卵\\n\u3000自身的HP低於50％時，\\n\u3000祂的攻擊將會變得更加兇暴。\\n‧汙穢的湯之神‧陽卵所造成的詛咒或咒術傷害，\\n\u3000將不會恢復TP。');
insert into unit_enemy_data values (318104, '汙穢的湯之神‧陽卵', 318104, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE1】沉溺於享樂的神驅，罪惡與汙穢\\n沸騰湧出。滿溢出來的負面瘴氣不只為凡人\\n帶來災禍，也侵蝕著神明自身。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧中幅度降低來自汙穢的湯之神‧陽卵\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧必殺技會對敵方全體造成特大幅度的\\n\u3000魔法傷害，並施加詛咒狀態。\\n\u3000對已處於詛咒狀態\\n\u3000的敵方改施加咒術狀態。\\n\u3000咒術所產生的持續傷害，\\n\u3000將會隨著每秒進而增加。\\n‧對敵方全體造成大幅度的魔法傷害，\\n\u3000並且使其陷入詛咒狀態，\\n\u3000同時大幅度降低其魔法防禦力。\\n\u3000之後，使自身陷入詛咒狀態。\\n\u3000此技能所造成的狀態異常與效果，\\n\u3000即使傷害無效也會施加。\\n‧對靠近自身的3名敵人\\n\u3000造成大幅度的物理傷害。\\n‧汙穢的湯之神‧陽卵所造成的\\n\u3000詛咒及咒術傷害，會使角色無法恢復TP。');
insert into unit_enemy_data values (318105, '汙穢的湯之神‧陽卵', 318106, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE2】名勝溫泉的化身，即使在紛亂之中\\n也恣意泡湯舒展。連神明都為之著迷的極致溫泉，\\n是否能成為眾人平息騷亂的轉機？\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自汙穢的湯之神‧陽卵所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧對敵方全體造成小幅度的物理傷害，\\n\u3000同時造成擊退，\\n\u3000並且大幅度降低固定範圍內\\n\u3000敵人的所有行動速度，然後開始泡湯。\\n\u3000當汙穢的湯之神‧陽卵泡湯時，\\n\u3000極大幅度降低自身的物理防禦力與魔法防禦力。\\n\u3000此技能所造成的狀態異常與效果，\\n\u3000即使傷害無效也會施加。\\n‧對靠近自身的3名敵方角色\\n\u3000造成小幅度物理傷害。');
insert into unit_enemy_data values (318106, '汙穢的湯之神‧陽卵', 318106, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE3】宴會到達了最高潮，新年第一道曙光也\\n即將升起。陽卵旅館上下一同，全心全意為客人服\\n務。掃去罪惡與汙穢，迎向清新的嶄新一年。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自汙穢的湯之神‧陽卵所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技會對敵方全體依照其最大HP\\n\u3000造成魔法比例傷害，並施加詛咒狀態。\\n\u3000對已處於詛咒狀態的敵方改施加咒術狀態。\\n\u3000咒術所產生的持續傷害，\\n\u3000將會隨著每秒進而增加。\\n‧對敵方全體造成小幅度的魔法傷害，\\n\u3000並施加詛咒狀態。\\n‧對靠近自身的3名敵方角色\\n\u3000造成中幅度物理傷害，\\n\u3000當敵人的HP未滿30％時，造成混亂狀態。\\n‧對靠近自身的3名敵方角色\\n\u3000造成大幅度物理傷害。\\n‧當汙穢的湯之神‧陽卵\\n\u3000自身的HP低於50％時，\\n\u3000祂的攻擊將會變得更加兇暴。\\n‧汙穢的湯之神‧陽卵所造成的詛咒或咒術傷害，\\n\u3000將不會恢復TP。');
