insert into unit_enemy_data values (318201, '不幸福的多倫多獸', 318200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】因魔法失控而獲得生命的聖誕\\n樹。受到負面情緒的驅使，在平安夜裡大鬧\\n城鎮，散播不幸。\\n\\n‧必殺技能對敵方全體依照其角色最大\\n\u3000HP造成物理比例傷害，造成短\\n\u3000時間暈眩。\\n\u3000對HP低於50％的敵人造成長時間暈眩。\\n\u3000長時間暈眩即使傷害無效也會\\n\u3000施加。\\n‧展開大幅度提升敵方全體的物理攻擊力、魔法\\n\u3000攻擊力，中幅度降低行動速度的領域。\\n‧對敵方全體造成中幅度的物理傷害。此外，以從\\n\u3000後方數來第2名的角色為中心，對小範圍內\\n\u3000賦予持續恢復HP的狀態。之後，中幅度\\n\u3000降低敵方全體的TP。\\n‧當剩餘時間不到30秒時，中幅度提升\\n\u3000不幸福的多倫多獸的行動速度，並改變\\n\u3000行動模式。\\n‧展開中幅度降低敵方全體的物理攻擊力、\\n\u3000魔法攻擊力的領域。\\n‧對敵方全體造成中幅度的物理傷害，並且中幅度\\n\u3000降低TP。\\n‧對敵方全體依照其角色最大HP造成物理\\n\u3000比例傷害。');
insert into unit_enemy_data values (318202, '不幸福的多倫多獸', 318200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】因魔法失控而獲得生命的聖誕\\n樹。受到負面情緒的驅使，在平安夜裡大鬧\\n城鎮，散播不幸。\\n\\n‧必殺技能對敵方全體依照其角色最大\\n\u3000HP造成物理比例傷害，造成短\\n\u3000時間暈眩。\\n\u3000對HP低於50％的敵人造成長時間暈眩。\\n\u3000長時間暈眩即使傷害無效也會\\n\u3000施加。\\n‧展開大幅度提升敵方全體的物理攻擊力、魔法\\n\u3000攻擊力，中幅度降低行動速度的領域。\\n‧對敵方全體造成中幅度的物理傷害。此外，以從\\n\u3000後方數來第2名的角色為中心，對小範圍內\\n\u3000賦予持續恢復HP的狀態。之後，中幅度\\n\u3000降低敵方全體的TP。\\n‧當剩餘時間不到30秒時，中幅度提升\\n\u3000不幸福的多倫多獸的行動速度，並改變\\n\u3000行動模式。\\n‧展開中幅度降低敵方全體的物理攻擊力、\\n\u3000魔法攻擊力的領域。\\n‧對敵方全體造成中幅度的物理傷害，並且中幅度\\n\u3000降低TP。\\n‧對敵方全體依照其角色最大HP造成物理\\n\u3000比例傷害。');
insert into unit_enemy_data values (318203, '不幸福的多倫多獸', 318200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】因魔法失控而獲得生命的聖誕\\n樹。受到負面情緒的驅使，在平安夜裡大鬧\\n城鎮，散播不幸。\\n\\n‧必殺技能對敵方全體依照其角色最大\\n\u3000HP造成物理比例傷害，造成短\\n\u3000時間暈眩。\\n\u3000對HP低於50％的敵人造成長時間暈眩。\\n\u3000長時間暈眩即使傷害無效也會\\n\u3000施加。\\n‧展開大幅度提升敵方全體的物理攻擊力、魔法\\n\u3000攻擊力，中幅度降低行動速度的領域。\\n‧對敵方全體造成中幅度的物理傷害。此外，以從\\n\u3000後方數來第2名的角色為中心，對小範圍內\\n\u3000賦予持續恢復HP的狀態。之後，中幅度\\n\u3000降低敵方全體的TP。\\n‧當剩餘時間不到45秒時，中幅度提升\\n\u3000不幸福的多倫多獸的行動速度，並改變\\n\u3000行動模式。\\n‧展開中幅度降低敵方全體的物理攻擊力、\\n\u3000魔法攻擊力的領域。\\n‧對敵方全體造成中幅度的物理傷害，並且中幅度\\n\u3000降低TP。\\n‧對敵方全體依照其角色最大HP造成物理\\n\u3000比例傷害。');
insert into unit_enemy_data values (318204, '不幸福的多倫多獸', 318204, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE1】身上籠罩著悲憤的氣場，伴隨著\\n不幸，如雪崩般召喚魔物。為了守護人們的\\n幸福與願望而戰吧，雙子天使！\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧中幅度降低來自不幸福的多倫多獸與其怪物同伴\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧不幸福的多倫多獸不會受到\\n\u3000環境效果所造成的傷害。\\n‧召喚魔物，擊退敵方全體。\\n‧每打倒1隻被召喚出來的魔物，\\n\u3000剩餘的目標數量就會減少1個。\\n\u3000之後再次召喚魔物出來，\\n\u3000擊退敵方全體。\\n‧對敵方全體依照最大HP\\n\u3000造成物理比例傷害。');
insert into unit_enemy_data values (318205, '不幸福的多倫多獸', 318205, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE2】透過扭曲的祝福，將人們的心拖入\\n地獄，可怕的大樹魔物。\\n其象徵平安夜的樣貌已不存在。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧中幅度降低來自不幸福的多倫多獸\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧必殺技能對敵方全體依照其角色\\n\u3000最大HP造成物理比例傷害。\\n\u3000而當HP在65％以上時，\\n\u3000使其陷入沉默狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧以從後方數來第2名的角色為中心，\\n\u3000對範圍內的敵人造成依照最大HP\\n\u3000造成物理比例傷害，\\n\u3000大幅度提升行動速度，\\n\u3000同時中幅度降低其TP。\\n‧對敵方全體依照其角色\\n\u3000最大HP造成物理比例傷害。\\n\u3000而當HP在85％以上時，\\n\u3000中幅度降低其TP。');
insert into unit_enemy_data values (318206, '不幸福的多倫多獸', 318204, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE3】因姊妹不合而產生的怪物，阻擋在\\n祈禱平安夜幸福的人們面前。現在正是找回\\n姊妹的情誼的時候，超級無敵幸福聖誕節！\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧中幅度降低來自不幸福的多倫多獸\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧必殺技能對敵方全體依照其角色\\n\u3000最大HP造成物理比例傷害，\\n\u3000造成短時間暈眩。\\n\u3000對HP低於50％的敵人\\n\u3000造成長時間暈眩。\\n\u3000長時間暈眩即使傷害無效也會施加。\\n‧展開大幅度提升敵方全體的\\n\u3000物理攻擊力、魔法攻擊力，\\n\u3000中幅度降低行動速度的領域。\\n‧對敵方全體造成中幅度的物理傷害。\\n\u3000此外，以從後方數來第2名的角色為中心，\\n\u3000對小範圍內賦予持續恢復HP的狀態。\\n\u3000之後，中幅度降低敵方全體的TP。\\n‧當剩餘時間不到45秒時，中幅度提升\\n\u3000不幸福的多倫多獸的行動速度，\\n\u3000並改變行動模式。\\n‧展開中幅度降低敵方全體的\\n\u3000物理攻擊力、魔法攻擊力的領域。\\n‧對敵方全體造成中幅度的物理傷害，\\n\u3000並且中幅度\\n\u3000降低TP。\\n‧對敵方全體依照其角色\\n\u3000最大HP造成物理比例傷害。');
