insert into unit_enemy_data values (318301, '祈福託災的招財貓', 318300, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】執著於自身使命，因而成為詛咒之物的\\n招財貓。其過於執著的願望，\\n帶來的不僅是幸福，亦招來了災厄。\\n\\n‧必殺技會以距離最近的1名敵人為中心，\\n\u3000造成小範圍的大幅度魔法傷害，\\n\u3000並使其陷入暈眩狀態，\\n\u3000同時中幅度降低TP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體造成中幅度的魔法傷害。\\n\u3000無視挑釁，\\n\u3000將距離最近的1名敵人拉至自己面前，\\n\u3000並小幅度拉近距離第2近之後的敵人。\\n‧以距離最近的1名敵人為中心，\\n\u3000造成小範圍的小幅度魔法傷害。\\n‧當剩餘時間低於50秒時，以自己為中心，\\n\u3000展開中幅度降低範圍內敵人物理攻擊力\\n\u3000與魔法攻擊力的領域。');
insert into unit_enemy_data values (318302, '祈福託災的招財貓', 318300, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】執著於自身使命，因而成為詛咒之物的\\n招財貓。其過於執著的願望，\\n帶來的不僅是幸福，亦招來了災厄。\\n\\n‧必殺技會以距離最近的1名敵人為中心，\\n\u3000造成小範圍的大幅度魔法傷害，\\n\u3000並使其陷入暈眩狀態，\\n\u3000同時中幅度降低TP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體造成中幅度的魔法傷害。\\n\u3000無視挑釁，\\n\u3000將距離最近的1名敵人拉至自己面前，\\n\u3000並小幅度拉近距離第2近之後的敵人。\\n‧以距離最近的1名敵人為中心，\\n\u3000造成小範圍的小幅度魔法傷害。\\n‧當剩餘時間低於50秒時，以自己為中心，\\n\u3000展開中幅度降低範圍內敵人物理攻擊力\\n\u3000與魔法攻擊力的領域。');
insert into unit_enemy_data values (318303, '祈福託災的招財貓', 318300, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】執著於自身使命，因而成為詛咒之物的\\n招財貓。其過於執著的願望，\\n帶來的不僅是幸福，亦招來了災厄。\\n\\n‧必殺技會以距離最近的1名敵人為中心，\\n\u3000造成小範圍的大幅度魔法傷害，\\n\u3000並使其陷入暈眩狀態，同時中幅度降低TP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體造成中幅度的魔法傷害。\\n\u3000無視挑釁，將距離最近的1名敵人\\n\u3000拉至自己面前，\\n\u3000並小幅度拉近距離第2近之後的敵人。\\n‧以距離最近的1名敵人為中心，\\n\u3000造成小範圍的小幅度魔法傷害。\\n‧當剩餘時間低於50秒時，\\n\u3000以自己為中心，\\n\u3000展開特大幅度降低範圍內敵人物理攻擊力\\n\u3000與魔法攻擊力的領域。');
insert into unit_enemy_data values (318304, '祈福託災的招財貓', 318304, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE1】詛咒之物的失控無停歇之勢，\\n並帶領眾多僕從進一步強化攻勢，\\n白與黑的僕從們拒絕並擊退所有敵人。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自祈福託災的招財貓\\n\u3000與其怪物同伴所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧以距離最近的1名敵人為中心，\\n\u3000造成小範圍的小幅度魔法傷害。\\n‧召喚同伴，擊退敵方全體。\\n‧每打倒1隻被召喚出來的同伴，剩餘的\\n\u3000目標數量就會減少1個。之後再次召喚\\n\u3000同伴出來，將擊退敵方全體。\\n‧白色招財貓的物理防禦力高，\\n\u3000會對距離最近的1名敵人\\n\u3000造成小幅度的物理傷害。\\n‧黑色招財貓的魔法防禦力高，\\n\u3000會對距離最遠的1名敵人\\n\u3000造成小幅度的物理傷害。');
insert into unit_enemy_data values (318305, '祈福託災的招財貓', 318305, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE2】招財貓的嘆息深藏積年累月的\\n悲傷，使眼前的一切陷入沉默之中，\\n甚至阻擋了那些試圖拯救自己的聲音。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自祈福託災的招財貓所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技會對敵方全體造成中幅度的魔法傷害，\\n\u3000並施加沉默狀態。此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n\u3000對敵方全體造成中幅度的魔法傷害，\\n\u3000並特大幅度提升自身的魔法防禦力。\\n‧以距離最近的1名敵人為中心，\\n\u3000造成小範圍的小幅度魔法傷害。\\n‧對敵方全體造成中幅度的物理傷害。');
insert into unit_enemy_data values (318306, '祈福託災的招財貓', 318304, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE3】招財貓真正的力量是吸引一切事物\\n並試圖終結它們。解放覆滿悲嘆的詛咒，\\n奪回笑容聚集的咖啡廳吧！\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧中幅度降低來自祈福託災的招財貓\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧必殺技會以距離最近的1名敵人為中心，\\n\u3000造成小範圍的大幅度魔法傷害，\\n\u3000並使其陷入暈眩狀態，\\n\u3000同時中幅度降低TP。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體造成中幅度的魔法傷害。\\n\u3000無視挑釁，\\n\u3000將距離最近的1名敵人拉至自己面前，\\n\u3000並小幅度拉近距離第2近之後的敵人。\\n‧以距離最近的1名敵人為中心，\\n\u3000造成小範圍的小幅度魔法傷害。\\n‧當剩餘時間低於50秒時，以自己為中心，\\n\u3000展開特大幅度降低範圍內敵人\\n\u3000物理攻擊力、魔法攻擊力\\n\u3000與大幅度降低行動速度的領域。');
insert into unit_enemy_data values (318400, '波賽頓‧海神之劍', 318400, 0, 0, 600, 3000, 1, 1.6, 0, 0, 0, '【物理】在深邃的海底等待挑戰者的\\n威武的海之化身。其揮舞的大劍一閃，\\n便能使阻擋於前者化為海中的藻屑。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自波賽頓所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技會對距離最近的1名敵方角色造成\\n\u3000特大幅度的物理傷害，同時中幅度降低\\n\u3000敵方全體的物理攻擊力與魔法攻擊力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的1名敵人\\n\u3000造成中幅度的物理傷害，\\n\u3000同時中幅度降低物理防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的1名敵方角色造成\\n\u3000特大幅度的物理傷害。');
insert into unit_enemy_data values (318401, '波賽頓‧大洋之杖', 318401, 0, 0, 600, 3000, 2, 1.6, 0, 0, 0, '【魔法】在深邃的海底等待挑戰者的\\n威武的海之化身。該法杖所施展的魔法能引起\\n暴風雨，對破壞恩賜之海的無禮之人降下天譴。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自波賽頓所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技會對距離最近的1名敵人\\n\u3000造成中幅度的魔法傷害。\\n\u3000之後，對敵方全體造成大幅度的魔法傷害，\\n\u3000並使其短時間內陷入暈眩狀態。\\n\u3000當角色的HP低於80％時，\\n\u3000使其長時間陷入暈眩狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體造成中幅度的魔法傷害\\n\u3000當角色的HP低於80％時，中幅度降低TP。\\n\u3000此技能所造成的傷害，不會使敵人恢復TP。\\n‧對距離最遠的1名敵人造成大幅度的魔法傷害。');
insert into unit_enemy_data values (318402, '波賽頓‧嵐擊之槍', 318402, 0, 0, 600, 3000, 1, 1.6, 0, 0, 0, '【魔法】在深邃的海底等待挑戰者的\\n威武的海之化身。其槍之亂擊能引起狂暴的\\n漩渦，將萬物拖入水底。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自波賽頓所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技會對前方數來第3名敵人為中心，\\n\u3000中幅度降低小範圍內最多3名敵人的\\n\u3000物理防禦力與魔法防禦力。\\n\u3000之後造成特大幅度的物理傷害。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n\u3000此技能所造成的傷害，不會使敵人恢復TP。\\n‧對距離最近的1名敵人\\n\u3000造成特大幅度的物理傷害，\\n\u3000同時造成擊退。此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧以前方數來第3名敵人為中心，\\n\u3000造成小範圍的特大幅度魔法傷害。\\n‧對距離最近的1名敵方角色造成\\n\u3000特大幅度的物理傷害。\\n‧當剩餘時間不到45秒時，攻擊將會變得\\n\u3000更加激烈且凶暴，同時防禦力將會有所提升，\\n\u3000變得更加頑強。\\n‧當左側的目標被破壞時，\\n\u3000將中幅度降低自身的物理防禦力與魔法防禦力。\\n‧當中央的目標被破壞時，\\n\u3000中幅度降低自身的物理防禦力與魔法防禦力。\\n‧當右側的目標被破壞時，\\n\u3000中幅度降低自身的物理防禦力與魔法防禦力。');
