insert into unit_enemy_data values (318501, '樂園守護者', 318500, 0, 16, 270, 3000, 2, 1.4, 0, 0, 0, '【魔法】當蘭德索爾逐漸淪陷回憶囚禁之中，\\n突然出現的異常魔物。以兩把冰冷堅硬的長槍\\n排除解放者們，守護著樂園。\\n\\n‧必殺技會對敵方全體造成大幅度的魔法傷害，\\n\u3000同時中幅度降低其行動速度，\\n\u3000小幅度降低其魔法防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧小幅度提升自身的魔法攻擊力，並大幅度提升\\n\u3000行動速度。\\n‧對前方數來第1名的角色造成中幅度的魔法傷害。\\n‧當左側的目標被破壞時，\\n\u3000將會中幅度降低物理/魔法防禦力。\\n‧當中央下方的目標被破壞時，\\n\u3000將會大幅度降低物理/魔法防禦力。\\n‧當中央上方的目標被破壞時，\\n\u3000將會大幅度降低物理/魔法防禦力。\\n‧當右側的目標被破壞時，\\n\u3000將會特大幅度降低物理/魔法防禦力。');
insert into unit_enemy_data values (318502, '樂園守護者', 318500, 0, 16, 270, 3000, 2, 1.4, 0, 0, 0, '【魔法】當蘭德索爾逐漸淪陷回憶囚禁之中，\\n突然出現的異常魔物。以兩把冰冷堅硬的長槍\\n排除解放者們，守護著樂園。\\n\\n‧必殺技會對敵方全體造成大幅度的魔法傷害，\\n\u3000同時中幅度降低其行動速度，\\n\u3000小幅度降低其魔法防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧小幅度提升自身的魔法攻擊力，並大幅度提升\\n\u3000行動速度。\\n‧對前方數來第1名的角色造成中幅度的魔法傷害。\\n‧當左側的目標被破壞時，\\n\u3000將會中幅度降低物理/魔法防禦力。\\n‧當中央下方的目標被破壞時，\\n\u3000將會大幅度降低物理/魔法防禦力。\\n‧當中央上方的目標被破壞時，\\n\u3000將會大幅度降低物理/魔法防禦力。\\n‧當右側的目標被破壞時，\\n\u3000將會特大幅度降低物理/魔法防禦力。');
insert into unit_enemy_data values (318503, '樂園守護者', 318500, 0, 16, 270, 3000, 2, 1.4, 0, 0, 0, '【魔法】當蘭德索爾逐漸淪陷回憶囚禁之中，\\n突然出現的異常魔物。以兩把冰冷堅硬的長槍\\n排除解放者們，守護著樂園。\\n\\n‧必殺技會對敵方全體造成大幅度的魔法傷害，\\n\u3000同時中幅度降低其行動速度，\\n\u3000小幅度降低其魔法防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧小幅度提升自身的魔法攻擊力，並大幅度提升\\n\u3000行動速度。\\n‧對前方數來第1名的角色造成中幅度的魔法傷害。\\n‧當剩餘時間不到25秒時，小幅度提升\\n\u3000自身的魔法攻擊力，並大幅度恢復自身的TP。\\n‧當左側的目標被破壞時，\\n\u3000將會中幅度降低物理/魔法防禦力。\\n‧當中央下方的目標被破壞時，\\n\u3000將會大幅度降低物理/魔法防禦力。\\n‧當中央上方的目標被破壞時，\\n\u3000將會大幅度降低物理/魔法防禦力。\\n‧當右側的目標被破壞時，\\n\u3000將會特大幅度降低物理/魔法防禦力。');
insert into unit_enemy_data values (318504, '樂園守護者', 318504, 0, 16, 270, 3000, 2, 1.4, 0, 0, 0, '【MODE1】疊起數層牢固的障壁阻擋來者去向\\n死命保護自己的模樣，就好像是在索求扭曲的樂園\\n一般，令人隱約感到空虛與縹緲。\\n\\n‧使此戰鬥中全部目標的破壞無效。\\n‧展開護盾，使其不會受到敵人造成的任何攻擊。\\n\u3000護盾受到攻擊時會漸漸降低耐久，\\n\u3000受到一定數量時護盾\\n\u3000就會被破壞。\\n‧戰鬥開始時，中幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧必殺技會對敵方全體\\n\u3000造成大幅度的魔法傷害，\\n\u3000同時中幅度降低其行動速度，\\n\u3000小幅度降低其魔法防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧小幅度提升自身的魔法攻擊力，\\n\u3000並大幅度提升行動速度。\\n‧對前方數來第1名的角色\\n\u3000造成中幅度的魔法傷害。\\n\u3000此技能造成的傷害會使其TP恢復量下降。');
insert into unit_enemy_data values (318505, '樂園守護者', 318505, 0, 16, 270, 3000, 2, 1.4, 0, 0, 0, '【MODE2】某段回憶浮現出兩名重要人物的\\n虛幻身影。少女自我激勵，\\n不讓無可替代的寶物再受到玷汙。\\n\\n‧使此戰鬥中全部目標的破壞無效。\\n‧戰鬥開始時，中幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧大幅度降低來自樂園守護者\\n\u3000與其怪物同伴\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧小幅度提升自身的魔法攻擊力，\\n\u3000並大幅度提升行動速度。\\n‧對前方數來第1名的角色\\n\u3000造成中幅度的魔法傷害。');
insert into unit_enemy_data values (318506, '樂園守護者', 318505, 0, 16, 270, 3000, 2, 0.8, 0, 0, 0, '【MODE3】黑暗混濁的詭異魔物，垂死之際\\n仍想做最後反抗。運用堅毅的心念與覺醒的力量，\\n開拓通往未來的道路吧。\\n\\n‧戰鬥開始時，中幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\u3000\\n‧大幅度降低來自樂園守護者\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧必殺技會對敵方全體造成大幅度的魔法傷害，\\n\u3000同時中幅度降低其行動速度，\\n\u3000小幅度降低其魔法防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧中幅度提升自身的魔法攻擊力，\\n\u3000並大幅度提升行動速度。\\n‧對前方數來第1名的角色\\n\u3000造成中幅度的魔法傷害。\\n‧剩餘體力低於25％時，小幅度提升\\n\u3000自身的魔法攻擊力，\\n\u3000並大幅度恢復自身的TP。\\n‧當左側的目標被破壞時，\\n\u3000將會中幅度降低物理/魔法防禦力。\\n‧當中央下方的目標被破壞時，\\n\u3000將會大幅度降低物理/魔法防禦力。\\n‧當中央上方的目標被破壞時，\\n\u3000將會大幅度降低物理/魔法防禦力。\\n‧當右側的目標被破壞時，\\n\u3000將會特大幅度降低物理/魔法防禦力。');
