insert into unit_enemy_data values (318601, '象徵久遠庭園之人', 318600, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】樂園的創造主，其打造的樂園充滿了\\n永恆的幸福。屏棄未來，改變自己的容貌，\\n阻擋著期望明日之人的去路。\\n\\n‧必殺技會解除敵方全體的護盾，\\n\u3000對敵方全體依照自身魔法攻擊力\\n\u3000與角色最大HP造成魔法比例傷害。\\n\u3000當角色的HP低於50％時，造成束縛狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧解除敵方全體的能力提升效果後，\\n\u3000對敵方全體造成小幅度的魔法傷害，\\n\u3000並施加沉默狀態。\\n\u3000並且中幅度降低自身的\\n\u3000物理防禦力與魔法防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的1名敵方角色造成\\n\u3000小幅度魔法傷害。');
insert into unit_enemy_data values (318602, '象徵久遠庭園之人', 318600, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】樂園的創造主，其打造的樂園充滿了\\n永恆的幸福。屏棄未來，改變自己的容貌，\\n阻擋著期望明日之人的去路。\\n\\n‧必殺技會解除敵方全體的護盾，\\n\u3000對敵方全體依照自身魔法攻擊力\\n\u3000與角色最大HP造成魔法比例傷害。\\n\u3000當角色的HP低於50％時，造成束縛狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧解除敵方全體的能力提升效果後，\\n\u3000對敵方全體造成小幅度的魔法傷害，\\n\u3000並施加沉默狀態。\\n\u3000並且中幅度降低自身的\\n\u3000物理防禦力與魔法防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的1名敵方角色造成\\n\u3000小幅度魔法傷害。');
insert into unit_enemy_data values (318603, '象徵久遠庭園之人', 318600, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】樂園的創造主，其打造的樂園充滿了\\n永恆的幸福。屏棄未來，改變自己的容貌，\\n阻擋著期望明日之人的去路。\\n\\n‧必殺技會解除敵方全體的護盾，\\n\u3000對敵方全體依照自身魔法攻擊力\\n\u3000與角色最大HP造成魔法比例傷害。\\n\u3000當角色的HP低於50％時，造成束縛狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧解除敵方全體的能力提升效果後，\\n\u3000對敵方全體造成小幅度的魔法傷害，\\n\u3000並施加沉默狀態。\\n\u3000並且中幅度降低自身的\\n\u3000物理防禦力與魔法防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的1名敵方角色\\n\u3000造成大幅度魔法傷害。\\n‧當自身的HP低於50％時，\\n\u3000改變行動模式，攻擊變得更加兇暴。');
insert into unit_enemy_data values (318604, '象徵久遠庭園之人', 318604, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE1】不願面對明日的創造主，\\n想以囚錮自身的鎖鏈將解放者們監禁在樂園。\\n用想邁向未來堅定決心，斬斷錯綜的鎖鏈吧。\\n\\n‧戰鬥開始時，小幅度提升自身的物理\\n\u3000防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧必殺技會解除敵方全體的護盾，\\n\u3000對敵方全體依照自身魔法攻擊力\\n\u3000與角色最大HP造成魔法比例傷害。\\n\u3000當角色的HP低於50％時，造成束縛狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n\u3000此技能所造成的傷害，\\n\u3000會中幅度降低隊伍角色的TP恢復量。\\n‧大幅度降低自身的物理防禦力與魔法防禦力，\\n\u3000並展開鎖鏈，經過一段時間後，\\n\u3000對敵方全體依照其最大HP造成\\n\u3000魔法比例傷害，並施加束縛狀態。\\n\u3000展開鎖鏈時，若受到一定的傷害，\\n\u3000此技能將會被迫中斷，\\n\u3000同時中幅度降低自身的物理防禦力與\\n\u3000魔法防禦力直到戰鬥結束。\\n\u3000中斷此技能所需的傷害量，\\n\u3000會隨此技能的使用次數增加。\\n‧對距離最近的1名敵方角色造成\\n\u3000小幅度魔法傷害。\\n\u3000此技能造成的傷害\\n\u3000會中幅度減少TP恢復量。');
insert into unit_enemy_data values (318605, '象徵久遠庭園之人', 318605, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE2】沉浸在甜美回憶裡的幸福，以及\\n對於未來的不安讓解放者們在樂園看見幻象。將對\\n未來的期待轉化為自身的力量，粉碎虛假的自己。\\n\\n‧戰鬥開始時，小幅度提升自身的物理\\n\u3000防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧承受來自象徵久遠庭園之人所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧召喚望、矛依未與嘉夜的闇影。\\n‧對距離最近的1名敵方角色造成\\n\u3000小幅度魔法傷害。');
insert into unit_enemy_data values (318606, '象徵久遠庭園之人', 318605, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE3】就連珍視之人的話語也傳達不到內心，\\n創造主準備對拒絕自己的人解放終結之力。\\n和夥伴與搭檔一起奪回耀眼的未來吧。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的\\n\u3000防禦力，在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧承受來自象徵久遠庭園之人所有的傷害，\\n\u3000導致中幅度降低隊伍角色的TP恢復量。\\n‧必殺技會解除敵方全體的護盾，\\n\u3000對敵方全體依照自身魔法攻擊力\\n\u3000與角色最大HP造成魔法比例傷害。\\n\u3000當角色的HP低於50％時，造成束縛狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧解除敵方全體的能力提升效果後，\\n\u3000對敵方全體造成中幅度的魔法傷害，\\n\u3000並施加沉默狀態，\\n\u3000特大幅度降低敵方全體的HP吸收。\\n\u3000並且中幅度降低自身的物理防禦力與\\n\u3000魔法防禦力。\\n\u3000此技能所造成的狀態異常與效果，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的1名敵方角色\\n\u3000造成大幅度魔法傷害。\\n‧當自身的HP低於50％時，\\n\u3000改變行動模式，攻擊變得更加兇暴。');
insert into unit_enemy_data values (318700, '富豪魔像', 318700, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】誕生自次元的狹縫中，活生生的財寶。\\n襲擊冒險者的同時，也會撒落輝煌寶物。\\n\\n‧必殺技會對敵方全體造成大幅度的物理傷害，\\n\u3000並大幅度降低物理攻擊力和魔法攻擊力。\\n\u3000之後，特大幅度降低自身的物理防禦力與\\n\u3000魔法防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體造成中幅度的物理傷害，\\n\u3000並使其暈眩。\\n\u3000之後，特大幅度降低自身的物理防禦力與\\n\u3000魔法防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對前方的3名角色造成中幅度的物理傷害，\\n\u3000同時造成擊退。之後，特大幅度降低自身的\\n\u3000物理防禦力與魔法防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對面前1名角色造成中幅度的物理傷害，\\n\u3000之後，特大幅度降低自身的物理防禦力與\\n\u3000魔法防禦力。');
