insert into unit_enemy_data values (318803, '大王猩猩', 318800, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】在森林深處守護生物們，心地善良的\\n猿類魔物。然而卻因牙痛而失去自我，\\n搖身一變成為傷害森林的流氓。\\n\\n‧必殺技會對距離最近的1名敵人造成\\n\u3000特大幅度的物理傷害，並使其暈眩。同時\\n\u3000對敵方全體造成大幅度的物理傷害。之後，\\n\u3000小幅度降低自身的物理防禦力與魔法防禦力。\\n‧以距離最近的1名敵人為中心，\\n\u3000大幅度降低小範圍內的物理防禦力，\\n\u3000並造成特大幅度的物理傷害。同時使其暈眩。\\n\u3000此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。之後，\\n\u3000小幅度降低自身的物理防禦力與魔法防禦力。\\n‧中幅度恢復自身的TP，並大幅度提升行動速度。\\n\u3000之後，小幅度降低自身的物理防禦力與\\n\u3000魔法防禦力。\\n‧當自身的HP低於50%時，\\n\u3000攻擊將會變得更加凶暴。之後，\\n\u3000小幅度降低自身的物理防禦力與魔法防禦力。\\n‧對距離最近的1名敵人造成\\n\u3000特大幅度的物理傷害。之後，\\n\u3000小幅度降低自身的物理防禦力與魔法防禦力。');
insert into unit_enemy_data values (318804, '大王猩猩', 318804, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE1】因痛苦而失去自我的大猿，甚至拒絕了\\n心愛的森林夥伴，遊俠們為了取回寧靜的\\n自然而戰，其強烈的意念正在受到考驗。\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少50％效果。\\n‧中幅度降低來自大王猩猩所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會對距離最近的1名敵人造成\\n\u3000特大幅度的物理傷害，並使其暈眩。\\n\u3000同時對敵方全體造成大幅度的物理傷害。\\n‧大幅度提升自身的物理攻擊力，\\n\u3000並極大幅度提升行動速度。\\n‧對距離最近的1名敵人\\n\u3000造成大幅度的物理傷害。');
insert into unit_enemy_data values (318805, '大王猩猩', 318805, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE2】大猿以撼動森林的咆哮迎擊\\n為魔物著想的遊俠們。\\n人類和魔物能否相互理解呢──\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧中幅度降低來自大王猩猩所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會對距離最近的1名敵人造成\\n\u3000特大幅度的物理傷害。\\n\u3000同時對敵方全體造成大幅度的物理傷害，\\n\u3000並使其暈眩。\\n\u3000此技能造成的傷害，將不會使敵人恢復TP。\\n\u3000此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧以距離最近的1名敵人為中心，\\n\u3000大幅度降低小範圍內的物理防禦力，\\n\u3000並造成特大幅度的物理傷害。\\n\u3000此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的1名敵人造成\\n\u3000大幅度的物理傷害。\\n‧開始蓄力，每秒中幅度恢復TP。蓄力期間，\\n\u3000特大幅度降低自身的物理防禦力與魔法防禦力。\\n\u3000蓄力途中，自身受到一定的傷害時，\\n\u3000此技能將會被迫中斷，並特大幅度降低自身的\\n\u3000TP。蓄力完成後，\\n\u3000特大幅度提升自身的物理攻擊力。');
insert into unit_enemy_data values (318806, '大王猩猩', 318806, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE3】賭上與魔物共存之戰也即將進入\\n尾聲。遊俠們將用盡最後的力量，為的不是傷害，\\n而是要把心意傳達出去！\\n\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧中幅度降低來自大王猩猩所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會對距離最近的1名敵人造成\\n\u3000特大幅度的物理傷害，並使其暈眩。\\n\u3000同時對敵方全體造成大幅度的物理傷害。之後\\n\u3000小幅度降低自身的物理防禦力與魔法防禦力。\\n‧以距離最近的1名敵人為中心，\\n\u3000大幅度降低小範圍內的物理防禦力，\\n\u3000並造成特大幅度的物理傷害。同時使其暈眩。\\n\u3000此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。之後，\\n\u3000小幅度降低自身的物理防禦力與魔法防禦力。\\n‧中幅度恢復自身的TP，並大幅度提升行動速度。\\n\u3000之後，\\n\u3000小幅度降低自身的物理防禦力與魔法防禦力。\\n‧當自身的HP低於50%時，\\n\u3000攻擊將會變得更加凶暴。之後，\\n\u3000小幅度降低自身的物理防禦力與魔法防禦力。\\n‧對距離最近的1名敵人造成\\n\u3000特大幅度的物理傷害。之後，\\n\u3000小幅度降低自身的物理防禦力與魔法防禦力。');
