insert into unit_enemy_data values (318901, '變貌的從者‧布拉德', 318900, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】吸收了超出自身所能負荷的魔力\\n而變貌的從者。失去自我，\\n甚至意圖危害信奉的對象。\\n\\n‧必殺技會解除敵方全體的護盾，\\n\u3000並依照自身的物理攻擊力對敵方全體\\n\u3000造成物理比例傷害。此技能的解除護盾，\\n\u3000即使傷害無效也會施加。\\n‧大幅度降低敵方全體的\\n\u3000物理攻擊力與魔法攻擊力。\\n\u3000同時特大幅度提升自己的物理攻擊力。\\n‧對物理攻擊力或魔法攻擊力其中一者\\n\u3000最高的1名敵方角色造成小幅度的魔法傷害，\\n\u3000並施加誘惑狀態。\\n\u3000並且解除能力提升效果。\\n\u3000此技能的效果，即使傷害無效也會施加。\\n‧對最靠近的1名敵人\\n\u3000造成大幅度的物理傷害。');
insert into unit_enemy_data values (318902, '變貌的從者‧布拉德', 318900, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】吸收了超出自身所能負荷的魔力\\n而變貌的從者。失去自我，\\n甚至意圖危害信奉的對象。\\n\\n‧必殺技會解除敵方全體的護盾，\\n\u3000並依照自身的物理攻擊力對敵方全體\\n\u3000造成物理比例傷害。此技能的解除護盾，\\n\u3000即使傷害無效也會施加。\\n‧大幅度降低敵方全體的\\n\u3000物理攻擊力與魔法攻擊力。\\n\u3000同時特大幅度提升自己的物理攻擊力。\\n‧對物理攻擊力或魔法攻擊力其中一者\\n\u3000最高的1名敵方角色造成小幅度的魔法傷害，\\n\u3000並施加誘惑狀態。\\n\u3000並且解除能力提升效果。\\n\u3000此技能的效果，即使傷害無效也會施加。\\n‧對最靠近的1名敵人\\n\u3000造成大幅度的物理傷害。');
insert into unit_enemy_data values (318903, '變貌的從者‧布拉德', 318900, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】吸收了超出自身所能負荷的魔力\\n而變貌的從者。失去自我，\\n甚至意圖危害信奉的對象。\\n\\n‧必殺技會解除敵方全體的護盾，\\n\u3000並依照自身的物理攻擊力對敵方全體\\n\u3000造成物理比例傷害。此技能的解除護盾，\\n\u3000即使傷害無效也會施加。\\n‧大幅度降低敵方全體的\\n\u3000物理攻擊力與魔法攻擊力。\\n\u3000同時特大幅度提升自己的物理攻擊力。\\n‧對物理攻擊力或魔法攻擊力其中一者\\n\u3000最高的1名敵方角色造成小幅度的魔法傷害，\\n\u3000並施加誘惑狀態。\\n\u3000並且解除能力提升效果。\\n\u3000此技能的效果，即使傷害無效也會施加。\\n‧當自身的HP低於50％時，\\n\u3000大幅度提升自身的物理攻擊力與行動速度。\\n‧對最靠近的1名敵人\\n\u3000造成大幅度的物理傷害。');
insert into unit_enemy_data values (318904, '變貌的從者‧布拉德', 318904, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE1】其身寄宿了龐大的魔力，化為\\n強大魔物的虔誠從者。那股力量一刻不停地\\n增強，將接觸的一切都葬送於黑暗中。\\n\\n‧戰鬥開始時，中幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。以此防禦力提升效果\\n\u3000而上升的防禦力，在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧承受來自變貌的從者‧\\n\u3000布拉德所有的傷害，\\n\u3000導致小幅度降低隊伍角色的TP恢復量。\\n‧必殺技會解除敵方全體的護盾，\\n\u3000並依照自身的物理攻擊力\\n\u3000造成物理比例傷害。此技能的解除護盾，\\n\u3000即使傷害無效也會施加。\\n‧特大幅度降低敵方全體的物理攻擊力，\\n\u3000小幅度降低敵方全體的魔法攻擊力。\\n\u3000同時大幅度提升自己的物理攻擊力與TP。\\n\u3000每次發動，此技能的效果都會再提升。');
insert into unit_enemy_data values (318905, '變貌的從者‧布拉德', 318905, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE2】魔物們憧憬的情感決堤而出。\\n爭先恐後想得到巨大的恩惠\\n而狂暴化的魔物們蜂擁而至。\\n\\n‧戰鬥開始時，中幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。以此防禦力提升效果\\n\u3000而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧大幅度降低敵方全體的\\n\u3000物理攻擊力與魔法攻擊力。\\n\u3000並且特大幅度提升全體我方的物理攻擊力。');
insert into unit_enemy_data values (318906, '變貌的從者‧布拉德', 318904, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE3】不想讓自身宏願的希望脫離\\n掌控，身懷龐大魔力的狂暴從者。\\n粉碎古老的意志，漂亮地妝點勝利！\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。以此防禦力提升效果\\n\u3000而上升的防禦力，在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧承受來自變貌的從者‧布拉德所有的傷害，\\n\u3000導致小幅度降低隊伍角色的TP恢復量。\\n‧必殺技會解除敵方全體的護盾，\\n\u3000並依照自身的物理攻擊力\\n\u3000造成物理比例傷害。\\n\u3000此技能的解除護盾，即使傷害無效也會施加。\\n‧大幅度降低敵方全體的\\n\u3000物理攻擊力與魔法攻擊力。\\n\u3000同時大幅度提升自己的物理攻擊力。\\n‧無視挑釁，對物理攻擊力或魔法攻擊力\\n\u3000其中一者最高的1名敵方角色\\n\u3000造成小幅度的魔法傷害，並施加誘惑狀態。\\n\u3000並且解除能力提升效果。\\n\u3000此技能的效果，即使傷害無效也會施加。\\n‧當自身的HP低於50％時，\\n\u3000大幅度提升自身的物理攻擊力與行動速度。\\n‧對最靠近的1名敵人\\n\u3000造成大幅度的物理傷害。');
insert into unit_enemy_data values (319000, '復活的白色島鯨', 319000, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】在遙遠的北之海再次見到的幻島之巨鯨。\\n吞食人們埋藏的慾望，\\n長久生存於海底深處。\\n\\n‧必殺技能對敵方全體依照其角色\\n\u3000最大HP造成物理比例傷害。\\n\u3000同時大幅度降低物理攻擊力與魔法攻擊力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體依照其最大HP造成\\n\u3000魔法比例傷害。此技能的比例傷害\\n\u3000隨著祕密物品的數量越多，\\n\u3000對其造成的傷害量就會上升越多。\\n‧對距離最近的3名敵人造成中幅度的魔法傷害，\\n\u3000並將其擊退，同時使其陷入暈眩狀態。\\n\u3000此技能的異常狀態，\\n\u3000即使傷害無效也會施加。\\n‧對最靠近的1名敵人\\n\u3000造成大幅度的物理傷害。\\n‧召喚3具祕密物品。召喚時將敵方全體擊退。\\n\u3000祕密物品會大幅度降低\\n\u3000敵方全體的物理攻擊力與魔法攻擊力。\\n\u3000祕密物品的HP低於一定數值時，\\n\u3000中幅度提升敵方全體的\\n\u3000物理攻擊力與魔法攻擊力，\\n\u3000同時當場消失。');
