insert into unit_enemy_data values (319503, '被解放的暴魔‧阿爾杰什', 319503, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE1】殘暴的四隻手所使出的\\n繽紛魔法球風暴。只要一度受到其熾烈的攻擊，\\n就沒有人可以再站起來。\\n\\n‧戰鬥開始時，小幅度提升\\n\u3000自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少25％效果。\\n‧中幅度降低來自被解放的暴魔‧阿爾杰什\\n\u3000所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會解除最靠近的1名敵人的護盾，\\n\u3000造成固定的魔法傷害。同時，\\n\u3000特大幅度提升自身的魔法攻擊力。\\n\u3000依照此技能所造成的傷害量，\\n\u3000攻擊力所提升的數值將會隨之增加。\\n\u3000若使用此技能將敵人擊倒，\\n\u3000額外使自身的魔法攻擊力\\n\u3000特大幅度上升。\\n‧對敵方全體造成中幅度魔法傷害，\\n\u3000並對最靠近的1名敵人\\n\u3000施加凍結狀態與火傷狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體造成中幅度的魔法傷害，\\n\u3000並在擊退敵方的同時，使其暈眩。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (319504, '被解放的暴魔‧阿爾杰什', 319503, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE2】瀰漫的瘴氣偽裝成\\n同學們的模樣。絕不會讓惡毒的卑劣行為\\n弄髒了燦爛耀眼的回憶──\\n\\n‧戰鬥開始時，小幅度提升\\n\u3000自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少25％效果。\\n‧中幅度降低來自被解放的暴魔‧阿爾杰什\\n\u3000所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧對最靠近的1名敵人\\n\u3000造成大幅度的魔法傷害。');
insert into unit_enemy_data values (319505, '被解放的暴魔‧阿爾杰什', 319503, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE3】此時此刻，提升至極限的魔力\\n將獲得解放。將學園每個人的聲援轉化為力量，\\n以青春魔法打飛敵人吧！\\n\\n‧戰鬥開始時，小幅度提升\\n\u3000自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少25％效果。\\n‧中幅度降低來自被解放的暴魔‧阿爾杰什\\n\u3000所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會解除最靠近的1名敵人的護盾，\\n\u3000造成固定的魔法傷害。同時，\\n\u3000特大幅度提升自身的魔法攻擊力。\\n\u3000依照此技能所造成的傷害量，\\n\u3000攻擊力所提升的數值將會隨之增加。\\n\u3000若使用此技能將敵人擊倒，\\n\u3000則能特大幅度提升\\n\u3000自身的魔法攻擊力。\\n‧對敵方全體造成特大幅度的魔法傷害。\\n‧對敵方全體造成大幅度的魔法傷害。\\n‧中幅度提升自身的魔法攻擊力，\\n\u3000並中幅度恢復自身的TP。\\n‧對最靠近的1名敵人造成\\n\u3000大幅度的魔法傷害。');
insert into unit_enemy_data values (319600, '冰霜獵犬', 319600, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】支配冰河地區的嚴霜魔狼。\\n冷酷的幼狼們貫穿目標，以冰爪撕裂獵物。\\n\\n‧容易受到每隔一定時間\\n\u3000產生的火傷、中毒與猛毒傷害的影響。\\n‧戰鬥開始時，\\n\u3000小幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧中幅度降低來自冰霜獵犬所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會對除了召喚物、分身以外，\\n\u3000距離最近的3名敵人造成特大幅度的\\n\u3000魔法傷害，並施加凍結狀態，\\n\u3000再小幅度提升自身的魔法防禦力，\\n\u3000直到戰鬥結束。此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (319601, '冰霜獵犬', 319601, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】支配冰河地區的嚴霜魔狼。\\n冷酷的幼狼們貫穿目標，以冰爪撕裂獵物。\\n\\n‧容易受到每隔一定時間\\n\u3000產生的火傷、中毒與猛毒傷害的影響。\\n‧戰鬥開始時，\\n\u3000中幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，特大幅度降低敵方全體的HP吸收。\\n‧戰鬥開始時，\\n\u3000展開小幅度降低敵方全體TP上升的領域。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少50％的效果。\\n‧中幅度降低來自冰霜獵犬所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會對除了召喚物、分身以外，\\n\u3000距離最近的3名敵人造成特大幅度的\\n\u3000魔法傷害，並施加凍結狀態，\\n\u3000再小幅度提升自身的魔法防禦力，\\n\u3000直到戰鬥結束。此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧除了召喚物、分身以外，\\n\u3000對距離最近的3名敵人造成大幅度的魔法傷害，\\n\u3000並中幅度降低其魔法防禦力。\\n\u3000此技能造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (319602, '冰霜獵犬', 319602, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】支配冰河地區的嚴霜魔狼。\\n冷酷的幼狼們貫穿目標，以冰爪撕裂獵物。\\n\\n‧中幅度降低來自冰霜獵犬\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧必殺技會對除了召喚物、分身以外\\n\u3000距離最近的3名敵人造成特大幅度的魔法傷害，\\n\u3000同時施加凍結狀態，\\n\u3000並小幅度提升自身的魔法防禦力\\n\u3000直到戰鬥結束。此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對除了召喚物、分身以外\\n\u3000距離最近的3名敵人\\n\u3000造成大幅度的魔法傷害，\\n\u3000中幅度降低其魔法防禦力。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (319700, '布武機', 319700, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】覬覦皇家裝備的天才發明家三女。\\n飄浮兵器使出槍林彈雨和特大炸彈，\\n從上空嘲笑著敵人驚慌失措的模樣。\\n\\n‧戰鬥開始時，\\n\u3000極大幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000對自身施加TP降低減少100％效果。\\n‧中幅度降低來自被解放的布武機\\n\u3000所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧當布武機的HP低於一定數值時，\\n\u3000飄浮兵器將墜落，無法行動。無法行動的期間，\\n\u3000自身的物理防禦力與魔法防禦力歸零。\\n‧必殺技會解除敵方全體的護盾，\\n\u3000造成特大幅度的物理傷害，\\n\u3000並使其暈眩。此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧普通攻擊會對最靠近的1名敵人\\n\u3000造成大幅度的物理傷害，並使其暈眩。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧擊退敵方全體，\\n\u3000召喚布武機炸藥♡。\\n‧布武機炸藥♡每當經過一定的時間，\\n\u3000會對敵方全體造成大幅度的物理傷害，\\n\u3000並在施加暈眩狀態後，當場消失。\\n\u3000此技能所造成的狀態異常，即使傷害無效\\n\u3000也會施加。但是，當布武機炸藥♡的HP\\n\u3000低於一定數值時，在對布武機造成\\n\u3000固定的物理傷害後，當場消失。');
insert into unit_enemy_data values (319800, '若菜', 319800, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】盜賊公會蠻賊三姊妹的次女。\\n為了幫助在戰鬥中害怕地躲在後方的若菜，\\n尖刺兄弟決定擋住冒險者的去路。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果而上升的\\n\u3000防禦力，在戰鬥中不會受到防禦力下降技能的\\n\u3000影響。\\n‧戰鬥開始時，對自身施加TP降低減少100％\\n\u3000效果。\\n‧中幅度降低來自若菜與尖刺兄弟所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會對敵方全體造成大幅度的物理傷害，\\n\u3000並使其暈眩。此技能所造成的狀態異常，即使\\n\u3000傷害無效也會施加。同時，對若菜造成固定的\\n\u3000物理傷害。\\n‧大幅度提升自身以外，我方全體的物理攻擊力，\\n\u3000並中幅度恢復自身的TP。\\n‧戰鬥開始時，尖刺兄弟會來幫忙。此時，擊退\\n\u3000敵方全體。\\n‧尖刺兄弟每次行動時，會極大幅度提升若菜的\\n\u3000物理防禦力與魔法防禦力。尖刺兄弟會\\n\u3000特大幅度提升自身的行動速度。');
insert into unit_enemy_data values (319900, '倭', 319900, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】豪放不羈的蠻賊三姊妹長女。\\n開局會以強力斬擊與弱化發起猛攻，\\n但若被逼至絕境，便會賭一把來改變戰況。\\n\\n‧戰鬥開始時，中幅度提升自身的物理防禦力\\n\u3000與魔法防禦力。以此防禦力提升效果\\n\u3000而上升的防禦力，在戰鬥中不會受到\\n\u3000防禦力下降技能的影響。\\n‧戰鬥開始時，對自身施加TP降低減少50％\\n\u3000效果。\\n‧中幅度降低來自倭所有傷害\\n\u3000產生的隊伍角色TP恢復量。\\n‧當倭的HP低於一定數值時，在小幅度降低\\n\u3000自身的TP後，會在一定時間內無敵，並使敵方\\n\u3000全體陷入沉默狀態。之後再孤注一擲投\\n\u3000擲炸彈。當倭賭贏時，會對前方的2名\\n\u3000角色依照其角色最大HP造成物理\\n\u3000比例傷害。當倭賭輸時，\\n\u3000會極大幅度降低自身的物理防禦力與魔法防禦力，\\n\u3000無法行動。\\n‧必殺技會特大幅度降低距離最近的1名\\n\u3000敵人的物理防禦力。之後，造成極\\n\u3000大幅度的物理傷害。\\n‧對距離最遠的1名敵人造成特大幅度的物理傷害\\n\u3000並大幅度拉進，再使其暈眩。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (320000, '哈立德', 320000, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】於遠古時代被製造出來的防衛兵器。由於\\n邪惡之人的願望而解除封印，從地底深處現身。\\n\\n‧必殺技會對敵方全體造成固定的魔法傷害，並\\n\u3000小幅度降低其魔法防禦力。此技能所造成的\\n\u3000狀態異常，即使傷害無效也會施加。\\n‧以距離最近的1名敵人為中心，造成小範圍的\\n\u3000大幅度魔法傷害。\\n‧對自身施加小幅度魔法傷害，並小幅度降低\\n\u3000物理防禦力與魔法防禦力。\\n‧當自身的HP低於50％時，右臂毀損，並改變\\n\u3000行動模式。同時特大幅度降低自身的物理防禦\\n\u3000力、魔法防禦力。\\n‧對距離最近的1名敵人造成大幅度的魔法傷害。');
