insert into unit_enemy_data values (320003, '哈立德', 320003, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE1】巨兵曾經率領一同守護城鎮的\\n兵士們，然而現在卻打算讓沙漠之國陷入混亂。\\n井然有序的合作，經過漫長的時光仍未改變。\\n\\n‧戰鬥開始時，小幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果而上升的\\n\u3000防禦力，在戰鬥中不會受到防禦力下降技能的\\n\u3000影響。\\n‧中幅度降低來自被解放的哈立德所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧對距離最近的1名敵人造成大幅度的魔法傷害。\\n‧戰鬥開始時，召喚3名古代小兵，擊退敵方\\n\u3000全體。\\n‧每打倒1名古代小兵，剩餘目標的數量就會\\n\u3000減少1個。之後會再次召喚古代小兵，擊退\\n\u3000敵方全體。');
insert into unit_enemy_data values (320004, '哈立德', 320003, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE2】集結古代智慧製作而成的兵器將\\n解放隱藏的力量。自巨大身軀內出現的紅蓮之光，\\n將使萬物化為沙漠的灰燼。\\n\\n‧戰鬥開始時，中幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果而上升的\\n\u3000防禦力，在戰鬥中不會受到防禦力下降技能的\\n\u3000影響。\\n‧中幅度降低來自被解放的哈立德所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧蓄滿能量後，對距離最近的3名角色造成\\n\u3000999999的固定魔法傷害，並對包含飛行\\n\u3000狀態的敵方全體造成特大幅度的魔法傷害。\\n\u3000之後特大幅度降低自身的物理防禦力、魔法\\n\u3000防禦力，以及能力提升效果，並施加火傷狀態。');
insert into unit_enemy_data values (320005, '哈立德', 320003, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE3】由於抱持邪念的愚者葬送了性命，\\n使得沙漠居民對古代兵器心懷恐懼。粉碎其\\n莫大的威脅，重拾大家的笑容吧。\\n\\n‧戰鬥開始時，中幅度提升自身的物理防禦力與\\n\u3000魔法防禦力。以此防禦力提升效果而上升的\\n\u3000防禦力，在戰鬥中不會受到防禦力下降技能的\\n\u3000影響。\\n‧中幅度降低來自被解放的哈立德所有傷害產生的\\n\u3000隊伍角色TP恢復量。\\n‧必殺技會對敵方全體造成固定的魔法傷害，並\\n\u3000小幅度降低其魔法防禦力。此技能所造成的\\n\u3000狀態異常，即使傷害無效也會施加。\\n‧以距離最近的1名敵人為中心，造成小範圍的\\n\u3000大幅度魔法傷害。\\n‧對自身施加小幅度魔法傷害，並小幅度降低\\n\u3000物理防禦力與魔法防禦力。\\n‧當自身的HP低於50％時，右臂毀損，並改變\\n\u3000行動模式。同時特大幅度降低自身的物理\\n\u3000防禦力、魔法防禦力和能力提升效果。\\n‧對距離最近的1名敵人造成大幅度的魔法傷害。');
insert into unit_enemy_data values (320200, '巴西利斯克‧阿洛斯', 320200, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】突然出現在雪山的變異巴西利斯克。\\n飛舞著的怪異巨大身軀，使聖夜前夕的人們和\\n魔物墜入絕望的深淵。\\n\\n‧必殺技會對距離最近的1名角色\\n\u3000造成特大幅度的魔法傷害。\\n\u3000同時對敵方全體造成中幅度的魔法傷害，\\n\u3000並且賦予石化狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的1名角色\\n\u3000造成特大幅度的魔法傷害\\n‧對距離最近的1名角色\\n\u3000造成中幅度的魔法傷害。');
insert into unit_enemy_data values (320201, '巴西利斯克‧阿洛斯', 320200, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】突然出現在雪山的變異巴西利斯克。\\n飛舞著的怪異巨大身軀，使聖夜前夕的人們和\\n魔物墜入絕望的深淵。\\n\\n‧必殺技會對距離最近的1名角色\\n\u3000造成特大幅度的魔法傷害。\\n\u3000同時對敵方全體造成中幅度的魔法傷害，\\n\u3000並且賦予石化狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的1名角色\\n\u3000造成特大幅度的魔法傷害\\n‧對距離最近的1名角色\\n\u3000造成中幅度的魔法傷害。');
insert into unit_enemy_data values (320202, '巴西利斯克‧阿洛斯', 320200, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】突然出現在雪山的變異巴西利斯克。\\n飛舞著的怪異巨大身軀，使聖夜前夕的人們和\\n魔物墜入絕望的深淵。\\n\\n‧必殺技會對距離最近的1名角色\\n\u3000造成特大幅度的魔法傷害。\\n\u3000同時對敵方全體造成中幅度的魔法傷害，\\n\u3000並且施加石化狀態。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對敵方全體造成中幅度的魔法傷害，\\n\u3000並且施加石化狀態。\\n此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。\\n‧對距離最近的1名角色\\n\u3000造成特大幅度的魔法傷害。\\n‧當自身的HP低於50％時，改變行動模式，\\n\u3000並中幅度提升自身的魔法攻擊力。\\n‧同時對距離最近的1名角色\\n\u3000造成特大幅度的魔法傷害。');
insert into unit_enemy_data values (320203, '巴西利斯克‧阿洛斯', 320203, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE1】受到狂暴怪鳥的波及，\\n住在深山的魔物們開始大鬧四方。\\n華麗的擊倒襲擊而來的魔物群，\\n取得可以點綴饗宴的食材吧！\\n\\n‧戰鬥開始時，\\n\u3000中幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果而上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000對自己施加TP降低減少50％效果。\\n‧中幅度降低來自\\n\u3000巴西利斯克‧阿洛斯、冰雪角鹿、\\n\u3000寒冰雛鳥、冰魚、寒冰鼴鼠\\n\u3000所有傷害產生的隊伍角色TP恢復量。\\n‧對距離最近的1名角色\\n\u3000造成中幅度的魔法傷害。\\n‧戰鬥開始時，\\n\u3000召喚冰雪角鹿、寒冰雛鳥、冰魚、寒冰鼴鼠，\\n\u3000並擊退敵方全體。\\n‧每打倒1隻怪物，\\n\u3000剩餘的目標數量就會減少1個。\\n\u3000之後會再次召喚怪物，\\n\u3000擊退敵方全體。');
insert into unit_enemy_data values (320204, '巴西利斯克‧阿洛斯', 320204, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE2】振翅高飛的怪鳥用光速\\n劃破寒冬的天空，將反抗者\\n以巨大的身軀擊飛。\\n能否抓準的反作用力的破綻\\n進行攻擊將是勝敗的關鍵。\\n\\n‧戰鬥開始時，\\n\u3000中幅度提升自身的物理防禦力與魔法防禦力。\\n\u3000以此防禦力提升效果上升的防禦力，\\n\u3000在戰鬥中不會受到防禦力下降技能的影響。\\n‧戰鬥開始時，\\n\u3000對自己施加TP降低減少50％效果。\\n‧在一定時間內會累積力量，飛到上空。\\n\u3000飛行中，賦予自身無敵狀態。\\n\u3000隨後會向下俯衝，對距離最近的1名角色\\n\u3000造成依照自身最大HP的物理比例傷害。同時，\\n\u3000依照此技能所造成的傷害量\\n\u3000造成特大幅度的物理傷害，\\n\u3000特大幅度降低自身的物理防禦力與魔法防禦力。');
