insert into unit_enemy_data values (320600, '雙冠龍雷克斯', 320600, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】棲息於吉歐‧格黑納的恐龍\\n魔物。頭部的雙冠不僅是用於威嚇，\\n還會變形為刀刃狀將獵物斬斷。\\n\\n‧戰鬥開始時，對自身施加\\n\u3000TP降低減少50％效果。\\n‧中幅度降低來自\\n\u3000雙冠龍雷克斯所有傷害\\n\u3000產生的隊伍角色\\n\u3000TP恢復量。\\n‧必殺技會依照\\n\u3000雙冠龍雷克斯的物理攻擊力\\n\u3000對敵方全體\\n\u3000造成物理比例傷害。\\n‧對前方2名角色\\n\u3000造成中幅度的物理傷害，\\n\u3000將其擊退同時使其暈眩。\\n\u3000此技能所造成的狀態異常，\\n\u3000即使傷害無效也會施加。');
insert into unit_enemy_data values (320700, '地獄的聖女富美惠', 320700, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】看管著神聖學院隱藏暗部的獄卒。\\n為了守護高喊絕對遵從教義的「鋼鐵聖女」的正\\n義，無情地強迫不願順從的罪人服從與洗心革面。\\n\\n‧必殺技會對敵方全體依照其角色最大HP\\n\u3000造成物理比例傷害。造成的傷害比例會隨著\\n\u3000自身的物理攻擊力而增加。\\n‧對距離最近的3名敵人依照其剩餘HP造成物理\\n\u3000比例傷害。造成的傷害比例會隨著自身的物理攻\\n\u3000擊力而增加。\\n‧特大幅度提升自身的物理攻擊力，並且特大幅度\\n\u3000恢復TP。自身的HP越低，物理攻擊力提升的\\n\u3000數值也越高。\\n‧對距離最近的1名敵人造成特大幅度的物理\\n\u3000傷害。');
