insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1001, 1, '惡德伊商會', '由壞人「惡德伊」擔任會長的邪惡商會。放高利貸、綁架、偷渡違法武器，做盡各種邪惡勾當。惡德伊說話雖然客氣，但卻是個大壞蛋。真正的壞人，指的就是他這種人。', 0, 1, 1);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1002, 1, '阿斯特賴亞大陸', '世界最大的大陸。在大陸的中心有個巨大都市『蘭德索爾』，其中矗立著一座高達天際的索爾之塔。', 0, 2, 2);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1003, 1, '阿斯特朗', '稱呼這個世界整體時所使用的單詞。也是阿斯特賴亞大陸的由來。不過，「阿斯特朗」的語源是從何而來，則沒有一個肯定的說法。', 0, 2, 3);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1004, 1, '愛梅斯', '在主角夢中出現的神秘少女。就她的口吻看來，她似乎知道有關主角的過去。特徵是話中帶刺，並有著破損的翅膀。可可蘿將愛梅斯當作是神一樣地崇拜，也接收她所下達的旨意。', 1000101, 1, 4);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1005, 1, '移動龍卷風', '修特帕魯的綽號。如同字面上的意思，即使只是走在路上，她也會像龍卷風一般，接二連三地引發各式各樣的事件，所以這麼叫她的人也自然而然的變多了。而本人似乎很喜歡『龍卷風』這個詞，所以用這個綽號叫她的話，她會很開心地回應。', 0, 1, 5);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1006, 1, '安妮羅潔‧馮‧修特帕魯', '杏奈的本名。不管對方是誰，都要先報上自己的姓名是杏奈的原則。', 0, 1, 6);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1007, 1, '魔瞳（Evil‧Eye）', '在我這個疾風之冥姬的左眼當中，已經累積了大量的魔力！\u3000當要使出極為強大的魔法時，我的眼睛就會放出這世上絕無僅有的閃耀光輝！', 0, 1, 8);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1008, 1, '純白之翼', '莫妮卡在祖國所隸屬的部隊名稱。有白色翅膀之意。為了提升這名字的知名度，莫妮卡每天都很努力地宣傳。', 0, 3, 9);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1009, 1, '純白之翼 蘭德索爾分部', '是由公會會長莫妮卡所組成的公會。成員為莫妮卡、妮諾、雪、空花、步未等5人。但與其說是「組成」，還不如說是「不小心組成了」比較正確。成員們總因自己少根筋的行動而引發危機，但她們又會自己華麗地解決掉。所以，這個公會到底該說是充斥著不成材的人，還是潛能無限呢，現在還無法判斷。', 0, 3, 10);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1010, 1, '紫藤家', '名聲響徹阿斯特賴亞大陸，可說是蘭德索爾第一的商家也不為過的知名家族。其中，由於許多為人津津樂道的傳聞，因此他們的千金「秋乃‧紫藤」的名字在蘭德索爾是廣為人知。不論是在好的意思，或是壞的意思上都是。', 0, 1, 11);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1011, 1, '貝利奧火山', '阿斯特賴亞大陸上最巨大的火山。至今仍頻繁的噴發，再加上有許多魔物棲息於此，是個危險的地區。', 0, 2, 12);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1012, 1, '埃斯特雷拉街道', '位於阿斯特賴亞大陸，綠意盎然的街道。由於位處氣候較佳的地域，棲息於此的植物，動物，昆蟲的種類也很多。在這裡散步，就能看到各式各樣的生物。', 0, 2, 13);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1013, 1, '深淵悠久＝翠翔裂空斬（Eternal Abyss=Divider）', '揮舞寄宿在劍上的魔力，將其釋放的魔法。壓縮成高密度的魔力能夠發揮出傲人的威力，光是稍微碰觸到就能讓人體無完膚。', 0, 1, 14);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1014, 1, '精靈族', '居住於阿斯特朗的四個種族的其中之一。有著尖尖長長的耳朵是他們外貌的特徵。一般來說，相較於其他種族智力較高，能使用魔法的人也比較多。因為熱愛自然，所以居住於森林裡的人不在少數，但近年來定居於蘭德索爾的人也逐漸增加。', 0, 1, 16);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1015, 1, '精靈之森', '位於阿斯特賴亞大陸，自然環境豐富的森林。在整個大陸的精靈族當中，大多生於此處，在出生後即在此與植物、動物等各式各樣的生命進行交流。', 0, 2, 17);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1016, 1, '結緣之花', '在棲息於貝利奧火山為數不多的植物當中，最為稀有的植物種類之一。因為非常稀有，因此有著「只要將它送給意上人，即可讓戀情開花」的傳聞。', 0, 1, 18);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1017, 1, '歐拉爾高山', '位於阿斯特賴亞大陸，非常險峻的高山。雖然有條山路與貝利奧火山相連，但是通行的難度實在太高，因此罕有人至。', 0, 2, 19);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1018, 1, '音響盤', '可紀錄聲音，很特殊的魔石。原理是注入魔力後，即可播放紀錄於其中的聲音，被加工成圓盤狀於市面上流通。依據魔石的大小，能播放的時間會有所不同。在過去，若要長時間收聽，就需要有人能持續灌注魔力才行，因此只有貴族與富翁持有。但是在數十年前，開發出能透過儲存魔力的石頭將魔力灌注於音響盤的技術，並以此製造出播放機器後，音響盤便快速地普及了。現在整個裝置的價格變得很合理，任誰都能輕鬆享受收聽音響盤的樂趣。', 0, 1, 20);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1019, 1, '哞哞自衛隊', '【動物苑】的下游組織。同屬【動物苑】的夥伴，主要是保護大家免於魔物威脅的武鬥派公會。也會調查並解決「闇影」之類的異常狀況。與【王宮騎士團】勢如水火，總是相互對立，彼此競爭的狀況也從不間斷。', 0, 1, 21);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1020, 1, '賭場', '蘭德索爾內的娛樂設施。可以在此遊玩輪盤、卡牌遊戲等等。無論是要打發時間或是希望一夜致富……匯聚了人們的希望與絕望，每天都十分熱鬧。', 0, 1, 22);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1021, 1, '慈樂之音', '由以偶像身份進行單獨活動的望擔任公會長的偶像公會。是個透過唱歌與跳舞（帶著哀傷的心情）戰鬥的公會，舉辦演唱會等表演為其主要的活動內容', 0, 1, 23);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1022, 1, '盟約外典術式第666（Geass Apocrypho・Tyrant Code）', '過去被神所封印在世界的背面，記載著這世界的各種法則之操縱術的一本書。修特帕魯將記載於該書當中之術式的一小節刻劃於靈魂，讓儲存於魔瞳裡的魔力不至於溢出。只有在使用大規模魔法時，會使用該術式將魔力灌滿全身。', 0, 1, 24);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1023, 1, '公會', '為特定目的而活動的人們的團體。在蘭德索爾當中，國民都有隸屬於「公會」的義務。加入某些公會，能享受部份公共設施，而未加入公會的人則可能會遭遇無法獲得社會信用等不少缺點。公會有各式各樣的目的，商業、軍事、文化事業等，支持著蘭德索爾的各種產業。另外，一個人也可以加入多個公會。', 0, 1, 25);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1024, 1, '公會管理協會', '管理蘭德索爾各公會的公家機關。各公會皆受到管理協會的嚴格管理，要是沒有實際進行活動，就會遭受解散處份。但是，也有像犯罪集團一樣的反社會的非公認公會存在。', 0, 1, 26);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1025, 1, '公會小屋', '組成公會後，賜予蘭德索爾國民們的活動據點。根據公會的活動規模、功績等，可擴充設施或是進行改建。', 0, 1, 27);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1026, 1, '冰晶山脈', '過去曾因採掘水晶而繁榮發展，但現在只是一座完全不見當時榮景的古城，聳立在山裡。知道這段歷史的人很少。', 0, 1, 28);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1027, 1, '效率狂', '指的是凡事皆以效率為最優先的人。簡單來說就是像美冬這樣的人。在打電動的時候，會照著攻略本進行攻略的那種類型。', 0, 1, 30);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1028, 1, '魔像', '魔法師所創造出來的彷生命體。會忠實地執行創造主的命令。透過泥土或礦石等物質生成，有著各式各樣的尺寸與能力。', 0, 1, 31);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1029, 1, '收藏家（Collector）', '除了魔法相關書籍，還會蒐集小說、文藝作品或是有趣的古代遺產等等，這是走遍各地蒐集了各種東西的七七香獲得的綽號。但本人覺得自己還不配擁有這個綽號。', 0, 1, 32);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1030, 1, '沙羅曼達之血', '此為居住於火山的魔物「沙羅曼達」的血液，有著很高的融解能力。腐蝕能力很高，對人體極為有害。就連沙羅曼達自己也可能被自己的血液給侵蝕，導致死亡。', 0, 1, 33);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1031, 1, '咲戀育幼院', '由前【王宮騎士團】的副團長咲戀所統籌與經營的公會。雖然是個孤兒院，但也有在做生意。咲戀在過去護衛王宮時，發現每天都有許多閒暇時間非常浪費，為了能為他人盡一份心力而開始營運。', 0, 1, 34);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1032, 1, '熾炎戰鬼煉獄血盟暗黑團（The‧Order‧Of‧Gehenna‧Immortals）', '修特帕魯在前世時所隸屬的暗黑騎士團。「暗黑騎士們在過去皆已墮落於罪惡當中。但是為了殲滅要顛覆世界的暗黑存在――《星之審判者》，他們決定使用相同的黑暗力量與其對抗。最後，在與星之審判者的戰鬥中，暗黑騎士們全數滅亡……」（節錄自『冥風戰記』）', 0, 1, 35);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1033, 1, '闇影', '有著人類姿態的特殊魔物。雖然會說人話，但那些也只是由沒有脈絡的單字所組成，因此推測牠們並未擁有智能。在蘭德索爾當中目擊他們的頻率急速飆升，被視為問題。', 0, 1, 36);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1034, 1, '索爾魔珠', '據說這是要抵達索爾之塔頂端不可或缺的道具，是個半透明的球體。現在已經沒有多少人相信這個傳說，被認為只存在於故事之中。', 0, 1, 37);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1035, 1, '索爾之塔', '聳立於蘭德索爾中心的巨塔。這是阿斯特賴亞大陸上最大的建築物，也是最大的謎團。', 0, 1, 38);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1036, 1, '紅豆麵包', '鈴的最愛。有時還會將所有的薪水拿去買紅豆麵包。豆沙餡派和紅豆餡派這兩種派別的論戰，已持續多年。', 0, 1, 7);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (1037, 1, '遊戲', '是近年製造而成的玩具。可以使用硬體的魔法道具，加上使用了特殊礦石打造的軟體記憶媒體一起玩。因為是以高技術打造而成，因此非常的高價。', 0, 1, 29);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2001, 2, '有我在喔好朋友1號', '這是碧蒐集了掉落在精靈之森的鐵皮零件所做出的人偶。被碧拿來當作是交朋友用的練習對象，每天都會跟它說話。通稱「好朋友號」。與好朋友號的對話，基本上都是碧單方面在說話，但是在碧的想像當中，這些對話都是很完美地在進行。', 0, 1, 39);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2002, 2, '暮光之都', '據說存在於這世界上某處的地方。在傳說中，這是一個在古代十分繁榮的都市，各種技術也都非常發達。有關暮光之都的傳說不少，像是「可鍛造出絕對不會斷掉的武器的刀匠」、「可來往於各星球間的飛空艇」等等都很有名。', 0, 1, 40);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2003, 2, '超能力', '能夠實現一般人類無法作到的事的超自然特殊身體能力。不需要進行系統式學習，也不需要詠唱咒文的地方，與魔法不同。截至目前為止，尚未證實有擁有超能力的人類。不過，有收到有人浮在空中的目擊情報，因此造成了那是魔法還是超能力的議論。', 0, 1, 41);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2004, 2, '通訊魔法', '阿斯特賴亞大陸所使用，遠距離通訊用的魔法。分別有由隸屬公家機關，或私立團體的魔法師透過經整頓的基礎魔法網路進行通訊，或是透過個人魔法通訊這兩種方法。但不論是哪一種，都是屬於高等魔法，因此精準度並不高，也容易發生障礙。', 0, 1, 42);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2005, 2, '惡魔偽王國軍', '伊莉亞‧奧恩斯坦與魔族的少女們所組成的公會。這名稱與過去伊莉亞和神明展開大規模對抗時，所率領的魔族最強軍隊的名稱相同。', 0, 1, 43);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2007, 2, '破壞者（Destroyer）', '有時會以保鏢的身份發揮自己的實力――惠理子的綽號。', 0, 1, 44);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2008, 2, '傳說的吸血鬼', '在太古時代，率領魔族與神明們對抗的魔族女王。雖然在魔族間是很知名的故事，但認為她真的存在的人也為數不少。同時也被稱呼為「傳說的大魔王」「傳說的魔女」等。', 0, 1, 45);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2009, 2, '破曉之星', '由日和、優衣、怜等 3 人所組成的助人公會。公會長由日和擔任，主要的活動內容就是幫助遇到困難的人。但是她們真正目的是，至今懷抱同樣夢想的 3 個人都想達到的願望，「到達索爾之塔的頂端」。', 0, 1, 46);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2010, 2, '洞窟', '出現在阿斯特賴亞大陸地底的洞穴。由於大多被魔物當成巢穴，所以一般都將其視為危險區域。不過，也會被用來進行違法交易，或是被盜賊當成祕密基地。也有人將其視為死後世界的入口，將其作為墓地來使用。', 0, 1, 47);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2011, 2, '動物苑', '獸人們的互助組織公會。居住在蘭德索爾的獸人們幾乎都隸屬於【動物苑】，接受其援助。雖然是個能與【公主騎士】匹敵的大規模公會，但由於每個人的參與意識都比較薄弱，所以沒有人能掌握公會的整體面向。這是無數公會的結合體，還有【哞哞自衛隊】【伊麗莎白牧場】等下游組織。', 0, 1, 48);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2012, 2, '東國', '妮諾十分嚮往，據說是在蘭德索爾東方的遙遠國度。忍者、武士、藝妓、富士山……據說那裡有著許多充滿異國情懷的事物。', 0, 1, 49);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2013, 2, '暮光流星群', '尋找暮光之都的人們用來交流各式情報的公會。公會長是流夏。除了流夏以外的成員，幾乎都是擁有綽號的名人，在這些人組成公會之後，便傳出了蘭德索爾最危險的公會出現了的傳言。', 0, 1, 50);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2014, 2, '王宮騎士團', '可說是【公主騎士】本隊的騎士團公會。他們用「團長」來稱呼公會長。可以說是蘭德索爾的國軍，活動目的是守護「王宮」以及王族，以及接受敕命達成任務。同時，作為守護城鎮治安的警察組織這面也很重要。有著由王侯貴族出任要職的傳統，出身於市民階級的騎士無法進入王宮等制約也很多。', 0, 1, 52);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2015, 2, '馬車', '阿斯特賴亞大陸的主要交通手段。雖然有著像飛空艇那樣高等的魔法移動裝置，但由於非常昂貴，在路地上移動時，主要還是使用馬車。也有許多蘭德索爾和外部村落聯合的，個人所有的定期馬車。', 0, 1, 53);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2016, 2, '獸人族', '居住於阿斯特朗的四個種族的其中之一。總人口數僅次於人族。擁有各式各樣的野獸特徵，一般來說，身體能力比其他種族要高。就外觀特徵來說，有著獸耳與尾巴。', 0, 1, 54);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2017, 2, '美食殿堂', '為了要進入某些需有加入公會才能進入的餐廳，由貪吃佩可所創立的公會。目的是邊走邊吃。由於才剛組成，所以成員也很少，是個弱小的公會。', 0, 1, 56);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2018, 2, '人族', '居住於阿斯特朗的四個種族的其中之一。是人口最多的種族。由於蘭德索爾的施政是以人族貴族為中心進行，所以某些人族的人會有特權意識，而這也導致了發生迫害其他種族的事件。', 0, 1, 57);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2019, 2, '幻影貓', '最近在蘭德索爾蔚為話題的神秘美少女義賊。會闖入罪大惡極的壞人家裡偷竊，並將偷到的寶物發送給貧困的百姓們。雖然真正的身份成謎，但珠希曾說「幻、幻影貓的真面目？\u3000我、我不知道喵。不過，我有聽說她是個非常可愛，又很喜歡貓咪的義賊喵。今後也請多多支持幻影貓唷喵！」以上。幻影貓……到底是何許人也呢……', 0, 1, 58);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2020, 2, '森林守衛', '管理並守護精靈族居住的森林，歷史悠久的公會。現在的公會長是美里。活動內容包含管理只有在精靈之森才能取得的藥草等交易品，以及負責擔任其他種族的人員進入森林時的嚮導等，範圍非常廣闊。', 0, 1, 59);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2021, 2, '普拉諾平原', '位於阿斯特賴亞大陸上的平原。由於遠離主要街道，是個人類無法觸及的自然之地，因此有許多魔物棲息於此。但由於離城鎮不遠，因此為了試試手腕而造訪此地的武藝者源源不絕。', 0, 1, 60);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2022, 2, '布里薩溪谷', '位於阿斯特賴亞大陸的溪谷。因其特殊的地形，使得微風吹拂不止，造訪此地的冒險者們都可感受到舒服的微風。', 0, 1, 61);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2023, 2, '公主騎士', '統治蘭德索爾的「王宮」的直屬公會。統括軍事、郵遞、警察、消防等公共事業的公會總稱即為【公主騎士】，有幾個下游組織。由於他們是要守護王族，特別是身為君王的公主的騎士，因此才會取了這個名字。', 0, 1, 62);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2024, 2, 'BB團', '正式名稱為「邊緣人掰掰團」。這是碧與主角在思考交朋友的方法時所組成的兩人小隊。雖然活動內容多采多姿，但總結來說是為了探討「朋友是什麼」如此宛如哲學般深遠的議題。就這層意義上，或許可以算是一個智慧型集團了。但實際的活動內容還是先別提了。', 0, 1, 55);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2025, 2, '防御魔石', '當面臨危險狀況時，只要按下按鈕就會發出很大聲的警告聲，是個護身用道具。主要是讓小孩子出門時帶在身上。它有著各式各樣的效果，從發出噪音到以發出超音波來破壞對方聽力都能辦到。', 0, 1, 63);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2026, 1, '伊麗莎白牧場', '位於山上的某個牧場公會。活動內容如其名就是一個牧場，飼養牛與馬之類的家畜，並且經營著畜牧業，但這裡同時也有當作讓獸人們放鬆的野餐場地，以及病人療養地等各種功能。平常幾乎不會與城鎮有所接觸，但因為需要將牛奶跟肉送往市場，因此商人們很頻繁地在此出沒。', 0, 3, 15);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (2027, 2, '橡子', '這是繼紅豆麵包之後鈴的另一愛好，依品種不同，味道也不一樣。鈴說，為了能在所有地方都能吃到，常常把它拿著到處走來走去。', 0, 1, 51);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (3001, 3, '魔族', '居住於阿斯特朗的四個種族的其中之一，人口最少。外觀的特徵是頭部長著兩根角。魔力、智力、運動、感覺等，魔族的人大多擁有某項優秀的能力。也有他們曾是與眾神爭奪世界霸權的高潔一族的傳說。', 0, 1, 64);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (3002, 3, '魔法道具', '在製作過程中加入了魔法，可讓效果長時間發揮的道具。像是附加了吸收衝擊魔法的防具，或是讓腕力上升的手鐲等道具都有在市面上流通。另外，魔法的效果會低於原本的道具。但因為即使是不太會使用魔法的人，只要有魔力就能使用，所以很受魔族與人族歡迎。雖然發生機率不高，但偶而會出現副作用，因此使用時需要特別注意這一點。', 0, 1, 65);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (3003, 3, '魔法少女鈴鈴噹', '蘭德索爾裡的兒童文學。是以過去存在的傳說英雄為主角的故事，也常常拿來當作大眾戲劇表演。', 0, 1, 66);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (3004, 3, '魔物', '棲息於阿斯特賴亞大陸的神秘生物。個性兇暴，威脅著人類與動物們的生活。', 0, 1, 67);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (3005, 3, '討伐魔物', '為了剷除兇暴並且威脅著人類生活的魔物，進行驅除與討伐的行為。討伐魔物時，採集到的肉、皮、油脂、骨頭、角、羽毛等等，都會用來製作生活用品，因此也可以將狩獵當作是維持生計的手段之一。另一方面，有時候也會發現不具殘暴生性的魔物，而將其視為變種，並當成保護對象以進行調查。', 0, 1, 68);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (3006, 3, '驅魔護身符', '能保護持有者遠離邪靈、魔物、災厄等的護身符。被當作受過神明治癒的聖石之類的魔法道具在使用。', 0, 1, 69);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (3007, 3, '瑪爾傑海岸', '阿斯特賴亞大陸的海岸。附近有個小小的城鎮，在偶而舉辦的祭典上，會用夜光花來點綴入夜的街道。由於海上也會出現魔物，所以接受到擊退魔物的工作委託的武人們也會造訪此處。', 0, 1, 70);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (3008, 3, '墨丘利財團', '秋乃為達成大商人父親所要求的「成為獨當一面的商人吧」的目標，而組成的公會。因為擁有龐大的資本，所以對各種事業進行融資與併購。與以義賊身份進行活動的珠希一起，教訓那些用低劣手段賺黑心錢的商人則是另一個不公開的活動目的。', 0, 1, 71);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (3009, 3, '幽靈', '人類或是動物死掉之後，因無法成佛而變成的非物質狀態的精神體。雖然在蘭德索爾內對於幽靈是否真的存在，引起了廣泛討論，但目前並沒有結論。最近雖然收到許多在販賣布丁的西點店看到幽靈的目擊情報，但有一派認為那只是幻影現象而已。', 0, 1, 72);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (3010, 3, '拉比林斯', '由神秘人物「拉比林斯達」所組成，探究世界之謎的公會。僅由少數幾位信賴的過的人所組成。表面是在經營著可麗餅店，但其實是假扮成普通公會的祕密結社。', 0, 1, 73);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (3011, 3, '蘭德索爾', '阿斯特賴亞大陸上最大的國家。另外，這也是該國首都的重要城市。', 0, 1, 74);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (3012, 3, '蘭德索爾王家', '掌管蘭德索爾施政的君王與其一族。蘭德索爾在傳統上是以國王的長女擔任君主的母系國家。王族居住在王城當中，受到【王宮騎士團】的嚴密保護。', 0, 1, 75);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (3013, 3, '現充', '就本來的意思而言，指的是在現實生活過得很充實的人。但在衍生意義上，據優花梨所說「最近無論是誰都把這個詞當成是『正在談戀愛的人』在用呢！\u3000我雖然是單身，不過現實生活也過得很充實啊！\u3000嗚嗚……總之你們就快爆炸啦！」以上。無論如何，似乎都非常希望現充爆炸的樣子。', 0, 1, 76);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (3014, 3, '小小甜心', '由美美、禊、鏡華 3 個好朋友所組成的公會。公會的目的就只是一起玩。這並非由公會管理協會正式認可的團體，只是她們三個人在玩公會的扮家家酒遊戲。', 0, 1, 77);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (3015, 3, '月光學院', '蘭德索爾的學校之一。由公會管理協會營運的公立教育機構。採用合班制度，所以在此上學的學生的年齡、種族、學習能力各有不同。主要是以學習能力來分班。另外，雖然蘭德索爾沒有義務教育，但因為教育相關的公會十分發達，所以知識水準很高。識字率幾乎達到100%。', 0, 1, 78);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (3016, 3, '盧幣', '在阿斯特賴亞大陸流通的貨幣單位。有著紙幣與硬幣。就價值標準來看，大概花1000盧幣能解決一頓飯。', 0, 1, 79);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (3017, 3, '消失', '指的是應該存在的人物的相關紀錄或記憶消失的奇怪現象。例如，小孩子一定有雙親，但即使調查資料也無法證實他們的存在，而小孩子也沒有關於雙親的任何記憶。因為這種奇怪現象的關係，因而造成許多小孩沒有監護人，也無處可去的社會問題。', 0, 1, 80);
insert into v1_1a326b2bdaad9d290daa79d636047fef20647dc63fdba1b9af6625c79e8b28a4 values (3018, 3, '獨眼惡魔（One Eye Devil）', '聽說過去光是有人從口中說出這個名字，就會讓大陸上的壞人們顫抖不已存在。而其真面目其實是深月。她只是一一擊退阻礙自己研究的人，不知不覺間就獲得了這個名號。據說當她掀開平時遮住左眼的眼罩時，地獄就會降臨。', 0, 1, 81);
