insert into v1_4a33f731db6f710376d60912348f54a308b13e9a3797ae8e47e0988a6baed82d values (9, 110501, 3, 100, 130, 105, 1.5, 9, 109, 0.012, '在海灘上賣力舞動的盛夏南國少女。即使在競賽的喧囂中精神也不會受擾亂，能展現驚人的中後段加速。', 0, 1, 0);
insert into v1_4a33f731db6f710376d60912348f54a308b13e9a3797ae8e47e0988a6baed82d values (10, 115701, 1, 103, 200, 105, 2.5, 10, 110, 0.014, '察覺海灘事件徵兆的盛夏名偵探。以高度的推理力與魔法，確實地擾亂對戰對手奔跑。', 1, 0, 0);
insert into v1_4a33f731db6f710376d60912348f54a308b13e9a3797ae8e47e0988a6baed82d values (11, 113601, 2, 103, 350, 105, 2.5, 11, 111, 0.071, '再次脫下鎧甲的灼熱騎士團長。以實戰中培養出的判斷力，能在一瞬間展開護盾使障礙物無效。', 0, 0, 1);
insert into v1_4a33f731db6f710376d60912348f54a308b13e9a3797ae8e47e0988a6baed82d values (12, 107901, 3, 103, 130, 120, 1.5, 12, 112, 0.014, '在海邊隨心所欲的怕水貓女。在海灘上現身的美少女義賊，將奪取對戰對手的速度。', 0, 1, 0);
